.emthemesModez-productsByCategory-productsColumn {
    @include grid-column(12, $float: none, $collapse: true);
    display: inline-block;
    font-size: fontSize("base"); // 1
    vertical-align: top;

    @include breakpoint("medium") {
        width: grid-calc(10, $total-columns);
    }
}


.emthemesModez-productsByCategory-products {
    .productGrid {
        margin: 0;
        display: flex;
        flex-wrap: wrap;
        align-items: stretch;

        .product {
            padding: 0;
        }
    }


    .card {
        height: 100%;
        margin: 0;
        border: container("border");
    }

    .card-figure {
        padding: container("padding") / 2;
    }

    .card-body {
        padding: container("padding") / 2;
    }


    .productGrid--maxCol5 {
        .card {
            border-left: 0;
            border-top: 0;
        }

        .product:nth-child(2n) .card {
            border-left: container("border");
        }

        .product:nth-child(2) .card,
        .product:nth-child(3) .card {
            border-top: container("border");
        }


        @include breakpoint("small") {

            .product:nth-child(2n) .card {
                border-left: 0;
            }

            .product:nth-child(3n-1) .card {
                border-left: container("border");
            }


            .product:nth-child(2) .card,
            .product:nth-child(3) .card,
            .product:nth-child(4) .card {
                border-top: container("border");
            }
        }

        @include breakpoint("medium") {
            .product:nth-child(3n-1) .card {
                border-left: 0;
            }

            .product:nth-child(3) .card,
            .product:nth-child(4) .card,
            .product:nth-child(5) .card,
            .product:nth-child(6) .card {
                border-top: container("border");
            }
        }
    }
}

.emthemesModez-productsByCategory-categoryImage {
    display: inline-block;
    width: 100%;
    position: relative;
    overflow: hidden;

    @include breakpoint("medium") {
        img {
            position: absolute;
            min-width: 100%;
            min-height: 100%;
        }

        .productGrid--maxCol5 & {
            width: grid-calc(4, 5);
        }
    }
}

@include emthemesModez-productsByCategoryTabs-type('sorting', 'productsByCategorySortingTabs');

// Variation 'supermarket1'
// ----------------------------------------------------------------


.emthemesModez-section--productsByCategorySortingTabs {
	@mixin stylingSupermarket($color, $bgcolor) {
	    .emthemesModez-productsByCategoryTabs--sorting.emthemesModez-productsByCategoryTabs--supermarket1 {
	        .page-heading {
				background-color: $bgcolor;

	        	@include breakpoint("medium") {
					background-color: transparent;

					.papaSupermarket-style--modern & {
						padding-left: spacing("single");
						padding-right: spacing("single");
					}
	        	}

	            a,
	            > span {
	            	color: $color;
	            	background: none;
	                border-bottom: 0;

	                @include breakpoint("medium") {
	                	color: stencilColor("color-textHeading");

	                	&:hover {
	                		color: $color-textLink-hover;
	                	}

	                	&:active {
	                		color: $color-textLink-active;	
	                	}
	                }
	            }
	        }

	        .emthemesModez-productsByCategoryTabs-details-sub-heading {
	            color: $color;
	            background-color: $bgcolor;
	        }

	        @include breakpoint("medium") {
	            .tab.is-active {
	                border-color: $bgcolor;
	            }
	        }
	    }
	}

	$color1: stencilColor('productsByCategorySortingTabs-heading-color1');
	$bgcolor1: stencilColor('productsByCategorySortingTabs-heading-backgroundColor1');
	$color2: stencilColor('productsByCategorySortingTabs-heading-color2');
	$bgcolor2: stencilColor('productsByCategorySortingTabs-heading-backgroundColor2');
	$color3: stencilColor('productsByCategorySortingTabs-heading-color3');
	$bgcolor3: stencilColor('productsByCategorySortingTabs-heading-backgroundColor3');

	@include stylingSupermarket($color1, $bgcolor1);

	&:nth-child(3n + 2) {
		@include stylingSupermarket($color2, $bgcolor2);
	}

	&:nth-child(3n) {
		@include stylingSupermarket($color3, $bgcolor3);
	}
}

// Products grid in section Products By Category Sorting Tabs
// ----------------------------------------------------------------------------
// 
// - 6 columns
// - Category image takes 3 columns
// - styling section heading
// ----------------------------------------------------------------------------


.emthemesModez-productsByCategoryTabs-heading {
    font-size: fontSize("larger");
    text-transform: uppercase;
    border-bottom: 2px solid;
    margin-bottom: spacing("single");
    margin-top: 0;

    a {
        text-decoration: none;
        display: inline-block;
        // padding: spacing("quarter") (spacing("half") + spacing("quarter")) (spacing("quarter") - remCalc(2px));
        padding: spacing("half") (spacing("half") + spacing("quarter")) spacing("half");

    }
}


.emthemesModez-productsByCategoryTabs {
    .li-heading {
        list-style: none;
    }

    .tab {
        display: none;
    }

    @include breakpoint("medium") {
        .tabs {
            border-bottom: {
                width: 2px;
                style: solid;
            }

            display: flex;
            justify-content: space-between;
            align-items: center;
        }

        .tab {
            border: 0;
            margin: 0;
            display: block;
        }

        .li-heading {
            margin-left: 0;
            margin-right: spacing("single");
        }

        .tab--link {
            margin-left: auto;

            a {
                padding-right: 0;
            }
        }
        .tab-title {
            padding: spacing("quarter") spacing("single");
        }
    }


    .page-heading {
        font-size: fontSize("small");
        text-transform: uppercase;
        margin: 0;
        text-align: left;

        @include breakpoint("medium") {
            font-size: fontSize("larger");
        }

        .papaSupermarket-style--modern & {
            font-weight: fontWeight("normal");

            @include breakpoint("medium") {
                font-size: fontSize("large");
            }
        }

        a,
        > span {
            text-decoration: none;
            display: inline-block;
            // padding: spacing("quarter") (spacing("half") + spacing("quarter")) (spacing("quarter") - remCalc(2px));
            padding: spacing("half") container("padding");

            @include breakpoint("medium") {
                padding-top: spacing("half");
                padding-bottom: spacing("half");
            }

        }
    }
}




.emthemesModez-productsByCategoryTabs-categoryImage {
    @include grid-column(12, $float: none);

    display: flex;
    flex-direction: column;

    .productGrid & {
        .card {
            flex: 1;
