// Progress Bar
// - https://css-tricks.com/css3-progress-bars/
// ============================================================================

.progressBar { 
    box-sizing: content-box;
    height: 20px;  /* Can be anything */
    position: relative;
    margin: 60px 0 20px 0; /* Just for demo spacing */
    background: #555;
    border-radius: 25px;
    padding: 10px;
    box-shadow : inset 0 -1px 1px rgba(255,255,255,0.3);
}
.progressBar > span {
    display: block;
    height: 100%;
    border-radius: 20px;
    background-color: rgb(43,194,83);
    // background-image: -webkit-gradient(
    //   linear,
    //   left bottom,
    //   left top,
    //   color-stop(0, rgb(43,194,83)),
    //   color-stop(1, rgb(84,240,84))
    //  );
    background-image: linear-gradient(
      center bottom,
      rgb(43,194,83) 37%,
      rgb(84,240,84) 69%
     );
    box-shadow: 
      inset 0 2px 9px  rgba(255,255,255,0.3),
      inset 0 -2px 6px rgba(0,0,0,0.4);
    position: relative;
    overflow: hidden;
    transition: width .2s ease-in;
}
.progressBar > span:after, 
.progressBar.animate > span > span {
    content: "";
    position: absolute;
    top: 0; left: 0; bottom: 0; right: 0;
    // background-image: 
    //    -webkit-gradient(linear, 0 0, 100% 100%, 
    //       color-stop(.25, rgba(255, 255, 255, .2)), 
    //       color-stop(.25, transparent), color-stop(.5, transparent), 
    //       color-stop(.5, rgba(255, 255, 255, .2)), 
    //       color-stop(.75, rgba(255, 255, 255, .2)), 
    //       color-stop(.75, transparent), to(transparent)
    //    );
    background-image: 
        linear-gradient(
          -45deg, 
          rgba(255, 255, 255, .2) 25%, 
          transparent 25%, 
          transparent 50%, 
          rgba(255, 255, 255, .2) 50%, 
          rgba(255, 255, 255, .2) 75%, 
          transparent 75%, 
          transparent
       );
    z-index: 1;
    background-size: 50px 50px;
    animation: progressBar-move 2s linear infinite;
    border-radius: 20px;
    overflow: hidden;
}

.progressBar.animate > span:after {
    display: none;
}

@keyframes progressBar-move {
    0% {
       background-position: 0 0;
    }
    100% {
       background-position: 50px 50px;
    }
}

.progressBar.orange > span {
    background-color: #f1a165;
    background-image: linear-gradient(top, #f1a165, #f36d0a);
    // background-image: -moz-linear-gradient(top, #f1a165, #f36d0a);
    // background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0, #f1a165),color-stop(1, #f36d0a));
    // background-image: -webkit-linear-gradient(#f1a165, #f36d0a); 
}

.progressBar.red > span {
    background-color: #f0a3a3;
    background-image: linear-gradient(top, #f0a3a3, #f42323);
    // background-image: -moz-linear-gradient(top, #f0a3a3, #f42323);
    // background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0, #f0a3a3),color-stop(1, #f42323));
    // background-image: -webkit-linear-gradient(#f0a3a3, #f42323);
}

.progressBar.nostripes > span > span, 
.progressBar.nostripes > span:after {
    animation: none;
    background-image: none;
}


// Bulk Order category template
// ============================================================================

.papaSupermarket-page--pages-custom-category-bulk-order .actionBar-section--mode,
.papaSupermarket-page--pages-custom-brand-bulk-order .actionBar-section--mode {
    display: none;
}


// Style for Mordern variation
