components/emthemesModez/common/hoverMen:
@include breakpoint("medium") {
	@if stencilString("navPages_animation") == "fade" {

		// 2nd-level menu
		// ========================================================================
		.navPage-subMenu.is-open:not(.navPage-subMenu--vertical) {
			display: block;
			opacity: 0;
			transform: translateY(20px);
			transition: opacity .2s ease-in, transform .2s ease-in, left .1s ease-in .2s;


			.navPages-item:not(.is-hover):not(:hover):not(:focus) & {
				left: -99999px !important;
			}	
		}

		.navPages-item.is-hover,
		.navPages-item:hover,
		.navPages-item:focus {
			.navPage-subMenu.is-open:not(.navPage-subMenu--vertical) {
				opacity: 1;
				transform: none;
				transition: opacity .2s ease-out, transform .2s ease-out;

			}
		}

		// Standard dropdown menu
		// ========================================================================
		.navPage-subMenu--standard {

			// 2nd-level dropdown menu
			// --------------------------------------------------------------------
			.navPage-childList.is-open {
				display: block;
				opacity: 0;
				transform: translateY(20px);
				transition: opacity .2s ease-in, transform .2s ease-in, left .1s ease-in .2s;
			}

			.navPage-subMenu-item:not(.is-hover):not(:hover):not(:focus) > .navPage-childList.is-open {
				left: -99999px !important;
			}

			.navPage-subMenu-item.is-hover,
			.navPage-subMenu-item:hover,
			.navPage-subMenu-item:focus {
				> .navPage-childList.is-open {
					opacity: 1;
					transform: none;
					transition: opacity .2s ease-out, transform .2s ease-out;
				}
			}

			// 3rd-level menu
			// --------------------------------------------------------------------
			.navPage-childList-item:not(.is-hover):not(:hover):not(:focus) > .navPage-childList.is-open {
				left: -99999px !important;
			}

			.navPage-childList-item.is-hover,
			.navPage-childList-item:hover,
			.navPage-childList-item:focus {
				> .navPage-childList.is-open {
					opacity: 1;
					transform: none;
					transition: opacity .2s ease-out, transform .2s ease-out;
				}
			}
		}

		// 4th-level menu on the left sidebar and grouped categories menu
		// ------------------------------------------------------------------------
		.emthemesModez-verticalCategories .navPage-childList-item {
			> .navPage-childList.is-open {
				display: block;
				opacity: 0;
				transform: translateY(20px);
				transition: opacity .2s ease-in, transform .2s ease-in, left .1s ease-in .2s;
			}

			&:not(.is-hover):not(:hover):not(:focus) > .navPage-childList.is-open {
				left: -99999px !important;
			}

			&.is-hover,
			&:hover,
			&:focus {
				> .navPage-childList.is-open {
					opacity: 1;
					transform: none;
					transition: opacity .2s ease-out, transform .2s ease-out;
				}
			}
		}

		.emthemesModez-verticalCategories.is-open:not(.emthemesModez-verticalCategories--open) {
			display: block;
			opacity: 0;
			transform: translateY(20px);
			transition: opacity .2s ease-in, transform .2s ease-in, left .1s ease-in .2s;

			.emthemesModez-navPages-verticalCategories-container:not(.is-hover):not(:hover):not(:focus) & {
				left: -99999px !important;
			}

			.emthemesModez-navPages-verticalCategories-container.is-hover &,
			.emthemesModez-navPages-verticalCategories-container:hover &,
			.emthemesModez-navPages-verticalCategories-container:focus & {
				opacity: 1;
				transform: none;
				transition: opacity .2s ease-out, transform .2s ease-out;
			}
		}
	}
}

// ============================================================================
// Make the main menu open when hover
// 
// 1. Import this file to allow the main menu open submenus when mouse hover
// 
// 2. Add data-collapsible-... to the main menu links in file navigation-list.html
// 
// <a class="navPages-action has-subMenu" href="{{url}}" 
//     data-collapsible="navPages-{{id}}"
//     data-collapsible-disabled-breakpoint="medium"
//     data-collapsible-disabled-state="open"
//     data-collapsible-enabled-state="closed">
//     
// ============================================================================

@include breakpoint("medium") {

	.emthemesModez-verticalCategories.is-open:not(.emthemesModez-verticalCategories--open) {
		display: none;
	}

	.emthemesModez-navPages-verticalCategories-container:hover,
	.emthemesModez-navPages-verticalCategories-container:focus {
		.navPages-action.is-open {
			// remove only for this theme
			// background-color: stencilColor("navPages-subMenu-backgroundColor");
		}

		.emthemesModez-verticalCategories.is-open {
			display: block;
			box-shadow: 1px 1px 3px rgba(color("blacks"), 0.1);
		}
	}


	.navPages-action.is-open {
		background-color: transparent;
	}

	.navPage-subMenu.is-open {
		display: none;
	}

	.navPages-item:hover,
	.navPages-item:focus {
		> .navPages-action {
			color: stencilColor("navPages-color-hover");
		}
		.navPages-action.is-open {
			// background-color: stencilColor("navPages-subMenu-backgroundColor");
		}

		.navPage-subMenu-action.is-open {
			background-color: transparent;
		}

		.navPage-subMenu.is-open {
			display: block;
		}
	}


}

components/emthemesModez/common/megaMenu:
