		display: none;
	}

	$input-height: $input-font-size * lineHeight("base") + spacing("quarter") * 2;

	.form-field {
		&:before {
			font-size: fontSize("small");
			height: $input-height;
			height: calc(#{$input-height} - #{$input-border-width * 2});
			line-height: $input-height;
			line-height: calc(#{$input-height} - #{$input-border-width * 2});
