// Variation 'supermarket1'
// ----------------------------------------------------------------------------
// 
// - For theme Supermarket
// ---------------------------------------------------------------------------- 

.emthemesModez-specialProductsTabs--supermarket1 {

	.tabs {
		display: flex;
		justify-content: center;
		background: container("fill", "dark");
		margin: 0 (-$container-padding / 2);
		border-bottom: 0;

		.papaSupermarket-style--modern & {
			margin: 0;
		}

		@include breakpoint("medium") {
			background: none;
			margin: 0;
			border-bottom: container("border");

			@if stencilString("card-textAlign") == "left" {
				justify-content: flex-start;
			}

			.papaSupermarket-style--modern & {
				justify-content: flex-start;
				border-bottom: 0;
			}
		}
	}

	.tab-title {
		color: stencilColor("color-textHeading");
		background: none;
		padding: spacing("quarter") 0;

		@include breakpoint("medium") {
			padding: spacing("half") 0;
		}

		.papaSupermarket-style--modern & {
			color: stencilColor("specialProductsTab-heading-color-inactive");
			padding: 0;
		}
	}


	.tab {
		border-bottom: 2px solid transparent;

		&.is-active {
			border-bottom: 2px solid stencilColor('specialProductsTab-heading-backgroundColor');

			.tab-title {
				color: stencilColor("color-textHeading");
				background: none;

				.papaSupermarket-style--modern & {
				    color: stencilColor("specialProductsTab-heading-color");
				}
			}
		}

		+ .tab {
			margin-left: spacing("single") + spacing("half");

			.papaSupermarket-style--modern & {
				margin-left: spacing("half");

				@include breakpoint("small") {
					margin-left: spacing("single");
				}

				@include breakpoint("medium") {
					margin-left: spacing("single") + spacing("half");
				}
			}
		}
	}


	.tabs-contents {
		margin: spacing("single") (-$container-padding / 2) 0;

		.papaSupermarket-style--modern & {
			margin-left: 0;
			margin-right: 0;
			@include breakpoint("medium") {
				margin-left: -(spacing("quarter"));
				margin-right: -(spacing("quarter"));
			}
		}
	}

	.tab-content {
		padding: 0;
	}

	.productGrid {
		margin-bottom: 0;
		margin-left: 0;
		margin-right: 0;

		.papaSupermarket-style--modern & {
			margin-bottom: 0;
			margin-left: 0;
			margin-right: 0;
		}

		.product {
			padding-left: 0;
			padding-right: 0;

			.papaSupermarket-style--modern & {
				padding-left: spacing("quarter");
				padding-right: spacing("quarter");
			}
		}
	}

	@include ie-only() {
		& .productGrid--maxCol4 .product {
			flex-basis: calc(50% - #{spacing("half")});

			@include breakpoint("medium") {
				flex-basis: calc(25% - #{spacing("half")});
			}
		}
	}

	.productCarousel .card, 
	.productGrid .card {
		border-color: transparent;
	}
}

components/foundation/accordion/accordio:
// Accordion nav list
// -----------------------------------------------------------------------------
//
// Purpose: Used as navigation menu, for example: faceted search menu.
//
// -----------------------------------------------------------------------------
.accordion--navList {

    .accordion-indicator {


        @include breakpoint("medium") {
            visibility: visible;
            width: 1rem;
            height: 1rem;
        }
    }
}

// =============================================================================
// ACCORDION (CSS)
// =============================================================================


.accordion {
    border-radius: $accordion-borderRadius;
    box-shadow: none;

    .navList {
        margin-bottom: 0;
    }
}

.accordion-content {
    border: 0;

    .form:only-child {
        margin-bottom: 0;
    }
}

.accordion-title {
    margin: 0;
}

.accordion-navigation {
    margin-top: -1px;
    position: relative;
    text-decoration: none;
}


// Accordion nav list
// -----------------------------------------------------------------------------
//
// Purpose: Used as navigation menu, for example: faceted search menu.
//
// -----------------------------------------------------------------------------
.accordion--navList {
    @include breakpoint("medium") {
        border: 0;
    }

    .sidebarBlock + & {
        @include breakpoint("medium") {
            margin-top: $accordion-navList-spacing;
        }
    }

    .accordion-block {
        @include breakpoint("medium") {
            margin-top: $accordion-navList-spacing;
        }
    }

    .accordion-content {
        padding: $accordion-navList-padding;

        @include breakpoint("medium") {
            padding: 0;
        }
    }

    .accordion-navigation {
        @include breakpoint("medium") {
            background-position: right 0 top 50%;
            border: 0;
        }
    }

    .accordion-title {
        font-weight: normal;
        padding: $accordion-navList-padding;
        text-decoration: none;
        text-transform: inherit;

        @include breakpoint("medium") {
            margin-bottom: spacing("half");
            margin-top: 0;
            padding: 0;
        }
    }

    .accordion-indicator {
        fill: color("greys", "light");
    }

    .accordion-navigation-actions {
        right: $accordion-navList-padding;
        top: $accordion-navList-padding + spacing("eighth");

        @include breakpoint("medium") {
            right: 0;
            top: 0;
        }
    }
}


// Accordion block
// -----------------------------------------------------------------------------
//
// Purpose: Child blocks of an accordion
//
// -----------------------------------------------------------------------------
.accordion-block:first-child {
    margin-top: 0;

    .accordion-navigation {
        border-radius: $accordion-borderRadius $accordion-borderRadius 0 0;
    }
}

.accordion-block:last-child {
    .accordion-navigation,
    .accordion-content {
        border-radius: 0 0 $accordion-borderRadius $accordion-borderRadius;
    }

    .accordion-navigation {
        border-bottom-width: 0;

        // scss-lint:disable NestingDepth
        &.is-open {
            border-bottom-width: 1px;
            border-radius: 0;
        }
    }
}

// Accordion navigation actions
// -----------------------------------------------------------------------------
//
// Purpose: Action links, usually aligned to the right of .accordion-navigation
//
// -----------------------------------------------------------------------------
.accordion-navigation-actions {
    position: absolute;
    right: $accordion-navigation-paddingHorizontal;
    top: $accordion-navigation-paddingVertical;

    > a {
        color: stencilColor("color-textSecondary");
        vertical-align: middle;

        // scss-lint:disable NestingDepth
        &:hover {
            color: stencilColor("color-textSecondary--hover");
        }
        // scss-lint:enable NestingDepth
    }
}

