components/foundation/breadcrumbs/breadc:
.breadcrumbs {
	text-align: center;
	margin-bottom: spacing("half");

	@include breakpoint("small") {
		margin-top: spacing("third");

		@if $theme-style == "compact" {
			margin-top: spacing("base");
			margin-bottom: spacing("quarter");
		}
	}

	.papaSupermarket-pageType--cart &,
	.papaSupermarket-page--pages-page &,
	.papaSupermarket-page--pages-contact-us &,
	.papaSupermarket-page--pages-blog-post &,
	.papaSupermarket-page--pages-blog &,
	.papaSupermarket-page--pages-category &,
	.papaSupermarket-page--pages-custom-category-bulk-order &,
	.papaSupermarket-page--pages-custom-brand-bulk-order &,
	.papaSupermarket-page--pages-custom-product-no-sidebar &,
	.papaSupermarket-page--pages-brand &,
	.papaSupermarket-page--pages-brands &,
	.papaSupermarket-page--pages-search &,
	.papaSupermarket-page--pages-product & {
		@include breakpoint("medium") {
			text-align: left;
		}
	}
}
.breadcrumb {
	text-transform: uppercase;
	font-weight:bold;
	Font-size: 14px;
	margin-top: 10px !important;
}

// Style Modern
// ----------------------------------------------------------------------------
.papaSupermarket-style--modern {
	.breadcrumbs {

		@include breakpoint("small") {
			margin-top: 0 !important;
		}
	}
}

// =============================================================================
// BREADCRUMBS (CSS)
// =============================================================================
//
//
// 1. Hide breadcrumbs on mobile-sized screens to improve screen real estate.
//
// 2. Since our breadcrumbs are centred with a variable width, we need to use
//    display: inline-block; on the breadcrumbs. Hence needing to remove the
//    white-space with font-size: 0;
//    The font-size is reset in the breadcrumb settings file.
//
// -----------------------------------------------------------------------------


.breadcrumbs {
    display: none; // 1
    font-size: 0; // 2
    text-align: center;

    @include breakpoint("small") {
        display: block; // 1
    }
}

.breadcrumb {
    display: inline-block;
    float: none;

    &.is-active {

        // scss-lint:disable NestingDepth
        > .breadcrumb-label {
            cursor: text;
        }
    }
}

.breadcrumb-label {
    text-decoration: none;
}

components/foundation/buttons/buttons.sc:
// =============================================================================
// BUTTONS (CSS)
// =============================================================================

.button {
    line-height: 1rem;
    transition: all 0.15s ease;

    + .button {
        margin-left: 0;

        @include breakpoint("medium") {
            margin-left: spacing("half");
        }
    }
}

.button[disabled] {
    cursor: not-allowed;
}

.button--icon {

    svg {
        fill: stencilColor("button--icon-svg-color");
    }
}

// =============================================================================
// DROPDOWN (CSS)
// =============================================================================


.dropdown {
    left: -9999px;
    outline: none;
    position: absolute;
}

.dropdown-menu {
    &.is-loading .loadingOverlay {
        background: none;
        padding: (spacing("double") + spacing("single")) 0;
        position: relative;
    }

    &.is-open {
        display: block;
    }
}

.dropdown-menu-item {
    text-align: left;

    &:first-child {

        // scss-lint:disable NestingDepth
        > a {
            padding-top: spacing("half");
        }
    }

    &:last-child {

        // scss-lint:disable NestingDepth
        > a {
            padding-bottom: spacing("base");
        }
    }

    > a {
        margin: 0;
        padding: spacing("sixth") (spacing("half") + spacing("quarter"));
        text-decoration: none;
    }
}

.form-wishlist {
    position: relative;
}

.dropdown-menu-button {
    width: 100%;
    &.is-open {
        border-bottom-left-radius: 0px;
        border-bottom-right-radius: 0px;
    }
    .icon {
        float: right;
        margin-left: 10px;

        svg {
            fill: stencilColor("button--default-color");
        }
    }
}

#wishlist-dropdown.is-open {
    background: white;
    left: auto !important;
    max-width: none !important;
    width: 100% !important;
    @include breakpoint('small') {
        padding-right: 20px;
    }
    @include breakpoint('medium') {
        padding-right: 0px;
    }
    @include breakpoint('large') {
        padding-right: 11px;
    }
    &:focus {
        outline: none;
    }
    [data-wishlist] {
        border-top: none;
        margin-bottom: 0px;
        width: 100%;
    }
    .button {
        border-top-left-radius: 0px;
        border-top-right-radius: 0px;
        width: 100%;
    }
    .button--has-items {
        font-weight: 700;
    }
    li:not(:last-child) .button {
        border-bottom: none;
        border-radius: 0px;
        border-top: none;
        margin-bottom: 0px;
    }
}

