        margin-left: 0;
        margin-right: 0;
        width: 100%;

        @include breakpoint("small") {
            display: inline-block;
            margin-bottom: 0;
            width: auto;
        }

        // scss-lint:disable NestingDepth
        + .button {

            @include breakpoint("small") {
                margin-left: spacing("quarter");
            }
        }
        // scss-lint:enable NestingDepth
    }
}


// Stencil table modifiers
// -----------------------------------------------------------------------------

.table--line {
