    border-width: 0 0 1px;

    .table-thead {
        background-color: stencilColor("body-bg");
        border-color: container("borderColor");
        border-width: 0 0 1px;

        // scss-lint:disable SelectorDepth, NestingDepth
        tr {

            th {
                padding-left: 0;
                padding-right: 0;

                @include breakpoint("small") {
                    text-align: center;

                    &:first-child {
                        text-align: left;
                    }

                    &:last-child {
                        text-align: right;
                    }
                }

                + th {
                    padding-left: spacing("single");
                }
            }
        }
    }

    tr {
        border-color: container("border");

        // scss-lint:disable SelectorDepth, NestingDepth
        td {
            display: block;
            padding-left: 0;
            padding-right: 0;

            @include breakpoint("small") {
                text-align: center;

                &:first-child {
                    text-align: left;
                }

                &:last-child {
                    text-align: right;
                }
            }

            + td {
