    @else if stencilString("applePay-button") == "white-border" {
        background-color: $applePay-white;
        background-image: -webkit-named-image(apple-pay-logo-black);
        border: 0.5px solid $applePay-black;
    }
    @else {
        background-color: $applePay-black;
        background-image: -webkit-named-image(apple-pay-logo-white);
    }
}

.cart-additionalCheckoutButtons {
    .apple-pay-checkout-button {
        margin-top: spacing("half");
    }
}

.apple-pay-supported {
    .apple-pay-checkout-button {
        display: block;

        @include breakpoint("small") {
            display: inline-block;
            float: right;
        }
    }
}

.previewCartCheckout {
    .apple-pay-checkout-button {
        float: none;
        margin-top: spacing("half");
    }
}

components/stencil/banners/banners-emthe:
// =============================================================================
// Banners Component
// =============================================================================


// Banners
// -----------------------------------------------------------------------------
//
// Purpose: Control the look and feel of the top and bottom site banners
//
// -----------------------------------------------------------------------------

.banners {
    background-color: stencilColor("topBanner-backgroundColor");
    background-image: -webkit-linear-gradient(right, stencilColor("topBanner-backgroundColor") 0%, stencilColor("topBanner-backgroundColorGradient") 100%);
    background-image: -o-linear-gradient(right, stencilColor("topBanner-backgroundColor") 0%, stencilColor("topBanner-backgroundColorGradient") 100%);
    background-image: linear-gradient(to right, stencilColor("topBanner-backgroundColor") 0%, stencilColor("topBanner-backgroundColorGradient") 100%);
    color: stencilColor("topBanner-color");
    padding: 0;



    .banner {
        padding: spacing("half") + spacing("eighth") 0;

        .papaSupermarket-style--modern & {
            max-width: none;
        }
    }

