components/stencil/cart/cart-emthemesMod:
// =============================================================================
// CART (CSS)
// =============================================================================

.cart-emptyText {
    margin: 0;
    text-align: center;
    font-size: fontSize("base");
}


// Cart layout
// -----------------------------------------------------------------------------

.cart-header-item {
    text-transform: uppercase;
}

// Cart content
// -----------------------------------------------------------------------------

.cart-item-figure {
    position: relative;
    height: auto;
    margin-bottom: spacing("half");

    @include breakpoint("small") {
        margin-bottom: 0;
    }
}

.cart-remove {
    background-color: transparent;
    width: auto;
    height: auto;
    float: none;
    margin-left: spacing("half");
    color: $color-textBase;
}

.cart-item-name {
    margin-bottom: spacing("half");
    font-size: fontSize("base");

    a {
        text-decoration: none;
        color: $card-title-color;

        &:hover,
        &:focus {
            color: $card-title-color-hover;
        }
    }
}

.cart-item-block {
    .definitionList {
        font-size: fontSize("smaller");
    }
}

.cart-item-title {
    a {
        color: $color-textLink;

        &:hover,
        &:focus {
            color: $color-textLink-hover;
        }

        &:active {
            color: $color-textLink-active;
        }
    }
}

.cart-item-image {
    max-height: 100%;
    margin: auto;
}

.cart-item-info {
    margin-top: spacing("half");
    @include breakpoint("small") {
        margin-top: 0;
    }
}

.cart-item-quantity {
    @include breakpoint("medium") {
        white-space: nowrap;
    }
}

.cart-item-options {
    font-size: fontSize("smaller");
}

.cart-item-options--giftWrapping {
    margin-top: spacing("half");
    margin-bottom: spacing("half");
}

.cart-item-option-description {
    margin-bottom: spacing("half");
}


// Cart totals
// -----------------------------------------------------------------------------

.cart-total {
    .form-input {
        padding-top: 0;
        padding-bottom: 0;
    }
    .form-select {
        padding-top: 0;
        padding-bottom: 0;
    }
}

.cart-total-label {
    padding-top: spacing("quarter");
    padding-bottom: spacing("quarter");
    width: 40%;

    @include breakpoint("medium") {
        width: 50%;
        padding-top: spacing("base");
        padding-bottom: spacing("base");
    }
}

.cart-total-value {
    padding-top: spacing("quarter");
    padding-bottom: spacing("quarter");
    font-weight: fontWeight("bold");
    color: stencilColor("color-textHeading");

    @include breakpoint("medium") {
        padding-top: spacing("base");
        padding-bottom: spacing("base");
    }
}

.cart-total-grandTotal {
    font-size: fontSize("larger");
    color: stencilColor("color-price");
    line-height: 1;

    @include breakpoint("medium") {
        line-height: inherit;
    }
}


// Cart actions
// -----------------------------------------------------------------------------
.coupon-code-add,
.coupon-code-cancel,
.shipping-estimate-show,
.shipping-estimate-hide,
.gift-certificate-add,
.gift-certificate-cancel {
    color: $color-textLink;

    &:hover {
        color: $color-textLink-hover;
    }

    &:active {
        color: $color-textLink-active;
    }
}

.cart-actions {
    margin-bottom: spacing("base");

    @include breakpoint("small") {
        margin-left: auto;
        margin-right: 0;
        width: grid-calc(9, $total-columns);
    }

    @include breakpoint("medium") {
        width: grid-calc(7, $total-columns);
    }

    @include breakpoint("large") {
        width: grid-calc(5, $total-columns);
    }

    .button {
        display: block;
        float: none;

        @if $theme-style == "compact" {
            padding-top: spacing("half") + spacing("quarter");
            padding-bottom: spacing("half") + spacing("quarter");
        }
    }
}


// Cart Preview
// ----------------------------------------------------------------------------

.previewCartItem-content {
    @include clearfix;
    padding-top: spacing("quarter");
    padding-bottom: spacing("quarter");
}

.previewCartItem-remove {
    float: right;
}

.previewCartItem-remove-icon {
    color: $color-textBase;
}

.previewCart-additionalCheckoutButtons {
    clear: both;
    text-align: center;
}

.definitionList--previewCartItem {
    font-size: fontSize("smallest");
    margin-bottom: spacing("eighth");
}

// ----------------------------------------------------------------------------
// Modern Style
// ----------------------------------------------------------------------------
.papaSupermarket-style--modern {
    .cart {
        background: container("fill");
        border: container("border");
        // border-radius: $container-border-radius;
        width: calc(100% - #{spacing("quarter") * 2});
        margin: 0 auto;

        @include breakpoint("medium") {
            width: 100%;
        }
    }

    .cart-header-item {
        padding: spacing("quarter") spacing("half");
    }

    .cart-header-total {
        @include breakpoint("medium") {
            padding-left: spacing("single");
            padding-right: spacing("single");
        }
    }

    .cart-item {
        padding: spacing("half") 0;

        @include breakpoint("small") {
            padding-left: 25%;
        }

        @include breakpoint("medium") {
            padding-left: 0;
        }
    }

    .cart-remove {
        top: 0;
        left: spacing("half");
    }

    .cart-item-block {
        @include breakpoint("medium") {
            padding: spacing("quarter") spacing("half");
        }
    }

    .cart-item-figure {
        @include breakpoint("medium") {
            width: 10%;
        }
    }

    .cart-item-image {
        @include breakpoint("medium") {
            left: spacing("half");
            right: spacing("half");
            max-width: calc(100% - #{spacing("single")});
        }
    }

    .cart-item-options--giftWrapping {
        margin-top: spacing("half");
        margin-bottom: spacing("half");
    }

    .cart-item-option-description {
        margin-bottom: spacing("half");
    }

    .cart-item-total {
        @include breakpoint("medium") {
            padding-left: spacing("single");
            padding-right: spacing("single");
        }
    }

    .cart-totalsWrapper {
        padding: 0 spacing("quarter");

        @include breakpoint("medium") {
            padding: 0;
        }
    }

    .cart-totals {
        margin-top: spacing("single");
        background: container("fill");
        border: container("border");
        padding: spacing("quarter");

        @include breakpoint("medium") {
            padding: spacing("half") spacing("single");
        }
    }

    .cart-total-label,
    .cart-total-value {
        @include breakpoint("medium") {
            padding: spacing("half") 0;
        }
    }

    .cart-total-label {
        color: $color-textBase;
    }

    .coupon-code-add,
    .coupon-code-cancel,
    .shipping-estimate-show,
    .shipping-estimate-hide,
    .gift-certificate-add,
    .gift-certificate-cancel {
        color: $color-primary;
        font-weight: $body-font-weight;
    }

    .cart-actions {
        padding: 0 spacing("quarter");

        @include breakpoint("medium") {
            padding: 0;
        }
    }

    .cart-additionalCheckoutButtons {
        text-align: center;
        padding: 0 spacing("quarter");

        @include breakpoint("medium") {
            padding: 0;
        }

        .FloatRight div {
            float: none;

            @include breakpoint("small") {
                float: right;
                clear: right;
            }
        }
    }
}

components/stencil/cart/cart.scss:
// =============================================================================
// CART (CSS)
// =============================================================================
//
// 1. Override server-generated inline-css
//
// -----------------------------------------------------------------------------

// Cart variables
// -----------------------------------------------------------------------------

$cart-item-spacing:                     spacing("single");

$cart-thumbnail-maxWidth:               remCalc(100px);
$cart-thumbnail-height:                 remCalc(100px);

$cart-item-label-offset:                $cart-thumbnail-maxWidth + $cart-item-spacing;

//
// Shared styles for additional checkout buttons
// -----------------------------------------------------------------------------

%additionalCheckoutButtons {
    @include clearfix;

    // scss-lint:disable SelectorFormat
    .FloatRight {
        @include clearfix;

        // scss-lint:disable SelectorDepth, NestingDepth
        p {
            // scss-lint:disable ImportantRule
            float: none !important;
            margin: spacing("third") 0;
            text-align: right;
        }

        div {
            float: right;
        }
    }

    .CheckoutButton {
        margin-bottom: spacing("base");

        &:first-child {
            margin-top: spacing("single");
        }

        &:last-child {
            margin-bottom: spacing("single");
        }
    }
}

// Cart layout
// -----------------------------------------------------------------------------
//
// 1. On mobile and tablet we need to re-structure the table to appear more like
//    a list, then visually show it as a table on larger screens only.
//
// 2. Hide the table headings on mobile and tablet, show them for larger screens.
//
// -----------------------------------------------------------------------------

.cart {
    display: block; // 1
    margin-bottom: spacing("single");
    width: 100%;

    @include breakpoint("medium") {
        display: table; // 1
    }
}

.cart-header {
    display: none; // 2

    @include breakpoint("medium") {
        display: table-header-group; // 2
        text-align: left;
    }
}

.cart-header-item {
    padding-bottom: spacing("base");

    &:last-child {
        text-align: right;
    }
}

.cart-list {
    border-top: container("border");
    display: block; // 1

    @include breakpoint("medium") {
        display: table-row-group; // 1
    }
}

.cart-item {
    border-bottom: container("border");
    clear: both;
    display: block; // 1
    padding: $cart-item-spacing 0;

    @include breakpoint("small") {
        @include clearfix;
        padding-left: grid-calc(3, $total-columns);
    }

    @include breakpoint("medium") {
        display: table-row; // 1
        padding-left: 0;

        &:before,
        &:after {
            display: none;
        }
    }
}

.cart-item-block {
    display: block; // 1

    @include breakpoint("medium") {
        display: table-cell; // 1
    }

    .definitionList {
        margin-bottom: spacing("quarter");
    }
}

.cart-item-figure {
    float: left;
    height: $cart-thumbnail-height;
    margin-bottom: $cart-item-spacing;
    width: grid-calc(4, $total-columns);
    position: relative;

    @include breakpoint("small") {
        // height: auto;
        margin-bottom: 0;
        margin-left: -(grid-calc(4, $total-columns));
        width: grid-calc(4, $total-columns);
    }

    @include breakpoint("medium") {
        float: none;
        width: grid-calc(1, $total-columns);
    }

    @include lazy-loaded-padding('productthumb_size');
}

.cart-item-fixed-image {
    width: 100%;
}

.cart-item-image {
    max-width: get-width(stencilString('productthumb_size'));

    @include lazy-loaded-img;

    @include breakpoint("medium") {
        margin-left:0;
    }
}

.cart-item-title {
    display: table-cell;
    height: $cart-thumbnail-height;
    padding-left: $cart-item-spacing;

    @include breakpoint("small") {
        display: block;
        height: auto;
        margin-bottom: $cart-item-spacing;
        overflow: hidden;
    }

    @include breakpoint("medium") {
        display: table-cell;
        padding: $cart-item-spacing;
    }
}

.cart-item-info {
    clear: both;
    width: 100%;

    // Supermarket: Fix bug of the base theme
    + .cart-item-info {
        margin-top: spacing("half");
    }

    @include breakpoint("small") {
        clear: none;
        float: left;
        overflow: hidden;
        width: grid-calc(4, $total-columns);

        + .cart-item-info {
            margin-top: 0;
        }
    }

    @include breakpoint("medium") {
        float: none;
        width: grid-calc(2, $total-columns);

        &:last-child {
            text-align: right;
        }
    }
}


// Cart content
// -----------------------------------------------------------------------------

.cart-header-quantity,
.cart-item-quantity {

    @include breakpoint("medium") {
        text-align: center;
    }
}

.cart-item-quantity {

    .form-increment {
        display: inline-block;
        margin: 0 spacing("eighth") 1px spacing("single");
        vertical-align: middle;

        @include breakpoint("medium") {
            margin-left: 0;
        }
    }

}

.cart-item-label {
    color: stencilColor("color-textBase");
    float: left;
    margin-bottom: spacing("quarter");
    text-align: right;
    width: grid-calc(4, $total-columns);

    @include breakpoint("small") {
        display: block;
        padding-left: $cart-item-spacing;
        text-align: left;
        width: 100%;
    }

    @include breakpoint("medium") {
        display: none;
    }
}

.cart-item-value {
    padding-left: $cart-item-spacing;

    @include breakpoint("medium") {
        padding-left: 0;
    }
}

.cart-item-brand {
    color: color("greys", "light");
    margin-bottom: spacing("eighth");
}

.cart-item-name {
    font-size: fontSize("smaller");
    margin: 0;

    + .definitionList {
        margin-top: spacing("quarter");
    }
}

.cart-item-options--giftWrapping {
    margin-top: spacing("half") + spacing("quarter");

    .cart-item-option-description {
        color: $body-font-color;
    }
}

.cart-remove {
    background-color: container("fill", "dark");
    border-radius: 50%;
    cursor: pointer;
    float: right;
    height: remCalc(18px);
    margin-top: remCalc(2px);
    padding: spacing("eighth");
    transition: background-color 0.15s ease;
    width: remCalc(18px);

    @include breakpoint("small") {
        margin-left: spacing("base");
    }

    &.cart-remove--spacer {
        visibility: hidden;
    }

    > svg {
        fill: stencilColor("icon-color");
    }
}


// Cart totals
// -----------------------------------------------------------------------------

.cart-totals {
    @include u-listBullets("none");
    margin-bottom: $cart-item-spacing;
    text-align: right;
    width: 100%;

    @include breakpoint("small") {
        float: right;
        width: grid-calc(9, $total-columns);
    }

    @include breakpoint("medium") {
        margin-bottom: spacing("base");
        width: grid-calc(7, $total-columns);
    }

    @include breakpoint("large") {
        width: grid-calc(5, $total-columns);
    }
}

.cart-total {
    @include clearfix;
    border-bottom: 0;

    @include breakpoint("medium") {
        + .cart-total {
            border-top: container("border");
        }
    }

    .form-input {
        display: inline-block;
        height: remCalc(32px);
        padding: spacing("third");
        vertical-align: top;
        width: grid-calc(9, $total-columns);
    }

    .form-select {
        background-position: right remCalc(5px) top remCalc(7px);
        background-size: remCalc(20px) remCalc(20px);
        height: remCalc(32px);
        margin-bottom: spacing("half");
        padding: spacing("third") spacing("half");
    }

    .form-inlineMessage {
        text-align: left;
    }
}

.cart-total-label {
    color: stencilColor("color-textSecondary");
    width: grid-calc(4, $total-columns);

    @include breakpoint("small") {
        width: grid-calc(6, $total-columns);
    }

    @include breakpoint("medium") {
        display: block;
        padding-left: 0;
        text-align: left;
    }
}

.cart-total-value {
    text-align: left;

    @include breakpoint("small") {
        text-align: right;
    }

    @include breakpoint("medium") {
        width: grid-calc(6, $total-columns);
    }
}

.cart-total-label,
.cart-total-value {
    float: left;
    padding: spacing("third") 0 spacing("third") $cart-item-spacing;

    @include breakpoint("medium") {
        padding: spacing("base") 0;
    }
}

.cart-total-grandTotal {
    font-family: fontFamily("headingSans");
    font-size: fontSize("small");
    padding-bottom: spacing("eighth");
    padding-top: spacing("eighth");
    position: relative;

    @include breakpoint("medium") {
        padding: spacing("base") 0;
        top: -(spacing("sixth"));
    }
}


// Cart actions
// -----------------------------------------------------------------------------

.coupon-code-add,
.coupon-code-cancel,
.shipping-estimate-show,
.shipping-estimate-hide,
.gift-certificate-add,
.gift-certificate-cancel {
    color: stencilColor("color-textSecondary");
    padding: 0;
    text-decoration: underline;
    transition: color 0.15s ease;

    &:hover {
        color: stencilColor("color-textSecondary--hover");
    }
}

.coupon-code-cancel,
.shipping-estimate-hide {
    font-style: italic;
}

.coupon-form,
.cart-gift-certificate-form {
    @include clearfix;
    font-size: 0;
    margin-bottom: spacing("base");

    .form-input,
    .button {
        float: left;
    }

    .form-input {
        width: 78%;
    }

    .button {
        margin: 0 0 0 2%;
        padding-left: spacing("half");
        padding-right: spacing("half");
        width: 20%;
    }
}

.estimator-form {
    margin-bottom: 0;

    .form-input {
        margin-bottom: spacing("half");
        width: 100%;
    }

    .button--primary {
        width: 60%;
    }
}

.estimator-form-label {
    clear: both;
    float: left;
    font-weight: fontWeight("normal");
    margin-bottom: 0;

    .form-label {
        line-height: remCalc(32px);
        margin-bottom: 0;
    }
}

.estimator-form-label-text {
    margin-left: remCalc(10px);
}

.estimator-form-input {
    float: right;
    margin-bottom: 1em;
    width: 60%;

    .form-input,
    .form-select {
        margin-bottom: 0;
    }
}

.estimator-form-input--price {
    width: 30%;
}

.estimator-form-row {
    @include clearfix;
}

.estimator-form-toggleUPSRate {
    display: block;
    margin-bottom: 1em;
}

.cart-actions {
    @include clearfix;
    clear: both;

    .button {
        display: block;
        margin-bottom: 0;

        @include breakpoint("small") {
            display: inline-block;
            float: right;
        }
    }

    .checkoutMultiple {
        clear: right;
        display: block;
        float: right;
        padding-top: spacing("quarter");
    }
}

.cart-additionalCheckoutButtons {
    @extend %additionalCheckoutButtons;
}

// papathemes-supermarket commented
// .previewCart-additionalCheckoutButtons {
//     @extend %additionalCheckoutButtons;
//     padding-right: 1.5rem;
// }


// Cart Preview
// -----------------------------------------------------------------------------
//
// 1. Need to vertically align a block of text
//
// -----------------------------------------------------------------------------

.previewCartList {
    @include u-listReset;
    border-collapse: collapse;
    display: table;
    table-layout: fixed;
    width: 100%;
}

.previewCartItem {
    @include grid-row;
    display: table-row;
    line-height: lineHeight("smaller");

    + .previewCartItem {
        border-top: container("border");
    }
}

.previewCartItem-image,
.previewCartItem-content {
    display: table-cell; // 1
    vertical-align: middle;
}

.previewCartItem-image {
    @include grid-column(4, $float: false);
    padding: 0;
    text-align: center;
    position: relative;

    img {
        @include lazy-loaded-img;

        /** Supermarket */
        width: calc(100% - 30px);
        max-height: 100%;
        object-fit: contain;
        /* Object-fit polyfill */
        font-family: 'object-fit: contain';
    }

    @include lazy-loaded-padding('productthumb_size');
}

.previewCartItem-content {
    @include grid-column(8, $float: false);
    padding-left: 0;
}

.previewCartItem-name {
    margin: 0;
    text-transform: none;

    > a {
        text-decoration: none;
    }
}

.previewCartItem-brand,
.previewCartItem-name {
    margin-bottom: spacing("eighth");
}

.previewCartItem-brand,
.previewCartItem-price {
