components/stencil/facetedSearch/faceted:
// Faceted search toggle
// -----------------------------------------------------------------------------
//
// 1. Do not show if viewport is over "medium" breakpoint
//
// -----------------------------------------------------------------------------
.facetedSearch-toggle {
	width: auto;
	display: block;
	margin-right: remCalc(60);
	margin-bottom: spacing("half");
	// padding: $input-verticalPadding $input-verticalPadding;
	padding: spacing("half");
	text-decoration: none;
	font-size: fontSize("smaller");

	&:before,
	&:after {
		display: none;
	}

	&.is-open .toggleLink-text--on {
		display: block;
	}

	+ .facetedSearch-navList.is-open {
		clear: both;
		top: -1px;
	}
}


.facetedSearch-toggle-text {
	display: none;
}

.facetedSearch-toggle-indicator {
	color: $color-textSecondary;
	width: auto;
	float: none;
	text-align: left;

	.toggleLink-text {
		font-size: fontSize("smaller");
		width: 100%;

		.icon {
			float: right;
		}
	}

	.icon svg {
		fill: $color-textSecondary;
	}
}


// Faceted search nav list
// -----------------------------------------------------------------------------
//
// - Styling theme design
//
// -----------------------------------------------------------------------------
.facetedSearch-navList {

	.accordion--navList {
		.accordion-navigation-actions {
			top: $accordion-navList-padding;

			@include breakpoint("medium") {
				top: 0;
			}
		}
	}

	.accordion-title {
		text-transform: uppercase;
		font-weight: fontWeight("bold");
		font-size: fontSize("base");
	}

	.navList-action--checkbox {
		&:before {
		    margin-top: remCalc(-(fontSize("smaller") * lineHeight("base") / 2));
		}

		&:after {
		    margin-top: remCalc(-(fontSize("smaller") * lineHeight("base") / 2) + 1px);
		}

	}
}

.facetedSearch.sidebarBlock {
	border-top: 0;
	border-bottom: 0;
	padding-top: 0;
	padding-bottom: 0;

	@include breakpoint("medium") {
		border-top: container("border");
		border-bottom: container("border");
		padding-top: container("padding");
		padding-bottom: container("padding");
	}

	@media (max-width: screenSize("medium") - 1px) {
		@if $theme-style == "modern" {
			margin-top: 0;
		}
		@else {
			margin: 0;
			padding: 0;
			border: 0;
		}

		.sidebarBlock ~ & {
			margin-top: 0;
		}
	}

	.papaSupermarket-style--modern & {
		.facetedSearch-toggle {
			border: 0;
			margin-left: -(spacing("half"));
			margin-bottom: 0;
		}

		.facetedSearch-toggle-indicator .toggleLink-text .icon {
			display: none;
		}
	}

	.facetedSearch-refineFilters {
		margin: (-(container("padding"))) (-(container("padding"))) 0;
		border-top: 0;
		border-left: 0;
		border-right: 0;
	}

	.navList {
		border: 0;
		margin: 0;
	}

	.navList-item {
		border: 0;
		padding: spacing("quarter") 0;

		+ .navList-item {
			border: 0;
		}
	}
}

// Modern Style
// ============================================================================
.papaSupermarket-style--modern {
	@media (max-width: screenSize("medium") - 1px) {
		.sidebarBlock ~ .facetedSearch.sidebarBlock  {
			margin-top: 0;
		}

		.emthemesModez-productsFilter--faceted ~ .facetedSearch .facetedSearch-toggle-indicator {
			border-right: container("border");
		}
	}
}

// =============================================================================
// FACET SEARCH (CSS)
// =============================================================================


// Faceted search toggle
// -----------------------------------------------------------------------------
//
// 1. Do not show if viewport is over "medium" breakpoint
//
// -----------------------------------------------------------------------------
.facetedSearch-toggle {
    @include grid-row;
    border: $facetedSearch-toggle-border;
    border-radius: $facetedSearch-toggle-borderRadius;
    border-width: $facetedSearch-toggle-borderWidth;
    display: block;
    margin-bottom: $facetedSearch-toggle-verticalSpacing;
    padding: $facetedSearch-toggle-padding;

    @include breakpoint("medium") {
        // scss-lint:disable ImportantRule
        display: none !important; // 1
    }

    &.is-open {
        border-bottom-left-radius: 0;
        border-bottom-right-radius: 0;
        margin-bottom: 0;
    }
}

.facetedSearch-toggle-text,
.facetedSearch-toggle-indicator {
    display: block;
    font-weight: normal;
}

.facetedSearch-toggle-text {
    @include grid-column(7);
    padding: 0;
    text-align: left;
}

.facetedSearch-toggle-indicator {
    @include grid-column(5);
    font-weight: bold;
    padding: 0;
    text-align: right;
}


// Faceted search nav list
// -----------------------------------------------------------------------------
//
// 1. Do not show if viewport is over "medium" breakpoint
//
// -----------------------------------------------------------------------------
.facetedSearch-navList {
    display: none;

    @include breakpoint("medium") {
        // scss-lint:disable ImportantRule
        display: block !important; // 1
    }

    &.is-open {
        display: block;

        // scss-lint:disable SelectorDepth, NestingDepth
        .accordion--navList,
        .accordion-block:first-child .accordion-navigation {
            border-top-left-radius: 0;
            border-top-right-radius: 0;
        }
    }

    .facetedSearch-toggle + &,
    .sidebarBlock + & {
        margin-top: $facetedSearch-toggle-verticalSpacing;
    }


    .facetedSearch-toggle + &.is-open,
    .sidebarBlock + &.is-open {
        margin-top: -1px;

        @include breakpoint("medium") {
            margin-top: $facetedSearch-toggle-verticalSpacing;
        }
    }

    .navList--inner {
        margin-left: spacing("single");
    }
}

.facet-quick-heading {
    text-transform: capitalize;
}

.facetedSearch-optionColumns {
    column-count: 3;
    column-gap: 20px;
}


// Faceted search refine filters
// -----------------------------------------------------------------------------
//
// 1. Only show if viewport is over "medium" breakpoint
//
// -----------------------------------------------------------------------------
.facetedSearch-refineFilters {
    display: none;

    @include breakpoint("medium") {
        display: block; // 1
    }
}

// Facted search ratings
.facetedSearch-content--rating {
    .is-selected {
        font-weight: bold;
    }
}

components/stencil/heroCarousel/heroCaro:
.heroCarousel-container {
    display: flex;
    flex-flow: row wrap;
    width: 100%;

    @if $theme-style == "compact" {
		@include breakpoint("medium") {
            margin-bottom: spacing("half");

            @if $theme-layout == "left-sidebar" {
                margin-top: spacing("half");
            }
		}
    }
}

.heroCarousel-container--hasBanners {
    @include breakpoint("medium") {
        flex-wrap: nowrap;
    }
}

.heroCarousel {
    margin-bottom: 0;
    margin-top: 0;
    width: 100%;

    .heroCarousel-container--hasBanners & {
        @include breakpoint("medium") {
            min-width: 66.6666%;
            *width: 66.6666%;
        }
    }

    .js & {
        opacity: 1;
        max-height: none;
        transition: none;
    }

    @include breakpoint("medium") {
        width: auto;
        flex: 1 1 0;
    }

    &.slick-initialized {
        max-height: none;
    }
    &.slick-dotted {
        margin-bottom: 0;
    }
}

.heroCarousel-banners {
    width: 100%;
    > * {
        height: 100% !important;
    }
    @include breakpoint("medium") {
        width: calc(33.33% - #{spacing("half")});
        flex: 0 0 auto;
        margin-left: spacing("half");
    }
    .papathemesHeroCarouselBanner {
        display: flex;
        flex-flow: row wrap;
        > * {
            margin: spacing("half") 0 0 0;
            width: 100%;
        }
        @include breakpoint("small") {
            margin: 0 0 0 (- spacing("half"));
            > * {
                margin: spacing("half") 0 0 spacing("half");
                width: calc(50% - #{spacing("half")});
            }
        }
        @include breakpoint("medium") {
            margin: - spacing("half") 0 0 0;
            > * {
                margin: spacing("half") 0 0 0;
                width: 100%;
            }
        }
    }
}

.heroCarousel .slick-next, 
.heroCarousel .slick-prev {
    top: 50%;
    display: none !important;

    @include breakpoint("medium") {
        display: block !important;
    }
}

.heroCarousel .slick-dots li {
    height: auto;
    width: auto;
    padding: remCalc(5px);

    button {
        background: $slick-dot-color;
    }

    button,
    button::before {
        @include square(6px);
        border-radius: remCalc(3px);
        transition: all 0.5s ease;
    }

    &.slick-active {
        button,
        button::before {
            width: remCalc(15px);
        }
    }


}

.js {
    .heroCarousel-slide:first-child {
        display: block;
    }

    .heroCarousel-slide + .heroCarousel-slide {
        opacity: 0;
        max-height: 0;
    }

    // .heroCarousel-slide:not(.slick-slide),
    // .heroCarousel-slide.slick-slide:not(.slick-active) {
    //     background-image: none !important;
    // }

    .slick-initialized .heroCarousel-slide + .heroCarousel-slide {
        opacity: 1;
        max-height: none;
    }
}

.heroCarousel-slide {
    .heroCarousel-image-wrapper {
        max-height: none;
    }

    .heroCarousel-image {
        max-height: none;
    }

    a {
        display: block;
    }
}

.heroCarousel-image-box {
    max-width: 100%;
    margin: 0 auto;
    position: relative;
}
.heroCarousel-image-box-padding {
    height: 0;
}

.heroCarousel-image {
    width: 100%;

    &.lazyload,
    &.lazyloading {
        width: auto;

        .heroCarousel-image-wrapper:not(.heroCarousel-image-wrapper--sized) & + .heroCarousel-content {
            opacity: 0;
        }
    }

    @include breakpoint("medium") {
        width: auto;
    }
}

.heroCarousel-content {
    @include breakpoint("medium") {
        background-color: rgba($carousel-bgColor, stencilNumber("carousel-bgColor-opacity") / 100);
    }
}

.heroCarousel-video-wrapper {
    position: relative;
    overflow: hidden;
}

.heroCarousel-video {
    position: absolute;
    top: 50%;
    left: 0;
    width: 100%;
    height: 0;
    padding-bottom: stencilString('carousel-video-ratio');
    transform: translateY(-50%);
    opacity: 0;
    visibility: hidden;
    transition: opacity 0.3s ease;

    .slick-active & {
        visibility: visible;
    }

    .heroCarousel:not([data-youtube-autoplay]) &,
    .slick-video-playing & {
        visibility: visible;
        opacity: 1;
    }

    iframe {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
    }
}


// 
// Make position of carousel description text of each slide 
// to left or right depends on theme settings
// 
@include breakpoint("medium") {
    @for $i from 0 through 4 {
        @if stencilString("carousel-slide" + #{$i} + "-position") == "left" {
            .heroCarousel-slide--#{$i} .heroCarousel-content {
                text-align: left;

                @include breakpoint("medium") {
                    left: auto;
                    right: 50%;
                    width: auto;
                    max-width: 37.5%;
                }
            }
        }
        @else if stencilString("carousel-slide" + #{$i} + "-position") == "right" {
            .heroCarousel-slide--#{$i} .heroCarousel-content {
                text-align: right;

                @include breakpoint("medium") {
                    left: 50%;
                    right: auto;
                    width: auto;
                    max-width: 37.5%;
                }
            }
        }
    }
}


.heroCarousel-title {
    font-family: $header-font-family;
    font-weight: fontWeight("bold");
    font-size: fontSize("largest");
    margin-bottom: 0;

    @include breakpoint("medium") {
        font-size: fontSize("hero");
    }
}

.heroCarousel-description {
    font-size: fontSize("base");
    margin-bottom: spacing("half");

    @include breakpoint("medium") {
        font-size: 36px;
        margin-bottom: 0;
    }
}


// ============================================================================
// Modern Style
// ============================================================================

.papaSupermarket-style--modern {
    .heroCarousel-title {
        font-family: $header-font-family;
        line-height: 1;

        @include breakpoint("medium") {
            font-size: fontSize("base") * 2;
            margin-bottom: spacing("half");
            text-shadow: 1px 1px 0 rgba(stencilColor("carousel-bgColor"), .2);
        }

        @include breakpoint("xxlarge") {
            font-size: fontSize("base") * 3;
        }
    }

    .heroCarousel-description {
        @include breakpoint("medium") {
            font-size: fontSize("base");
            text-shadow: 1px 1px 0 rgba(stencilColor("carousel-bgColor"), .2);
        }

        @include breakpoint("xxlarge") {
            font-size: fontSize("small");
        }
    }

    .heroCarousel-content {
        @include breakpoint("xxlarge") {
            top: 5rem;
            transform: none;
        }
    }

    @for $i from 0 through 4 {
        @if stencilString("carousel-slide" + #{$i} + "-position") == "left" {
            .heroCarousel-slide--#{$i} .heroCarousel-content {
                text-align: center;

                @include breakpoint("medium") {
                    text-align: left;
                    left: 5rem;
                    right: auto;
                    width: auto;
                    max-width: calc(50% - 5rem);
                }

                @include breakpoint("xxlarge") {
                    max-width: calc(45% - 5rem);
                }
            }
        }
        @else if stencilString("carousel-slide" + #{$i} + "-position") == "right" {
            .heroCarousel-slide--#{$i} .heroCarousel-content {
                text-align: center;

                @include breakpoint("medium") {
                    text-align: left;
                    left: 50%;
                    right: auto;
                    width: auto;
                    max-width: calc(50% - 5rem);
                }

                @include breakpoint("xxlarge") {
                    left: 55%;
                    max-width: calc(45% - 5rem);
                }
            }
        }
    }
}

// HERO CAROUSEL
// -----------------------------------------------------------------------------
//
// Purpose: Styles the hero carousel component, which basically adds extras to the
// vendor slick carousel, to specifically display hero images.
//
// 1. Use object-fit on the image tag to keep aspect ratio and cover the space
//    created by the outer div. The JS will detect browsers that do not support
//    object-fit and instead copy the image src to a the background-image of the
//    wrapper div and add the compat-object-fit class.
//
// 2. Visually overrides the top margin for '.body' defined in _body.scss.
//    The '.body' top margin creates space between the header and page content.
//    However, on the homepage, we want the carousel to be flush with the header.
//
// 3. Allows image to scale on large screens while preventing the top and bottom
//    from becoming cut off.
// -----------------------------------------------------------------------------

.heroCarousel {
    width: 1px;
    min-width: 100%;
    margin-bottom: (spacing("double") + spacing("single"));
    margin-top: -(spacing("single")); // 3

    @include breakpoint("medium") {
        margin-top: -(spacing("single") + spacing("base")); // 3
    }

    &.slick-initialized { // 2
        max-height: remCalc(1000);
        opacity: 1;
    }

    &:not(.slick-initialized) :not(.heroCarousel-slide--first).heroCarousel-slide {
        display: none;
    }

    a {
        text-decoration: none;
    }

    .slick-next,
    .slick-prev {
        top: 50%;
    }

    .slick-next {
        right: 15px;

        @include breakpoint("medium") {
            right: 25px;
        }
    }

    .slick-prev {
        left: 15px;

        @include breakpoint("medium") {
            left: 25px;
        }
    }

    .slick-dots {
        bottom: spacing("third");

        @include breakpoint("medium") {
            bottom: spacing("single");
        }

        // scss-lint:disable SelectorDepth, NestingDepth
        li {
            @include carouselOpaqueBackgrounds($slick-dot-bgColor);
        }
    }
}

.heroCarousel-slide {
    position: relative;
    a {
        text-decoration: none;
    }

    .heroCarousel-image {
        @include breakpoint("medium") {
            object-fit: cover; // 1
            max-height: remCalc(600px);
            width: 100%;
        }
    }

    .heroCarousel-image-wrapper {
        @include breakpoint("medium") {
            max-height: remCalc(600px);
        }
    }

    &.compat-object-fit { // 1
        background-size: cover;
        background-position: 50%;
        background-repeat: no-repeat;

        &.stretch {
            @include breakpoint("large") { // 4
                background-size: 100% 100%;
            }
        }

        .heroCarousel-image {
            opacity: 0;
        }
    }
}

.heroCarousel-content {
    background-color: rgba($carousel-bgColor, 1);
    padding: spacing("half") spacing("single") (spacing("double"));
    text-align: center;

    @include breakpoint("medium") {
        @include carouselOpaqueBackgrounds;
        background-color: rgba($carousel-bgColor, 0.9);
        left: 0;
        margin: 0 auto;
        padding: spacing("single") * 1.5;
        position: absolute;
        right: 0;
        top: 50%;
        transform: translateY(-50%);
        width: remCalc(700px);

        &.heroCarousel-content--empty {
            background-color: transparent;
        }
    }
}

.heroCarousel-title {
    color: $carousel-title-color;
    font-size: fontSize("small");
    margin-top: 0;

    @include breakpoint("medium") {
        font-size: fontSize("hero");
    }
}

.heroCarousel-description {
    color: $carousel-description-color;
    font-size: fontSize("tiny");

    @include breakpoint("small") {
        font-size: fontSize("smallest");
    }

    @include breakpoint("medium") {
        font-size: fontSize("smaller");
    }
}

.heroCarousel-action {
    margin: 0;

    @include breakpoint("medium") {
        margin-top: spacing("single");
    }
}

