    margin: $maintenanceNotice-header-margin;
}

// =============================================================================
// ADMIN BAR MODE (CSS)
// =============================================================================

body.hasAdminBar {
    padding-top: 101px; // 55px (Mobile Header) + 46px (Admin Bar)

    @include breakpoint("medium") {
        padding-top: $adminBar-height;

        .banners {
        }

        .header {
            padding-top: 0;
        }
    }

    .header {
        padding-top: $adminBar-height;

        @include breakpoint("medium") {
            padding-top: 0;
        }
