		}

		.navPages-action-moreIcon {
			display: block;
		}

		.navPage-subMenu-list {
			margin: 0 !important;
		}

		.navPage-subMenu-item {
			width: 100%;
			padding: 0 !important;

			&:first-child {
				display: block;
			}

			> .navPage-subMenu-action {
				margin-bottom: 0;
			}
		}

		.navPage-subMenu-action {
			padding-left: spacing("half") + spacing("single");
			padding-right: spacing("half") + spacing("quarter");
		}

