		    display: block;
		}

		.navPage-childList-item {
		    position: static;
		}

		.navPage-childList-action.is-open {
		    .navPages-action-moreIcon {
		        display: block;
		    }
		}

		.navPage-childList-item .navPage-childList.is-open {
