components/stencil/productCarousel/compo:
// PRODUCT CAROUSEL
// -----------------------------------------------------------------------------
//
// Purpose: Imports the dependencies of the product carousel component
//
// -----------------------------------------------------------------------------

@import "productCarousel";
@import "productCarousel-emthemesModez";

components/stencil/productCarousel/produ:
.productCarousel {
	.papaSupermarket-style--modern & {
		margin: 0;
	}
}

.page-heading + .productCarousel {
	$pd: spacing("half");
	$h: remCalc(fontSize("small") * 1.25) + spacing("single"); // same page-heading height
	$t: spacing("single");

	@if $theme-style == "modern" {
		$pd: spacing("eighth");
		$h: remCalc(fontSize("small") * 1.25);
		$t: spacing("half");
	}
	@else if $theme-style == "compact" {
		$pd: spacing("half");
		$h: remCalc(fontSize("small") * 1.25) + spacing("single");
	}

	.slick-prev,
	.slick-next	{
		top: -($t + $h);
		margin: 0;
		width: auto;
		height: auto;
		padding: $pd;
		transform: none;

		&:before {
			width: $h - $pd * 2;
			height: $h - $pd * 2;
			margin: 0;
		}
	}

	.slick-prev {
		left: auto;
		$r: $h;

		@if $theme-style == "modern" {
			right: $r + spacing("half");
		}
		@else {
			right: $r;
		}
	}

	.slick-next	{
		@if $theme-style == "modern" {
			right: spacing("half");
		}
		@else {
			right: 0;
		}
	}

	@include breakpoint("medium") {
		@if $theme-style == "modern" {
			$pd: spacing("quarter");
			$h: remCalc(fontSize("large") * 1.25);

			.slick-prev,
			.slick-next	{
				top: -($t + $h);
				padding: $pd;

				&:before {
					width: $h - $pd * 2;
					height: $h - $pd * 2;
				}
			}

			.slick-prev {
				right: spacing("half") + $h;
			}
		}
	}
}

.productCarousel-slide {
	padding: 0;
}


// ============================================================================
// Modern Style
// ============================================================================
.papaSupermarket-style--modern {
	.productCarousel .slick-track {
		display: flex;
		align-items: stretch;
	}

	.productCarousel-slide {
		height: auto;
	}

	.card {
		height: 100%;
	}
}

// PRODUCT CAROUSEL
// -----------------------------------------------------------------------------
//
// Purpose: Styles the product carousel component, which basically adds extras to the
// vendor slick carousel, to specifically display product
//
// 1. If there's no JS layout the products out based on the grid.
//
// -----------------------------------------------------------------------------

.productCarousel { // 1
    @include grid-row(
        $behavior: nest
    );
}

.productCarousel-slide { // 1
    @include grid-column(6);

    @include breakpoint("small") {
        width: grid-calc(4, $total-columns);
    }

    @include breakpoint("medium") {
        width: grid-calc(3, $total-columns);
    }

    @include breakpoint("large") {
        width: grid-calc(2, $total-columns);
    }

    .card {
        margin-bottom: 0;

        .card-title {
            overflow-wrap: break-word;
        }
    }
}

