// Fix social icons messy on product page
// ----------------------------------------------------------------------------

.google_plusone_iframe_widget {
    width: 38px !important;
    height: 24px !important;
}

.fb_iframe_widget + .fb_iframe_widget {
    display: none;
}

.addthis_button_facebook_like.icon--facebook_like > svg {
