components/vendor/slick/slick.scss:
// SLICK
// -----------------------------------------------------------------------------
//
// Purpose: These are the styles for the base Slick carousel
//
// -----------------------------------------------------------------------------


//
// Next and Previous buttons for all carousels
//
// Purpose: Override the plugin to give some Stencil styling to the js generated mark-up
//
// 1. Set the background again because the plugin sets the full background property on hover
// 2. Prevent screen flicker when CSS transition is applied
//
// -----------------------------------------------------------------------------

.slick-next,
.slick-prev {
    @include carouselOpaqueBackgrounds($slick-arrow-bgColor);
    z-index: 1;
    height: remCalc(61px);
    // margin-top: -(remCalc(15px)); // removed by Papathemes - Supermarket
    padding: remCalc(10px);
    width: remCalc(40px);

    &::before {
        background-position: 50%;
        background-repeat: no-repeat;
        background-size: 100%;
        display: block;
        height: remCalc(41px);
        width: remCalc(20px);
    }

    &:hover,
    &:focus {
        @include carouselOpaqueBackgrounds($slick-arrow-bgColor); // 1
        background-position: 50%;
        background-repeat: no-repeat;
        background-size: 100%;
    }
}

.slick-next {
    right: -10px;

    @include breakpoint("large") {
        right: -(spacing("double") + spacing("quarter"));
    }

    &::before {
        background-image: $slick-arrow-next;
    }

    &:hover {
        // scss-lint:disable NestingDepth
        &::before {
            background-image: url("data:image/svg+xml;base64,");
        }
    }
}

.slick-prev {
    left: -15px;

    @include breakpoint("large") {
        left: -(spacing("double") + spacing("quarter"));
    }

    &::before {
        background-image: $slick-arrow-prev;
    }

    &:hover {
        // scss-lint:disable NestingDepth
        &::before {
            background-image: url("data:image/svg+xml;base64,");
        }
    }
}

.slick-slide img
{
    display: block;
    width: 100%;
}


//
// Carousel dots
//
// Purpose: Stencil overrides for the js generated carousel dots
//
// 2. Nesting depth due to plugin css
// -----------------------------------------------------------------------------

.slick-dots {  // 2
    margin: 0;

    li {
        @include square(32px);
        margin: 0;
        padding: remCalc(5px);

        // scss-lint:disable SelectorDepth, NestingDepth
        button,
        button::before {
            @include square(15px);
            border-radius: 50%;
        }

        button {
            // scss-lint:disable NestingDepth
            border: 1px solid $slick-dot-color;
            display: inline-block;
            margin: 0;
            padding: 0;
            position: relative;
            text-align: center;
            vertical-align: middle;

            &:hover {
                &::before {
                    background: $slick-dot-color;
                }
            }
        }

        button::before {
            display: block;
            left: -1px;
            top: -1px;
            transition: background 100ms ease-out;
        }

        &.slick-active {
            // scss-lint:disable NestingDepth
            button::before {
                background: $slick-dot-color-active;
            }
        }
    }

}


//
// disabled carousel elements
// -----------------------------------------------------------------------------
.slick-disabled {
    cursor: $cursor-default-value;
    opacity: 0.1;
}


//
// Slick track
// -----------------------------------------------------------------------------
.slick-track {
    backface-visibility: hidden; // 2
    perspective: 1000px; // 2
}

//
// iOS Safari fix https://github.com/kenwheeler/slick/issues/2834#issuecomment-389590661
// -----------------------------------------------------------------------------
div.slick-slider {
    width: 1px;
    min-width: 100%;
    *width: 100%;
}

components/vendor/slick/tools.scss:
// Slick Carousel Tools
// -----------------------------------------------------------------------------
//
// Purpose: Tools used to style the slick carousel
//
// 1. Plugin SASS assumes you want an icon font, to override this and not output
// that CSS, setting the url value to null
//
// 2. Setting opaque background in old browsers
//
// -----------------------------------------------------------------------------

@function image-url($url) { // 1
    @return null;
}

// scss-lint:disable DuplicateProperty
@mixin carouselOpaqueBackgrounds($color: rgba($carousel-bgColor, 0.9)) { // 2
    background-color: rgba($color, 1);
    background-color: $color;
}
// scss-lint:enable DuplicateProperty

