 */

.emthemesModez-blogGrid {
	@include u-listBullets("none");
	@include grid-row($behavior: "nest");
	font-size: 0; // 1
	margin-bottom: spacing("single");

	.emthemesModez-blogItem {
		@include grid-column(12, $float: none);
        display: inline-block;
        font-size: fontSize("base"); // 1
        vertical-align: top;

		@include breakpoint("small") {
            width: grid-calc(4, $total-columns);
        }
	}

	.blog-post-figure {
		width: 100%;
	}

	.blog-post-body {
		width: 100%;
	}
}

@include breakpoint("small") {
	.emthemesModez-blogGrid--featured {
		.emthemesModez-blogItem:first-child {
			width: 50%;
			float: left;
		}

		.emthemesModez-blogItem:nth-child(2),
		.emthemesModez-blogItem:nth-child(3) {
			width: 50%;

			.blog {
				display: flex;
				align-items: flex-start;
			}

			.blog-post-figure {
				flex: 1;
				margin: 0 spacing("single") 0 0;

				.first-tag {
					bottom: spacing("single") * 2;
				}
			}

			.blog-post-body {
				flex: 1;
			}

			.blog-date {
				display: none;
			}
		}
	}
}

/**
 * ----------------------------------------------------------------------------
 * - Blog page 2-columns layout &
 * - Blog page 2-columns layout has a first post show full width.
 * ----------------------------------------------------------------------------
 */
@include breakpoint("small") {

	.emthemesModez-blog--2col,
	.emthemesModez-blog--2col-featured {	
		@include grid-row($behavior: "nest");
		font-size: 0; // 1
		margin-bottom: spacing("single");

		> * {
			font-size: fontSize("base"); // 1
		}

		.blog {
			@include grid-column(6, $float: none);
	        display: inline-block;
	        vertical-align: top;
		}
	}

	.emthemesModez-blog--2col .blog:nth-child(2) {
		margin-top: 0;
    	padding-top: 0;
	}

	.emthemesModez-blog--2col-featured .blog:first-child {
		display: block;
		width: 100%;
	}
}


/**
 * ============================================================================
 * Common blog elements
 * ============================================================================
 */


.blog-post-figure {
	position: relative;

	.first-tag {
		position: absolute;
		bottom: spacing("single");
		left: 0;

		a {
			display: block;
			padding: spacing("quarter") spacing("single");
			background: #fff;
			text-transform: uppercase;
			font-weight: fontWeight("bold");
			font-size: fontSize("smaller");
			letter-spacing: letterSpacing("large");
			text-decoration: none;
		}
	}
}

/**
 * Blog page which has Sidebar
 */
.page-sidebar + .page-content {
	.blog-post-figure {
		width: 100%;
	}
}



.blog-thumbnail {
	overflow: hidden;
	margin: 0 0 spacing("single");
}

.blog-post-body {
	text-align: left;
}

.page-sidebar + .page-content {
	.blog-post-body {
		width: 100%;
	}
}


.blog-title {
	@if $theme-style == "compact" {
		margin: 0 0 spacing("half");
	}
	@else {
		margin: 0 0 spacing("single");
	}

	a {
		color: inherit;
	}
}
h1.blog-title {
	margin-top: spacing("single");

	@include breakpoint("small") {
		margin-top: spacing("half");
	}
}

.blog-post {
	font-size: fontSize("base");
	margin-bottom: spacing("single");
}

.blog--has-tags,
.blog--has-socials {

	.blog-post-body {
		@include clearfix;
	}

	.blog-post {
		border-bottom: 1px solid container("borderColor");

		@if $theme-style == "compact" {
			padding-bottom: spacing("half");
		}
		@else {
			padding-bottom: spacing("single");
		}
	}
}

.blog-summary {
	@if $theme-style == "compact" {
		margin: 0 0 spacing("half");
	}
	@else {
		margin: 0 0 spacing("single");
	}
}

.blog-dateContainer {
	display: flex;
	justify-content: space-between;

}

.blog-readmore a {
	text-decoration: none;
	margin-top: spacing("single");

	@if $theme-style == "compact" {
		font-weight: inherit;
		text-transform: capitalize;
	}
	@else {
		font-weight: fontWeight("bold");
		text-transform: uppercase;
	}
}


.blog-date,
.blog-author {
	font-size: fontSize("smaller");
	color: $color-textSecondary;
	text-align: right;
}

.blog-date-date {
	&:before {
		font-family: FontAwesome;
		content: "\f017";
		font-size: fontSize("base");
		display: inline-block;
		padding-right: spacing("quarter");
	}
}

.blog-date-author {
	padding-left: spacing("half");
	&:before {
		font-family: FontAwesome;
		content: "\f2c0";
		font-size: fontSize("base");
		display: inline-block;
		padding-right: spacing("quarter");
	}
}

.blog {
	.tags {
		margin-bottom: 0;
		text-align: left;

		@if $theme-style == "compact" {
			margin-top: spacing("half");
		}
		@else {
			margin-top: spacing("single");
		}
	}

	.addthis_toolbox {
		text-align: left;

		@if $theme-style == "compact" {
			margin-top: spacing("half");
		}
		@else {
			margin-top: spacing("single");
		}

		@include breakpoint("small") {
			text-align: right;
		}
	}
}

.blog--has-tags .tags,
.blog--has-socials .addthis_toolbox {
	@include breakpoint("small") {
		float: left;
		width: 50%;
	}
}

.blog .socialLinks {
	text-align: left;

	@include breakpoint("small") {
		text-align: right;
	}
}


/* fix wrong width of pinterest icon */
.addthis_toolbox a.at300b.addthis_button_pinterest {
	width: spacing("quarter") * 2 + lineHeight("base") * fontSize("base");

	.pin_it_iframe_widget {
		display: none;
	}
}


.emthemesModez-blog-bottomSection {
	margin-top: spacing("single") * 3;
}




// ============================================================================
// Recent Blog Posts - Style 'footerList'
// ============================================================================

.page-heading--blogGrid-footerList {
	font-size: fontSize("larger");
	text-align: left;
	text-transform: uppercase;
	margin: 0 0 spacing("single");
}

.emthemesModez-blogGrid--footerList {

	@include breakpoint("medium") {
		margin-bottom: 0;
	}

	.emthemesModez-blogItem {
		display: block;
		width: 100%;

		&:last-child {
			margin-bottom: -(spacing("single"));
		}
	}

	.blog-thumbnail {
		@include square(70px); // TOOD: add to theme settings
		margin: 0 $column-gutter $column-gutter 0;
	}

	.blog {
		display: flex;
		align-items: flex-start;
	}

	.blog-post-figure {
		flex: 0;
		margin: 0;
	}

	.blog-post-body {
		flex: 1;
	}

	.blog-title {
		font-size: fontSize("base");
		margin-bottom: spacing("half");
	}
}


// ============================================================================
// Blog on Sidebar
// ============================================================================
.sidebarBlock--blog {
	.navList {
		@include breakpoint("small") {
			display: flex;
			flex-wrap: wrap;
		}

		@include breakpoint("medium") {
			display: block;
		}
	}

	.navList-item {
		@include breakpoint("small") {
			width: 50%;
		}

		@include breakpoint("medium") {
			width: 100%;
		}
	}

	.blog-post-figure {
		@include breakpoint("medium") {
		    width: 100%;
		}
	}

	.blog-post-body {
		@include breakpoint("small") {
		    width: 100%;
		}
	}

	.blog-dateContainer {
		@include breakpoint("medium") {
		    display: block;
		}
	}

	.blog-date,
	.blog-author {
		display: none;

		@include breakpoint("medium") {
			text-align: left;
		}
	}

	.blog-readmore {
		+ .blog-date {
			@include breakpoint("medium") {
				margin-top: spacing("single");
			}
		}
	}
}

// ============================================================================
// Modern Style
// ============================================================================
.papaSupermarket-style--modern {
	.emthemesModez-blogGrid {
		margin: 0;
		background-color: container("fill");
		border: container("border");
		border-radius: $container-border-radius;
		box-shadow: 0 3px 0 container("borderColor");
		padding: spacing("half") spacing("quarter");

		.emthemesModez-blogItem {
			padding: 0 spacing("quarter");
		}
	}

	.blog-title {
		font-size: fontSize("small");
		font-weight: fontWeight("normal");
		margin-bottom: spacing("half");
	}

	.blog-summary {
		margin-bottom: spacing("half");
	}

	.blog-readmore a {
		text-transform: capitalize;
		font-weight: inherit;
		color: $color-primary;

		&:hover {
			color: $color-textBase;
		}
	}

	.sidebarBlock--blog {
		.navList-item {
			padding: spacing("half");
		}

		.blog {
			margin-bottom: 0;
		}

		.blog-thumbnail {
			margin-bottom: spacing("half");
		}


		.blog-post {
			margin-bottom: spacing("half");
		}

		.blog-readmore + .blog-date {
			margin-top: spacing("half");
		}

		.blog-summary {
			color: $color-textBase;
		}
	}
}

layouts/blog/blog.scss:
// =============================================================================
// BLOG
// =============================================================================


// Blog post
// -----------------------------------------------------------------------------

.blog {
    margin-bottom: spacing("single");

    @include breakpoint("medium") {
        margin: auto;
        width: 100%;
    }

    + .blog {
        border-top: container("border");
        margin-top: spacing("double");
        padding-top: spacing("double");
    }

    .tags {
        margin-top: spacing("double");
        text-align: center;
    }

    .socialLinks {
        text-align: center;
    }

    // scss-lint:disable SelectorFormat
    .addthis_toolbox {
        margin-top: spacing("double");
    }
    // scss-lint:enable SelectorFormat
}

.blog-title {
    font-size: fontSize("large");
    margin: 0 spacing("single") spacing("quarter");

    a {
        text-decoration: none;
    }
}

.blog-date,
.blog-author {
    color: stencilColor("color-textSecondary");
    text-align: center;
}

.blog-author {
    margin-bottom: spacing("single") + spacing("base");
}

.blog-post {
    font-size: fontSize("smaller");
    text-align: left;
}

.blog-thumbnail {
    margin: 0 auto (spacing("base") * 2);
    position: relative;
    max-width: get_width(stencilString('blog_size'));

    img {
        width: 100%;
    }
}

.blog-post-figure {
    display: block;
    margin: auto;
    text-align: center;

    @include breakpoint("medium") {
        width: grid-calc(8, $total-columns);
    }
}

.blog-post-body {
    display: block;
    margin: 0 auto;
    text-align: center;
    width: 100%;

    @include breakpoint("small") {
        width: grid-calc(10, $total-columns);
    }

    @include breakpoint("medium") {
        width: grid-calc(8, $total-columns);
    }

    @include breakpoint("large") {
        width: grid-calc(6, $total-columns);
    }
}

layouts/body/body-emthemesModez.scss:
.body {
    margin-bottom: spacing("double");
    margin-top: 0;
}

.page {
    display: flex;
    flex-direction: column;

    margin-left: -(container("padding") / 2);
    margin-right: -(container("padding") / 2);

    @include breakpoint("medium") {
        margin-left: -$column-gutter / 2;
        margin-right: -$column-gutter / 2;
        display: block;
    }
}

.main {
    padding: 0 $container-padding / 2 - container("padding") / 2;

    @include breakpoint("medium") {
        padding: 0;
    }
}

.page-content {
    order: 2;

    // When main menu navPages style is Modern
    .papaSupermarket-style--modern & {
        margin-top: spacing("half");
        padding-left: 0;
        padding-right: 0;

        @include breakpoint("medium") {
            padding-left: $column-gutter / 2;
            padding-right: $column-gutter / 2;
        }
    }
}

.page-content--centered {
    @include breakpoint("medium") {
        width: 60%;
    }
}

.page-content--sticky {
    @include breakpoint("medium") {
        position: sticky;
        top: 0;
    }
}

.page-sidebar {
    order: 3;
    margin-top: spacing("single");


    @include breakpoint("medium") {
        width: grid-calc(2, 10);
        color: $color-textSecondary;


        @if $theme-style == "compact" {
            margin-top: spacing("half");
        }
        @else {
            margin-top: spacing("single");

            // when layout is fixed left sidebar
            .papaSupermarket-layout--left-sidebar & {
                @if stencilString('navPages_categoriesMenu') == 'group' {
                    margin-top: -(spacing("single") + spacing("base") + spacing("half"));
                    z-index: zIndex("low"); // same header's z-index
                }
                @if stencilString('navPages_categoriesMenu') == 'horizontal' {
                    margin-top: 0;
                }
            }

            // Category page & Show Subcategories Menu is enabled.
            .papaSupermarket-pageType--category.papaSupermarket--showSubcategoriesMenu & {
                margin-top: spacing("single");
                z-index: zIndex("lower"); // lower header
            }

            // no pages navigation
            .papaSupermarket-layout--left-sidebar.papaSupermarket--hideContentNav & {
                margin-top: 0;
            }

            // when banner below header show
            .emthemesModezBannersBlockAfterHeader + .body & {
                margin-top: 0;
            }

            // When main menu navPages style is Modern
            .papaSupermarket-style--modern & {
                margin-top: spacing("half");
            }
        }


        + .page-content {
            width: grid-calc(8, 10);
            float: right;
        }

        + .page-sidebar + .page-content {
            width: grid-calc(6, 10);
        }
    }

    .papaSupermarket-style--modern & {
        padding-left: $column-gutter / 2 + spacing("quarter");
        padding-right: $column-gutter / 2 + spacing("quarter");

        @include breakpoint("medium") {
            padding-left: $column-gutter / 2;
            padding-right: 0;
        }
    }

    // fix margin issue of child elements
    > nav {
        border-top: 0.5px solid transparent;
    }
}

.page-sidebar--top {
    order: 1;
    margin-top: 0;

    @include breakpoint("medium") {
        @if $theme-style == "compact" {
            margin-top: spacing("half");
        }
    }
}

.page-sidebar--bottom {
    clear: left;

    @include breakpoint("medium") {
        &,
        .papaSupermarket-layout--left-sidebar & {
            margin-top: spacing("single");
        }
    }
}

.page-sidebar--right {
    float: right;

    .papaSupermarket-layout--left-sidebar & {
        margin-top: if($theme-style == "compact", spacing("half"), spacing("single"));

        @include breakpoint("medium") {
            z-index: zIndex("low") - 1; // same header's z-index - 1
        }
    }

    .papaSupermarket-style--modern & {
        margin-top: spacing("single");

        @include breakpoint("medium") {
            margin-top: spacing("half");
        }
    }
}

.page-sidebar--sticky {
    @include breakpoint("medium") {
        position: sticky;
        top: 0;
    }
}


.page-content-body {
    .papaSupermarket-style--modern & {
        @include u-modern-container-box($_shadow: false);

        padding: spacing("quarter");
        width: calc(100% - #{spacing("quarter") * 2});
        margin: 0 auto;

        @include breakpoint("small") {
            padding: spacing("single") spacing("half");

            &.emthemesModez-blog--2col .blog,
            &.emthemesModez-blog--2col-featured .blog {
                padding-left: spacing("half");
                padding-right: spacing("half");
            }
        }

        @include breakpoint("medium") {
            width: 100%;
        }
    }
}

.papaSupermarket-style--modern[class*='papaSupermarket-page--pages-account-'] {
    .body {
        @include breakpoint("medium") {
            margin-top: spacing("half");
        }
    }
}

.papaSupermarket-categoryPage-description {
    .papaSupermarket-style--modern & {
        padding: 0 spacing("half");
    }    
}

layouts/body/body.scss:
// =============================================================================
// BODY (CSS)
//
// 1. Header is fixed on small screens, use the content body to create the whitespace
//    between it and the header on all situations. If you change the spacing here,
//    please update .heroCarousel too.
//
// =============================================================================


.body {
    margin-bottom: spacing("double") * 2;
    margin-top: spacing("single"); // 1

    @include breakpoint("medium") {
        margin-top: spacing("single") + spacing("base"); // 1
    }
}


.page {
    @include grid-row(
        $behavior: nest
    );
}

.page-content,
.page-content--full {
    @include grid-column(12);
}

.page-content--centered {

    @include breakpoint("medium") {
        @include grid-column(
            $columns: 8,
            $center: true
        );
    }
}

.page-content--textCenter {
    text-align: center;
}

.page-sidebar {
    @include grid-column(12);
    position: relative;

    @include breakpoint("medium") {
        @include grid-column(3);

        + .page-content {
            @include grid-column(9);
        }
    }
}

layouts/brands/brand.scss:
.brand-image-container {
    position: relative;

    @if stencilString('brand_main_size') != 'original' {
        max-width: get-width(stencilString('brand_main_size'));

        img {
            @include lazy-loaded-img;
            // papathemes-supermarket:
            width: 100%;
            height: 100%;
            object-fit: contain;
            object-position: left top;
            font-family: "object-fit: contain";
        }

        @include lazy-loaded-padding('brand_main_size');
    }
}

.brand {
    .card-img-container {
        max-width: get-width(stencilString('brand_size'));

        @include lazy-loaded-padding('brand_size');
    }
}

layouts/brands/brandGrid-papaSupermarket:
.brandGrid {
    &--newest {
        display: flex;
        flex-flow: row wrap;
    }
}
.papaSupermarket-style--modern {
    .brandGrid {
        @include u-modern-container-box;

        padding: spacing("half") spacing("quarter");
        width: calc(100% - #{spacing("quarter") * 2});
        margin: 0 auto;

        @include breakpoint("medium") {
            width: 100%;
            padding: spacing("single") spacing("half");
        }

