}

layouts/container/container-emthemesMode:
// =============================================================================
// GRID (CSS)
// =============================================================================

%emthemesModez-container {
	margin-left: auto;
    margin-right: auto;
    max-width: none;
    padding: 0 container("padding") / 2;
    position: relative;
    width: layout("fixed");                                                                                             

    @include breakpoint("medium") {
        padding: 0 $container-padding;
    }

    @include breakpoint("large") {
    	max-width: remCalc(1170) + ($container-padding * 2);
        padding: 0 $container-padding;
    }

    @include breakpoint("xxlarge") {
    	max-width: layout("max") + ($container-padding * 4);
        padding: 0 $container-padding * 2;
    }
}

.container {
    @extend %emthemesModez-container;

    .container {
        padding-left: 0;
        padding-right: 0;
    }
}

layouts/container/container.scss:
// =============================================================================
// GRID (CSS)
// =============================================================================


.container {
    margin-left: auto;
    margin-right: auto;
    max-width: layout("max") + ($container-padding * 4);
    padding: 0 $container-padding;
    position: relative;
    width: layout("fixed");

    @include breakpoint("large") {
        padding: 0 ($container-padding * 4);
    }
}

layouts/footer/footer-emthemesModez.scss:
.footer {
	padding: 0;
	border-top: 0;
	color: stencilColor("footer-color");

	a {
		text-decoration: none;
		color: stencilColor("footer-linkColor");

		&:hover,
		&:focus,
		&:active {
			color: stencilColor("footer-linkColor-hover");
		}
	}

	address {
		font-style: normal;
	}
}

.footer-info {
	margin: 0 (-(container("padding")) / 2);
	padding: spacing("double") 0 0;
	display: flex;
	flex-wrap: wrap;
	align-items: flex-start;

	&:before,
	&:after {
		display: none;
	}

	@include breakpoint("medium") {
		margin: 0 (-$container-padding / 2);
	}

	.footer-info-heading {
		font-size: fontSize("large");
	}
}

.footer-info-col {
	color: stencilColor("footer-color");

	flex: 1 1 50%;
	max-width: 50%; // Require for IE 11
	min-width: width("6/12");
	width: auto;
	text-align: left;


	@include breakpoint("medium") {
		flex-basis: width("2/12");
		min-width: width("2/12");
		max-width: width("2/12"); // Require for IE 11
	}


	.fa-ul {
		margin-left: 0;
		margin-bottom: spacing("single");

		@include breakpoint("medium") {
			margin-left: spacing("single") + spacing("quarter");
		}

		li {
			margin-bottom: spacing("half");
		}
	}

	.fa-li {
		left: -(spacing("single") + spacing("half"));
		display: none;

		@include breakpoint("medium") {
			display: inline;
		}
	}
}

.footer-info-col--brands {
	.footer-info-list {
		column-count: 2;
	}

	max-width: 100%;
	flex-basis: 100%;

	@include breakpoint("medium") {
		flex-basis: width("2/12");
		min-width: width("2/12");
		max-width: width("2/12"); // Require for IE 11
	}
}

.footer-info-heading {
	color: stencilColor("footer-headingColor");
	font-size: fontSize("larger");
	text-transform: uppercase;
	margin: 0 0 spacing("single");

	@include breakpoint("small") {
		text-align: left;
	}
}

.footer-info-text {
	margin-bottom: spacing("single");
}


.footer-info-list {

	li {
		margin: 0 0 spacing("half");
	}
	a {
		color: stencilColor("footer-color");

		&:hover,
		&:focus,
		&:active {
            color: stencilColor("footer-color-hover");
        }
	}
}


.footer-info-col--about {
	flex-basis: 100%;
	max-width: 0;
	min-width: 100%;
	text-align: center;
	padding-bottom: spacing("double");
	border-bottom: container("border");

	@include breakpoint("medium") {
		flex-basis: 50%;
		max-width: 50%;
		min-width: 50%;
		text-align: left;
		border-bottom: 0;
		padding-bottom: 0;
	}

	.footer-info-heading {
		text-align: center;

		@include breakpoint("medium") {
			text-align: left;
		}
	}
}




.footer-copyright {
	background: stencilColor("copyright-backgroundColor");
	color: stencilColor("copyright-color");
	padding: spacing("half") 0;
	text-align: center;

	a {
		color: stencilColor("footer-linkColor");

		&:hover,
		&:focus,
		&:active {
			color: stencilColor("footer-linkColor-hover");			
		}
	}

	p {
		margin-bottom: 0;
	}

	.container {
		@include breakpoint("small") {
			display: flex;
			align-items: center;
			justify-content: space-between;
		}
	}

}

.footer-payment-icons {
	margin: 0 0 0 auto;
    text-align: center;
    height: auto;

    .footer-payment-icon {
    	height: remCalc(35px);
    	width: remCalc(40px);
    	margin: 0 spacing("quarter");
    }

    svg {
        fill: stencilColor("copyright-color");
    }
}


// ============================================================================
// 3 Columns: About, Blog, Newsletter
// ============================================================================


.emthemesModez-footer-topColumns-wrapper {
	background: stencilColor("footer-secondary-backgroundColor");
}

.emthemesModez-footer-topColumns {
	color: stencilColor("footer-secondary-color");
	padding: spacing("single") + spacing("quarter") 0;

	a {
		color: stencilColor("footer-secondary-linkColor");

		&:hover,
		&:focus,
		&:active {
			color: stencilColor("footer-secondary-linkColor-hover");
		}
	}

	.page-heading,
	.footer-info-heading {
		color: stencilColor("footer-secondary-headingColor");
	}

	@include breakpoint("medium") {
		display: flex;

		@if $theme-style == "compact" {
			padding: spacing("single") 0;
		}

		> * {
			flex: 1;
			margin: 0 $column-gutter 0 0;

			&:last-child {
				margin-right: 0;
			}
		}
	}
}

// ----------------------------------------------------------------------------
// Block About Us
// ----------------------------------------------------------------------------

.emthemesModez-footer-about {

}

.footer-logo {
	margin: 0 0 spacing("single");

	a {
		text-decoration: none;
	}

	.header-logo-image-container {
		margin: 0 auto;

		@include breakpoint("medium") {
			margin: 0;
		}
	}

	.header-logo-image {
		@include breakpoint("medium") {
			right: unset;
		}
	}
}


// ----------------------------------------------------------------------------
// Brands Carousel on footer
// ----------------------------------------------------------------------------
.emthemesModez-brandCarousel-container {
	text-align: center;
	font-size: fontSize("base");

	.papaSupermarket-style--modern & {
		margin-top: spacing("single");
		background-color: container("fill");
		border: container("border");
		border-radius: $container-border-radius;
		box-shadow: 0 3px 0 container("borderColor");

		.slick-next,
		.slick-prev {
			margin-top: 0;
			padding: 0;
			width: remCalc(32);
			height: remCalc(32);
			text-align: center;
			transform: translateY(-50%);

			&:before {
				background: none;
				content: '\f054';
				font-family: FontAwesome;
				font-size: fontSize("base");
				line-height: remCalc(32);
				margin: 0;
				display: inline;
				width: auto;
				height: auto;
			}
		}

		.slick-prev:before {
			content: '\f053';
		}
	}
}

.emthemesModez-brandCarousel {
	margin-bottom: 0;
}

.emthemesModez-brandCarousel-slide {
	text-transform: uppercase;
	text-align: center;
	padding: 0 spacing("half");
	display: inline-block;

	a {
		font-size: fontSize("large");
		text-decoration: none;
		display: block;
		white-space: nowrap;

		&:hover img,
		&:focus img {
			opacity: 0.5;
		}
	}

	img {
		opacity: 1;
		margin: 0 auto;

		&.lazyload {
			height: 0;
		}
	}
}

.emthemesModez-brandCarousel .slick-track {
	margin: 0 auto;

	.slick-slide {

	}
}


// ----------------------------------------------------------------------------
// Footer - Modern Style
// ----------------------------------------------------------------------------
.papaSupermarket-style--modern .footer {
	font-weight: $body-font-weight;

	.footer-info-heading {
		font-size: fontSize("small");
		text-transform: capitalize;
	}

	@include breakpoint("medium") {
		.footer-info-col,
		.footer-info-col--about {
			flex-basis: 20%;
			max-width: 20%;
			min-width: 20%;
		}

		.footer-info-col--brands {
			flex-basis: 40%;
			max-width: 40%;
			min-width: 40%;
		}

		.footer-info-list {
			li {
				position: relative;
				padding-left: spacing("half");

				&:before {
					content: '\f111';
					font-family: FontAwesome;
					font-size: 0.4rem;
					color: rgba(stencilColor("footer-color"), .3);
					position: absolute;
					top: 0.5rem;
					left: 0;
				}
			}
		}

		.emthemesModez-footer-topColumns {
			padding-top: spacing("single");
			padding-bottom: spacing("single");
		}

		.emthemesModez-newsletterForm {
			justify-content: space-around; // IE10 doesn't work with center value

			.footer-info-heading {
				margin-left: auto;
				font-size: fontSize("larger");
				text-transform: none;
			}

			.form-prefixPostfix .form-input {
				border: 0;
			}

			> .form {
				margin-left: 0;
				margin-right: auto;
			}
		}

		.header-logo-text {
		    font-size: fontSize("large");
		}
	}
}

// Cookie Consent
// ------------------------------------------------------------------------------------------------

#consent-manager-update-banner {
	position: static !important;
}

layouts/footer/footer.scss:
// =============================================================================
// FOOTER (CSS)
// =============================================================================


// Layout and columns
// -----------------------------------------------------------------------------
//
// 1. To enable the grid's display: inline-block; to work properly, we need to
//    remove the white-space that it creates between columns with font-size: 0;
// 2. Re-set the font-size that was reduced to 0 in point 1 for children.
//
// -----------------------------------------------------------------------------

.footer {
    background-color: $footer-background;
    border-top: container("border");
    padding: spacing("double") 0;
}

.footer-info {
    @include grid-row($behavior: nest);
    font-size: 0; // 1
}

.footer-info-col {
    @include grid-column(12);
    color: stencilColor("color-textSecondary");
    display: inline-block;
    float: none;
    font-size: fontSize("base"); // 2
    margin-bottom: spacing("double");
    text-align: center;
    vertical-align: top;

    @include breakpoint("small") {
        text-align: left;
        width: width("6/12");
    }

    @include breakpoint("medium") {
        width: width("4/12");
    }

    > :first-child {
        margin-top: 0;
    }

    > :last-child {
        margin-bottom: 0;
    }
}

.footer-info-col--small {
    @include breakpoint("medium") {
        width: width("2/12");
    }
}

.footer-info-col--social {
    width: 100%;
}

.footer-info-col--left {
    @include breakpoint("small") {
        padding: 0;
    }
}

.footer-info-col--right {
    @include breakpoint("small") {
        left: 50%;
        position: inherit;
    }

    @include breakpoint("medium") {
        left: 0;
        padding: 0;
        text-align: right;
    }
}


// Content
// -----------------------------------------------------------------------------

.footer-info-heading {
    color: $footer-heading-fontColor;
    font-size: fontSize("base");
    text-transform: inherit;
}

.footer-info-list {
    list-style: none;
    margin-left: 0;

    a {
        color: black;
        text-decoration: none;

        // scss-lint:disable NestingDepth
        &:hover {
            color: stencilColor("color-textSecondary--hover");
        }
        // scss-lint:enable NestingDepth
    }
}

.footer-copyright {
    text-align: center;

    @include breakpoint("small") {
        text-align: left;
    }

    > .powered-by {
        color: stencilColor("color-textSecondary");
        font-size: fontSize("tiny");
        margin: 0;
    }

    a {
        color: stencilColor("color-textSecondary");
        text-decoration: none;

        // scss-lint:disable NestingDepth
        &:hover {
            color: stencilColor("color-textSecondary--hover");
        }
        // scss-lint:enable NestingDepth
    }
}

.footer-payment-icons {
    height: remCalc(40);
    margin-top: spacing("single");

    @include breakpoint("medium") {
        margin-top: spacing("double");
    }

    .footer-payment-icon {
        height: 100%;
        margin: spacing("quarter");
        vertical-align: middle;
        width: remCalc(50);
    }

    svg {
        fill: stencilColor("icon-color");
    }
}

.footer-newsletter-summary {
    text-align: left;
}

layouts/header/header-emthemesModez.scss:
// =============================================================================
// HEADER (CSS)
//
// Custom header for theme emthemes-modez. Inherits css from the base theme.
//
// -----------------------------------------------------------------------------


.header {
    min-height: $header-height;
    box-shadow: 0 2px 5px rgba(color("blacks"), 0.2);
    border-bottom: 0;
    z-index: zIndex("high");

    @include breakpoint("medium") {
        border-bottom: 0;
        box-shadow: none;
        z-index: auto;

        &.is-sticky {
            position: fixed;
            z-index: zIndex("high");
        }
    }
}

.header-logo {
    > a {
        padding: 0;
        display: flex;
        align-items: center;
        justify-content: center;
        min-height: $header-height;
        width: 100%;

        @at-root .header-logo--left > a {
            justify-content: flex-start;
        }

        @at-root .header-logo--right > a {
            justify-content: flex-end;
        }

        &:hover {
            color: $storeName-color;
        }

        &:active {
            color: $storeName-color;
        }
    }

    @include breakpoint("medium") {
        margin-top: 0;
        margin-bottom: 0;

        > a {
            display: inline;
        }
    }
}

.header-logo-text {
    letter-spacing: letterSpacing("base");
    font-weight: fontWeight("normal");
    margin: 0;
}

.header-logo-image-container {
    width: get-width(stencilString('logo_size'));
    max-width: 100%;

    .header-logo-image {
        @include breakpoint("medium") {
            max-height: 100%;
        }
    }

}

