    .navUser-or {
        color: stencilColor("topHeader-color");
    }

    .navUser-action {
        color: stencilColor("topHeader-color");
        display: inline-flex;
        align-items: center;

        @if $theme-style == "compact" {
            padding: spacing("half") spacing("half") 0;
        }
        @else {
            padding: spacing("half");
        }

        svg {
            fill: stencilColor("topHeader-icon-color");
            stroke: stencilColor("topHeader-icon-color");
        }

        &:hover,
        &:focus,
        &:active {
