}

.emthemesModez-header-userSection--logo-left > .container {
    @include breakpoint("medium") {
        display: flex;
        justify-content: space-between;
        align-items: center;

        .navUser {
            order: 3;
            padding-right: 0;
        }

        .header-logo {
            order: 1;
            padding-right: spacing("single");
        }
    }
}

.emthemesModez-header-userSection--logo-right > .container {
    @include breakpoint("medium") {
        .navUser {
            order: 1;
            padding-left: 0;
        }

        .header-logo {
            order: 3;
            padding-left: spacing("single");
        }
    }
}


.emthemesModez-header-userSection--logo-center > .container {
    @include breakpoint("medium") {
        .navUser {
            order: 3;
            flex: 1 0 width("3/12");
            width: width("3/12");
            padding-right: 0;
            justify-content: flex-end;
        }

        .header-logo {
            order: 2;
            flex: 1 0 width("6/12");
            width: width("6/12");
            padding-left: spacing("single");
            padding-right: spacing("single");
        }

        .emthemesModez-quickSearch {
            order: 1;
            flex: 1 0 width("3/12");
            width: width("3/12");
        }
    }    
}

// ----------------------------------------------------------------------------
// Header User Section - Modern Style
// ----------------------------------------------------------------------------
.papaSupermarket-style--modern .emthemesModez-header-userSection {
    @include breakpoint("medium") {
        .container {
            padding-top: spacing("half");
            padding-bottom: spacing("quarter");
        }
        .navUser {
            &:before,
            &:after {
                display: none;
            }
        }

        .navUser-item-icon {
            font-size: fontSize("largest");
            color: stencilColor("navUser-color");

            &:before {
                vertical-align: baseline;
            }
        }

        .navUser-section {
            justify-content: flex-end;

            &:before,
            &:after {
                display: none;
            }
        }

        .navUser-item--account {
            align-items: center;

            .navUser-action {
                display: block;
            }

            .navUser-item-icon:before {
