.productGrid {
    display: flex;
    flex-wrap: wrap;
    align-items: stretch;
    margin-bottom: spacing("double");

    &:before,
    &:after {
        display: none;
    }

    .product {
        flex: 0 1 50%;
        display: flex;
        flex-direction: column;
        align-items: stretch;
        padding-left: 0;
        padding-right: 0;

        @include breakpoint("medium") {
            flex-basis: 25%;
        }

        @include ie-only() {
            display: block;
        }
    }
}

.productCarousel,
.productGrid {
    .card {
        padding: spacing("quarter");
        border: container("border");
        border-color: transparent;
        flex: 1;
        position: relative;

        @include breakpoint("small") {
            padding-bottom: spacing("quarter") + spacing("base") + buttonSize("small", "verticalPadding") * 2 + spacing("quarter");
        }

        @include breakpoint("medium") {
            padding: spacing("half");

            &:hover .card-buttons {
                visibility: visible;
                clip: auto;
                opacity: 1;
                padding-top: 0;
                z-index: zIndex("lower"); // equal badge's z-index
            }
        }

        .dropdown--quickSearch & {
            background-color: container("fill");
        }
    }

    .card-buttons {
        position: absolute;
        bottom: 0;
        left: 0;
        width: 100%;
        padding: spacing("half");
        display: none;

        @include breakpoint("small") {
            display: flex;
            align-items: flex-start;
        }

        @include breakpoint("medium") {
            visibility: hidden;
            clip: rect(0, 0, 0, 0);
            opacity: 0;
            transition: opacity 0.5s ease;
        }

        @mixin icon($icon) {
            &:before {
                text-indent: 0;
                float: left;
                font-family: FontAwesome;
                font-size: fontSize("base");
                content: '"' + unquote($icon) + '"';
                padding-right: spacing("quarter");
            }

            &.button--small {
                padding: buttonSize("small", "verticalPadding");
            }
        }

        @mixin hideText() {
            font-size: 0 !important;
            text-indent: -9999px;

            &:before {
                padding-right: 0;
            }
        }

        .card-figcaption-button {
            @include icon(\f07a);
            flex: 1;
            margin-left: spacing("quarter");
            font-size: fontSize("base");
            letter-spacing: 0;

            &:first-child {
                margin-left: 0;
            }

            .papaSupermarket-style--modern & {
                font-size: fontSize("smallest");
            }
        }

        .card-figcaption-button--compare {
            @include icon(\f0ec);
            @include hideText();

            flex: 0;

            input[type="checkbox"], 
            input[type="radio"] {
                width: 0;
                height: 0;
                clip: rect(0, 0, 0, 0);
            }
        }

        .card-figcaption-button.quickview {
            @include icon(\f002);
            white-space: nowrap;
        }
    }


    .card-buttons--alt {
        position: absolute;
        bottom: auto;
        top: 50%;
        left: 0;
        right: 0;
        width: auto;
        padding: 0;
        transform: translateY(-50%);
        justify-content: center;

        // hide by default
        visibility: hidden;
        clip: rect(0, 0, 0, 0);
        opacity: 0;
        transition: opacity 0.5s ease;

        @at-root .card:hover .card-buttons--alt {
            visibility: visible;
            clip: auto;
            opacity: 1;
        }

        .card-figcaption-button {
            flex: 0 1 auto;
        }
    }
}

