.mode-1 .productGrid {
    .product {
        width: 100%;
        flex-basis: 100%;
    }

    .card {
        @include clearfix;
    }

    .card-figure {
        width: calc(50% - #{spacing("quarter")});
        float: left;

        @include breakpoint("small") {
            width: calc(#{width('4/12')} - #{spacing("single")*2/3});
        }
    }

    // .card-figcaption {
    //     opacity: 1;
    //     display: block;
    //     left: calc(100% + #{spacing("single")});
    //     top: auto;
    //     right: calc(-100% - #{spacing("single")});
    // }

    // .card-buttons {
    //     display: flex;
    //     padding: 0;
    //     flex-wrap: wrap;
    // }

    .card-body {
        width: calc(50% - #{spacing("quarter")});
        float: right;
        text-align: left;
        padding-top: 0;

        @include breakpoint("small") {
            width: calc(#{width('5/12')} - #{spacing("single")*2/3});
            float: left;
            margin-left: spacing("single");
        }

        .card-text--price {
            display: none;
        }
    }

    .card-title {
        @if $theme-style == "modern" {
            font-weight: fontWeight("bold");
        }
    }

    .card-text--summary {
        @include breakpoint("small") {
            display: block;
        }
    }

    .card-info {
        @if $theme-card-info {
            display: block;
        }
    }

    .card-aside {
        width: calc(50% - #{spacing("half")});
        display: block;
        float: right;
        clear: right;

        @include breakpoint("small") {
            width: calc(#{width('3/12')} - #{spacing("single")*2/3});
            text-align: center;
        }

        @include breakpoint("xxlarge") {
            width: calc(#{width('2/12')} - #{spacing("single")*2/3});
        }

        .price {
            font-size: fontSize("small");
            font-weight: fontWeight("bold");
        }

        .card-buttons {
            position: static;
            display: block;
            padding: 0;
            visibility: visible;
            clip: none;
            opacity: 1;

            .card-figcaption-button {
                margin-left: 0;
                // text-align: left;
                &:before {
                    min-width: remCalc(20);
                    // float: none;
                    // display: inline;
                }
            }

            .card-figcaption-button--compare {
                width: 100%;
                float: none;
                font-size: fontSize("base") !important;
                text-indent: 0;

                &:before {
                    @media (max-width: screenSize("medium") - 1px) {
                        display: none;
                    }
                }

                + .card-figcaption-button.quickview {
                    margin-left: spacing("quarter");

                    @include breakpoint("small") {
                        margin-left: 0;
                    }
                }
            }

            .card-figcaption-button.quickview {
                float: left;
                font-size: 0;

                &:before {
                    padding-right: 0;
                }

                @include breakpoint("small") {
                    float: none;
                    font-size: fontSize("base");

                    &:before {
                        padding-right: spacing("quarter");
                    }
                }
            }
        }
    }

    //
    // Styling product list with detailed info on Mobile
    //
    @if $theme-card-info {
        @media (max-width: screenSize("small") - 1px) {

            @if $theme-style != "modern" {
                .product + .product {
                    border-top: container("border");
                    padding-top: spacing("single");
                }
            }

            .card-figure {
                margin-bottom: spacing("half");
            }

            .card-body {
                display: inline;
                float: none;

                .card-text--price {
                    display: block;
                }
            }

            .card-title,
            .card-text {
                max-width: calc(50% - #{spacing("quarter") + spacing("half")});
                margin-left: calc(50% + #{spacing("half")});
            }

            .card-text--summary {
                display: block;
                max-width: none;
                margin-left: 0;
                clear: both;
            }

            .card-info {
                clear: both;
                column-count: 2;
                column-gap: spacing("half");
            }

            .card-aside {
                float: none;
                width: 100%;
                margin-top: spacing("half");

                .card-text--price {
                    display: none;
                }

                .card-buttons {
                    display: flex;

                    .button + .button {
                        margin-left: spacing("quarter");
                    }
                }
            }
        }
    }
}


// ============================================================================
// Modern Style
// ============================================================================
.papaSupermarket-style--modern {
    .productGrid {
        margin: 0;

        @include breakpoint("medium") {
            margin-left: -(spacing("quarter"));
            margin-right: -(spacing("quarter"));
        }

        .product {
            padding: 0 spacing("quarter") spacing("half");

            @include ie-only() {
                flex-basis: auto;
            }
        }
    }

    @include ie-only() {
        .mode-1 .productGrid .product {
            flex-basis: 100%;
        }

        .mode-2 .productGrid .product {
            flex-basis: calc(50% - #{spacing("half")});
        }

        .mode-3 .productGrid .product {
            flex-basis: calc(50% - #{spacing("half")});

            @include breakpoint("medium") {
                flex-basis: calc(33.33% - #{spacing("half")});
            }
        }

        .mode-4 .productGrid .product {
            flex-basis: calc(50% - #{spacing("half")});

            @include breakpoint("medium") {
                flex-basis: calc(25% - #{spacing("half")});
            }
        }

        .mode-6 .productGrid .product {
            flex-basis: calc(50% - #{spacing("half")});

            @include breakpoint("medium") {
                flex-basis: calc(16.66% - #{spacing("half")});
            }
        }
    }

    .card {
        margin-bottom: 0;
    }

    .productCarousel,
    .productGrid {
        .card {
            border-color: container("borderColor");
            border-radius: $container-border-radius;
            box-shadow: 0 3px 0 container("borderColor");

            @include ie-only() {
                height: 100%;
            }
        }
    }
}

layouts/products/productGrid.scss:
// =============================================================================
// PRODUCT GRID (CSS)
// =============================================================================


// Product grid view
// -----------------------------------------------------------------------------
//
// 1. Re-set the font-size, as we're reducing it to 0 in the .products class.
//
// -----------------------------------------------------------------------------

.productGrid {

    @include u-listBullets("none");
    @include grid-row($behavior: "nest");
    font-size: 0; // 1
    margin-bottom: spacing("single");

    .product {
        @include grid-column(6, $float: none);
        display: inline-block;
        font-size: fontSize("base"); // 1
        vertical-align: top;

        @include breakpoint("small") {
            width: grid-calc(4, $total-columns);
        }

        @include breakpoint("medium") {
            width: grid-calc(3, $total-columns);
        }
    }
    
    .card:hover {
        border: 1px solid gray;
    }
}


// Product when sidebar is present
// -----------------------------------------------------------------------------
//
// 1. When the sidebar is visible, reduce products to 3 across.
//
// -----------------------------------------------------------------------------

.page-sidebar + .page-content {

    .productGrid {
        // scss-lint:disable SelectorDepth, NestingDepth
        .product { // 1
            @include breakpoint("small") {
                width: grid-calc(4, $total-columns);
            }
        }
    }

}


//
// Product Grid with masonry layout
//
// Purpose: Display products in a masonry grid, with a display inline-block for
// older browsers that don't support css multicolumn
//
// -----------------------------------------------------------------------------

.productMasonry {
    column-count: 2;
    column-fill: auto;
    column-gap: remCalc(20px);
    margin: 0 0 spacing("single");
    overflow: hidden;

    @include breakpoint("small") {
        column-count: 3;
    }

    @include breakpoint("medium") {
        column-count: 4;
    }

    .product {
        break-inside: avoid;
        page-break-inside: avoid; // for firefox
        display: block;
        margin-bottom: spacing("double");
        padding: 0;
        width: 100%;
    }

        .card {
            margin: 0;
        }
    }


// Product grid view variation
// -----------------------------------------------------------------------------

.productGrid--maxCol6 {
    .no-csscolumns & {
        // scss-lint:disable SelectorDepth, NestingDepth
        .product {
            @include breakpoint("medium") {
                width: grid-calc(2, $total-columns);
            }
        }
    }

    .csscolumns & {
        @include breakpoint("medium") {
            // emthemesModez: fix stencil bug
            // column-count: 6;
            @include grid-column(12);
            // ---

            // scss-lint:disable SelectorDepth, NestingDepth
            .product {
                @include grid-column(2);
            }
        }
    }
}

.productGrid--maxCol3 {
    .no-csscolumns & {
        // scss-lint:disable SelectorDepth, NestingDepth
        .product {
            @include breakpoint("medium") {
                width: grid-calc(4, $total-columns);
            }
        }
    }

    .csscolumns & {
        @include breakpoint("medium") {
            @include grid-column(12);

            float: none;

            // scss-lint:disable SelectorDepth, NestingDepth
            .product {
                @include grid-column(4, $float: none);
            }
        }
    }
}

layouts/products/productList-emthemesMod:
