			transition: opacity 0.5s ease;
			position: absolute;
			left: spacing("half");
			bottom: -(spacing("quarter"));
			flex-wrap: wrap;
			background: container("fill");
		}

		.button {
			flex: 0;
			margin-right: spacing("quarter");

			@include breakpoint("medium") {
				margin-bottom: spacing("quarter");
			}

			&:first-child {
				flex: 1;
			}

			+ .button {
				margin-top: 0;
				margin-left: 0;
			}
		}
	}


	.button.quickview {
		font-size: 0;
		content: '';
		padding-left: spacing("half");
		padding-right: spacing("half");

		&:before {
			content: '\f002';
			font-family: FontAwesome;
			font-size: fontSize("base");
			vertical-align: middle;
		}
	}
}

.productList--maxCol4 {
	.product {

        @include breakpoint("medium") {
            width: grid-calc(3, $total-columns);
        }

		&:nth-child(2n+1) {
			@include breakpoint("small") {
				clear: both;
			}
			@include breakpoint("medium") {
				clear: none;
			}
        }

        &:nth-child(4n+1) {
			@include breakpoint("medium") {
				clear: both;
			}
        }
	}
}

.productList--maxCol1,
.sidebarBlock .productList {
	@include breakpoint("medium") {
		.product {
			width: grid-calc(12, 12);
		}

		.product:last-child .listItem {
			margin-bottom: 0;
			padding-bottom: 0;
			border-bottom: 0;
		}
	}
}

.sidebarBlock .productList {
	@include breakpoint("medium") {
		.product {
			padding: 0;
		}

		.listItem-figure {
			padding-right: 0;
		}
	}
}

.page-sidebar + .page-content .productList--maxCol1 {
	.listItem-details {
		@include breakpoint("large") {
			padding-right: 0;
		}
	}

	.listItem-actions {
		@include breakpoint("large") {
			width: auto;
		}
	}
}

layouts/products/productList.scss:
// =============================================================================
// PRODUCT LIST (CSS)
// =============================================================================


// Product list
// -----------------------------------------------------------------------------

.productList {
    @include u-listBullets("none");

    .product {
        // scss-lint:disable NestingDepth
        + .product {
            margin-top: spacing("base") * 4;
        }
    }
}


// Product list item
// -----------------------------------------------------------------------------

.listItem {

    @include breakpoint("small") {
        @include grid-row($behavior: "nest");
        display: table;
    }
}

.listItem-figure,
.listItem-body {

    @include breakpoint("small") {
        display: table-cell;
        vertical-align: top;
    }

    @include breakpoint("large") {
        vertical-align: middle;
    }
}


// List figure
// -----------------------------------------------------------------------------

.listItem-figure {
    margin: 0 0 spacing("single");

    @include breakpoint("small") {
        margin-bottom: 0;

        @include breakpoint("small") {
            padding-left: spacing("half");
            padding-right: spacing("half");
            width: grid-calc(3, $total-columns);
        }
    }
}

.listItem-image {
    width: 100%;
}

.listItem-body {

    @include breakpoint("small") {
        padding-left: spacing("base") + spacing("sixth");
        padding-right: spacing("half");
        width: grid-calc(9, $total-columns);
    }
}


// List content
// -----------------------------------------------------------------------------

.listItem-content {

    @include breakpoint("large") {
        display: table;
    }
}

.listItem-details,
.listItem-actions {

    @include breakpoint("large") {
        display: table-cell;
        vertical-align: top;
    }
}


// List details
// -----------------------------------------------------------------------------

.listItem-details {

    @include breakpoint("large") {
        padding-right: spacing("double") * 2;

        > :last-child {
            margin-bottom: 0;
        }
    }
}

.listItem-rating {
    margin-bottom: spacing("quarter");
}

.listItem-brand {
    color: color("greys", "light");
    font-size: fontSize("base");
    margin: 0 0 spacing("eighth");

    @include breakpoint("medium") {
        font-size: fontSize("smaller");
        margin-bottom: 0;
    }
}

.listItem-title {
    font-size: fontSize("smaller");
    margin: 0 0 spacing("eighth");

    @include breakpoint("medium") {
        font-size: fontSize("small");
    }

    > a {
        text-decoration: none;
    }
}

.listItem-price {
    margin-bottom: spacing("base");

    @include breakpoint("medium") {
        font-size: fontSize("small");
    }
}


// List actions
// -----------------------------------------------------------------------------
//
// 1. Magic number width to align closer to the intended design.
//
// -----------------------------------------------------------------------------

.listItem-actions {

    @include breakpoint("large") {
        vertical-align: middle;
        width: 22%; // 1

        .button--compare {
            @include buttonSize("small");
        }
    }

    .button {
        margin-bottom: 0;

        @include breakpoint("large") {
            width: 100%;

            // scss-lint:disable NestingDepth
            + .button {
                margin: spacing("half") 0 0;
            }
        }
    }
}


// Product list when sidebar is present
// -----------------------------------------------------------------------------

.page-sidebar + .page-content {

    .productList {

        // scss-lint:disable SelectorDepth, NestingDepth
        .listItem-details {

            @include breakpoint("large") {
                padding-right: spacing("double");
            }
        }

        .listItem-actions {

            @include breakpoint("large") {
                width: 30%;
            }
        }
    }
}

layouts/products/productSaleBadges-emthe:
.sale-flag-side {
    border-radius: 0;
    font-size: fontSize("smallest");
    height: auto;
    padding: spacing("quarter") / 2 spacing("quarter");
    top: 0;
    left: auto;
    right: 0;
    z-index: zIndex("lowest"); // lower than mega menu

