    $h: remCalc(fontSize("smallest") * lineHeight("base") + 5px)+spacing("quarter");

    + .sale-flag-side {
        top: $h;
    }

    + .sale-flag-side + .sale-flag-side {
        top: $h * 2;
    }

    + .sale-flag-side + .sale-flag-side + .sale-flag-side {
