}

.sidebarBlock--mobileCollapsible {
	.sidebarBlock-heading {
		position: relative;
		padding-right: spacing("double");
		margin-bottom: -(container("padding"));

		&:after {
			font-family: FontAwesome;
			content: '\f105';
			position: absolute;
			top: spacing("half");
			right: spacing("single");
		}

		&.is-open {
			margin-bottom: spacing("single");
			&:after {
				content: '\f107';
