pages/subscribed:
---
blog:
    recent_posts:
        limit: {{theme_settings.homepage_blog_posts_count}}
shop_by_brand:
    limit: {{theme_settings.max_shop_by_brand}}
---
{{#partial "page"}}

<section class="page">

    {{#if theme_settings.layout '===' 'left-sidebar'}}
    <div class="page-sidebar">
        {{> components/papa-supermarket/home/sidebar}}
    </div>
    {{/if}}

    <div class="page-content {{#if theme_settings.layout '!==' 'left-sidebar'}}page-content--centered{{/if}}">
        <h1 class="page-heading">
            {{#if subscription.error}}
                {{lang 'newsletter.subscribed_heading_error'}}
            {{else}}
                {{lang 'newsletter.subscribed_heading'}}
            {{/if}}
        </h1>

        {{#if subscription.error}}
            {{> components/common/alert-error subscription.error}}
        {{else}}
            {{> components/common/alert-success (lang 'newsletter.subscribed_message')}}
        {{/if}}

    </div>
</section>

{{/partial}}
{{> layout/base}}

components/papa-supermarket/sidebar/subp:
{{#if page.sub_pages}}
<div class="sidebarBlock sidebarBlock--navList sidebarBlock--mobileCollapsible">
    <h5 class="sidebarBlock-heading sidebarBlock-heading--subpages is-open" data-collapsible="#subpages-navList" data-collapsible-disabled-breakpoint="medium" aria-expanded="true">{{page.title}}</h5>
    <ul id="subpages-navList" class="navList is-open" aria-hidden="false">
        {{#each page.sub_pages}}
        <li class="navList-item"><a class="navList-action" href="{{url}}" title="{{title}}">{{title}}</a></li>
        {{/each}}
    </ul>
</div>
{{/if}}

components/papa-supermarket/sidebar/face:
{{#if theme_settings.fb_page '!==' ''}}
<div class="sidebarBlock sidebarBlock--fbPage emthemesModezBannersBlockSidebar02">
    <div class="fb-page" data-href="https://www.facebook.com/{{theme_settings.fb_page}}/" data-tabs="messages" data-height="300" data-small-header="false" data-adapt-container-width="true" data-hide-cover="false" data-show-facepile="true"><blockquote cite="https://www.facebook.com/{{theme_settings.fb_page}}/" class="fb-xfbml-parse-ignore"><a href="https://www.facebook.com/{{theme_settings.fb_page}}/">{{theme_settings.fb_page}}</a></blockquote></div>
    {{#if is_ajax}}
    <script>FB.XFBML.parse()</script>
    {{/if}}
</div>
{{/if}}

components/papa-supermarket/sidebar/blog:
{{#if posts}}
<div class="sidebarBlock sidebarBlock--blog">
    <h5 class="sidebarBlock-heading" data-collapsible="#sidebar-blog-navList" data-collapsible-disabled-breakpoint="medium" aria-expanded="false">{{lang 'blog.recent_posts'}}</h5>
    <ul class="navList">
        {{#each posts}}
        <li class="navList-item">
            {{> components/blog/post post=this hide_social_icons=true settings=../settings hide_summary=../hide_summary hide_readmore=../hide_readmore}}
        </li>
        {{/each}}
    </ul>
</div>
{{/if}}

components/common/header:
{{#or banners.top_metadata theme_settings.instantload}}
<div class="banners" data-banner-location="top">
    {{#if settings.data_tag_enabled}}
        {{#each banners.top_metadata}}
            {{#if (occurrences this.content 'emthemesModezBannersBlock') '===' 0}}
                <div class="banner">
                    {{{this.content}}}
                </div>
            {{/if}}
        {{/each}}
    {{else}}
        {{#each banners.top_metadata}}
            {{#if (occurrences this.content 'emthemesModezBannersBlock') '===' 0}}
                <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-banner-id="{{this.banner-name}}">
                    <div data-event-type="promotion-click">
                        {{{this.content}}}
                    </div>
                </div>
            {{/if}}
        {{/each}}
    {{/if}}
</div>
{{/or}}
{{#if theme_settings.bannerAdvTopHeader '===' 'all'}}
    <div data-emthemesmodez-remote-banner="emthemesModezBannersBlockAdvTopHeader"></div>
{{else if theme_settings.bannerAdvTopHeader '===' 'nothome'}}
    {{#if page_type '!==' 'default'}}
    <div data-emthemesmodez-remote-banner="emthemesModezBannersBlockAdvTopHeader"></div>
    {{/if}}
{{/if}}
<header class="header" role="banner" {{#if theme_settings.header_sticky '===' 'all'}} data-stickymenu{{/if}}>
    <a href="#" class="mobileMenu-toggle" data-mobile-menu-toggle="menu">
        <span class="mobileMenu-toggleIcon">Toggle menu</span>
    </a>

    <div class="emthemesModez-header-topSection">
        <div class="container">
            {{> components/common/navigation-top}}
        </div>
    </div>

    <div class="emthemesModez-header-userSection emthemesModez-header-userSection--logo-{{theme_settings.logo-position}}"{{#if theme_settings.header_sticky '===' 'search'}} data-stickymenu{{/if}}>
        <div class="container">
            {{> components/common/navigation}}

            {{#if template_file '===' 'pages/home'}}
                <h1 class="header-logo header-logo--{{theme_settings.logo-position}}">
                    {{> components/common/store-logo}}
                </h1>
            {{else}}
                <div class="header-logo header-logo--{{theme_settings.logo-position}}">
                    {{> components/common/store-logo}}
                </div>
            {{/if}}

            {{> components/common/quick-search}}
        </div>
    </div>

    <div class="navPages-container{{#if theme_settings.navPages_bg_stretch}} navPages-container--bg{{/if}}" id="menu" data-menu>
        <div class="container">
            {{> components/common/navigation-menu}}
        </div>
    </div>

</header>

{{> components/common/quick-search style="mobileOnly"}}

<div class="dropdown dropdown--quickSearch" id="quickSearch" aria-hidden="true" tabindex="-1" data-prevent-quick-search-close>
    <div class="container">
        <section class="quickSearchResults" data-bind="html: results"></section>
    </div>
</div>
<div class="emthemesModez-overlay"></div>

{{{region name="header_bottom--global"}}}
{{{region name="header_bottom"}}}

{{#if theme_settings.bannerAfterHeader '===' 'all'}}
    <div data-emthemesmodez-remote-banner="emthemesModezBannersBlockAfterHeader"></div>
{{else if theme_settings.bannerAfterHeader '===' 'nothome'}}
    {{#if page_type '!==' 'default'}}
        <div data-emthemesmodez-remote-banner="emthemesModezBannersBlockAfterHeader"></div>
    {{/if}}
{{else if theme_settings.bannerAfterHeader '===' 'onlyhome'}}
    {{#if banners.top_metadata}}
        {{#each banners.top_metadata}}
            {{#if (occurrences this.content 'emthemesModezBannersBlockAfterHeader') '!==' 0}}
                {{{this.content}}}
            {{/if}}
        {{/each}}
    {{/if}}
{{/if}}

components/common/footer:
{{#or banners.bottom theme_settings.instantload}}
<div class="container">
    {{#if settings.data_tag_enabled}}
        <div class="banners banners--bottom" data-banner-location="bottom">
            {{#each (limit banners.bottom_metadata 1)}}
                <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                    <div data-event-type="promotion-click">
                        {{{this.content}}}
                    </div>
                </div>
            {{/each}}
        </div>
    {{else}}
        <div class="banners banners--bottom" data-banner-location="bottom">
            <div class="banner">
                {{{limit banners.bottom 1}}}
            </div>
        </div>
    {{/if}}
</div>
{{/or}}

{{{region name="footer_before"}}}
{{{region name="footer_before--global"}}}

{{#if settings.show_newsletter_box}}
    {{#if theme_settings.nl_popup_show '===' 'home'}}
        {{#if page_type '===' 'default'}}
            {{> components/emthemes-modez/newsletter/modal}}
        {{/if}}
    {{else if theme_settings.nl_popup_show '===' 'all'}}
        {{> components/emthemes-modez/newsletter/modal}}
    {{/if}}
{{/if}}

<footer class="footer" role="contentinfo">
    {{#if settings.show_newsletter_box}}
    {{#if theme_settings.footer_show_newsletter}}
    <div class="emthemesModez-footer-topColumns-wrapper">
        <div class="container">
            <div class="emthemesModez-footer-topColumns">
                <div class="emthemesModez-footer-newsletter">
                    {{> components/emthemes-modez/newsletter/section}}
                </div>
            </div>
        </div>
    </div>
    {{/if}}
    {{/if}}

    {{{region name="newsletter_below"}}}
    {{{region name="newsletter_below--global"}}}


    <div class="container">
        <section class="footer-info">

            {{#if theme_settings.footer_show_aboutus}}
                <article class="footer-info-col footer-info-col--about" data-section-type="storeInfo">
                    {{{region name="footer_before_logo--global"}}}
                    <div class="footer-info-heading">
                        <div class="footer-logo">
                            {{> components/common/store-logo}}
                        </div>
                    </div>
                    {{{region name="footer_below_logo--global"}}}

                    {{#if theme_settings.footer_show_contact}}
                        <ul class="fa-ul">
                            <li><i class="fa fa-li fa-map-marker"></i><address>{{nl2br settings.address}}</address></li>
                            {{#if settings.phone_number}}
                            <li><i class="fa fa-li fa-phone"></i><a href="tel:{{settings.phone_number}}">{{lang 'footer.call_us' phone_number=settings.phone_number}}</a></li>
                            {{/if}}
                        </ul>
                    {{/if}}

                    {{#if theme_settings.social_icon_placement_bottom}}
                        {{> components/common/social-links style="alt"}}
                    {{/if}}

                    {{{region name="footer_below_contact--global"}}}
                </article>
            {{/if}}


            {{#if theme_settings.footer_show_pages '>' -2}}
                <article class="footer-info-col" data-section-type="footer-webPages">
                    <h5 class="footer-info-heading">{{lang 'footer.navigate'}}</h5>
                    <ul class="footer-info-list">
                        {{#if theme_settings.footer_show_pages '>' -1}}
                            {{#each (limit pages theme_settings.footer_show_pages)}}
                                <li>
                                    <a href="{{url}}">{{name}}</a>
                                </li>
                            {{/each}}
                        {{else}}
                            {{#each pages}}
                                <li>
                                    <a href="{{url}}">{{name}}</a>
                                </li>
                            {{/each}}
                        {{/if}}
                        <li>
                            <a href="{{urls.sitemap}}">{{lang 'sitemap.page_title'}}</a>
                        </li>
                    </ul>
                    {{{region name="footer_below_pages--global"}}}
                </article>
            {{/if}}

            {{#if theme_settings.footer_show_categories '>' -1}}
                <article class="footer-info-col" data-section-type="footer-categories">
                    <h5 class="footer-info-heading">{{lang 'footer.categories'}}</h5>
                    <ul class="footer-info-list">
                        {{#if theme_settings.footer_show_categories '>' 0}}
                            {{#each (limit categories theme_settings.footer_show_categories)}}
                                <li>
                                    <a href="{{url}}">{{name}}</a>
                                </li>
                            {{/each}}
                        {{else}}
                            {{#each categories}}
                                <li>
                                    <a href="{{url}}">{{name}}</a>
                                </li>
                            {{/each}}
                        {{/if}}
                    </ul>
                    {{{region name="footer_below_categories--global"}}}
                </article>
            {{/if}}

            {{#if theme_settings.footer_show_links_1}}
                {{> components/emthemes-modez/common/footer-links index=1}}
            {{/if}}

            {{#if theme_settings.footer_show_links_2}}
                {{> components/emthemes-modez/common/footer-links index=2}}
            {{/if}}

            {{#if theme_settings.footer_show_links_3}}
                {{> components/emthemes-modez/common/footer-links index=3}}
            {{/if}}

            {{#if theme_settings.footer_show_links_4}}
                {{> components/emthemes-modez/common/footer-links index=4}}
            {{/if}}


            {{#if theme_settings.show_shop_by_brand}}
                <article class="footer-info-col footer-info-col--brands" data-section-type="footer-brands">
                    <h5 class="footer-info-heading">{{lang 'footer.brands'}}</h5>
                    <ul class="footer-info-list">
                        {{#each (limit shop_by_brand theme_settings.show_shop_by_brand)}}
                            <li>
                                <a href="{{url}}">{{name}}</a>
                            </li>
                        {{/each}}
                        <li><a href="{{urls.brands}}">{{lang 'common.view_all'}}</a></li>
                    </ul>
                    {{{region name="footer_below_brands--global"}}}
                </article>
            {{/if}}

        </section>
        {{{region name="footer_below_columns--global"}}}
    </div>

    <div class="footer-copyright">
        <div class="container">
            <p class="powered-by">
                {{#if theme_settings.show_copyright_footer}}
                    &copy; <span id="copyright_year"></span> {{settings.store_name}}.
                    <script>
                        document.getElementById("copyright_year").innerHTML = new Date().getFullYear();
                    </script>
                {{/if}}
                {{#if theme_settings.show_powered_by}}
                    {{lang 'footer.powered_by'}} <a href="http://bigcommerce.com" rel="nofollow">BigCommerce</a>. Theme designed by <a href="https://papathemes.com" rel="nofollow">Papathemes</a>.
                {{/if}}
            </p>

            {{> components/common/payment-icons}}

            {{{region name="ssl_site_seal--global"}}}
        </div>
    </div>
</footer>

{{{region name="footer_bottom--global"}}}

components/common/navigation-list:
{{#if children}}
<a class="navPages-action has-subMenu {{#if menu_type '==' 'vertical'}}navPages-action--vertical{{/if}}" href="{{url}}"
    {{#if menu_type '!=' 'vertical'}}data-instantload{{/if}}
    {{#if id '==' category.id}}data-current-category{{/if}}
    data-collapsible="navPages-{{id}}{{idSuffix}}"
    {{#if menu_type '!=' 'vertical'}}data-collapsible-disabled-breakpoint="medium"{{/if}}
    data-collapsible-disabled-state="open"
    data-collapsible-enabled-state="closed">
    {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
</a>
<div class="navPage-subMenu
        {{#if menu_type '==' 'vertical'}}
            navPage-subMenu--vertical
        {{else if menu_type '==' 'standard'}}
            navPage-subMenu--standard
        {{/if}}
    " id="navPages-{{id}}{{idSuffix}}" aria-hidden="true" tabindex="-1">
    {{#if theme_settings.navPages_banners}}
        {{#if menu_type '==' 'mega'}}
            <div class="navPage-subMenu-rightBanners" data-emthemesmodez-remote-banner="emthemesModezBannersBlockMegaMenuRight{{@index}}"></div>
        {{/if}}
    {{/if}}
    <ul class="navPage-subMenu-list">
        <li class="navPage-subMenu-item">
            <a class="navPage-subMenu-action navPages-action" href="{{url}}" data-instantload>{{lang 'category.view_all.name' category=name}}</a>
        </li>
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if children}}
                    <a
                        class="navPage-subMenu-action navPages-action has-subMenu"
                        href="{{url}}"
                        {{#if ../../menu_type '!=' 'vertical'}}data-instantload{{/if}}
                        {{#if id '==' ../../category.id}}data-current-category{{/if}}
                        data-collapsible="navPages-{{id}}{{../../idSuffix}}"
                        {{#if ../../menu_type '!=' 'vertical'}}data-collapsible-disabled-breakpoint="medium"{{/if}}
                        data-collapsible-disabled-state="open"
                        data-collapsible-enabled-state="closed">
                        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}{{../../idSuffix}}">
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action" href="{{url}}" data-instantload>{{lang 'category.view_all.name' category=name}}</a>
                        </li>
                        {{#each children}}
                        <li class="navPage-childList-item">
                            {{> components/common/navigation-list-child 
                                    category_id=../../../category.id 
                                    idSuffix=../../../idSuffix 
                                    theme_settings=../../../theme_settings
                                    menu_type=../../../menu_type}}
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action navPages-action" href="{{url}}" data-instantload {{#if id '==' ../../category.id}}data-current-category{{/if}}>{{name}}</a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
    {{#if theme_settings.navPages_banners}}
        {{#if menu_type '==' 'mega'}}
            <div class="navPage-subMenu-bottomBanners" data-emthemesmodez-remote-banner="emthemesModezBannersBlockMegaMenuBottom{{@index}}"></div>
        {{/if}}
    {{/if}}
</div>
{{else}}
<a class="navPages-action" href="{{url}}" data-instantload {{#if id '==' category.id}}data-current-category{{/if}}>{{name}}</a>
{{/if}}

components/products/list-item:
{{#if settings.data_tag_enabled}}
    <article class="listItem"
        data-event-type="{{event}}"
        data-entity-id="{{id}}"
        data-position="{{position}}"
        data-name="{{name}}"
        data-product-category="{{#each category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}"
        data-product-brand="{{brand.name}}"
        data-product-price="
            {{#or customer (unless theme_settings.restrict_to_login)}}
                {{#if price.with_tax}}
                    {{price.with_tax.value}}
                {{else}}
                    {{price.without_tax.value}}
                {{/if}}
            {{else}}
                {{lang 'common.login_for_pricing'}}
            {{/or}}
        ">
{{else}}
    <article class="listItem" >
{{/if}}
    <figure class="listItem-figure">
        {{#if theme_settings.product_outstock_badge}}
            {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                {{#if out_of_stock_message }}
                    <div class="sale-flag-side sale-flag-side--outstock">
                        <span class="sale-text">{{out_of_stock_message}}</span>
                    </div>
                {{/if}}
            {{/or}}
        {{/if}}
        {{#or price.non_sale_price_with_tax price.non_sale_price_without_tax}}
            {{#if theme_settings.product_sale_badges '===' 'sash'}}
                <div class="sale-flag-sash">
                    <span class="sale-text">{{lang 'emthemesmodez.on_sale'}}</span>
                </div>
            {{else if theme_settings.product_sale_badges '===' 'topright'}}
                <div class="sale-flag-side">
                    <span class="sale-text">{{lang 'emthemesmodez.on_sale'}}</span>
                </div>
            {{else if theme_settings.product_sale_badges '===' 'burst'}}
                <div class="starwrap">
                    <div class="sale-flag-star"></div>
                    <div class="sale-text-burst">{{lang 'emthemesmodez.on_sale'}}</div>
                </div>
            {{/if}}
        {{/or}}
        {{#if theme_settings.product_custom_badges}}
            {{#each custom_fields}}
                {{#if name '===' '__badge'}}
                    <div class="sale-flag-side sale-flag-side--custom">
                        <span class="sale-text">{{{value}}}</span>
                    </div>
                {{/if}}
            {{/each}}
        {{/if}}
        {{> components/common/responsive-img
            image=image
            class="listItem-image"
            fallback_size=theme_settings.productgallery_size
            lazyload=theme_settings.lazyload_mode
            default_image=theme_settings.default_image_product
            otherAttributes=(getVar 'productCardImageSizeAttrs')
        }}
    </figure>
    <div class="listItem-body">
        <div class="listItem-content">
            <div class="listItem-details">
                <h4 class="listItem-title">
                    <a href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}>{{name}}</a>
                </h4>
                {{#if brand.name}}
                <p class="listItem-brand">{{brand.name}}</p>
                {{/if}}
                {{#if price}}
                    <div class="listItem-price">
                        {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                            {{> components/products/price price=price customer=customer}}
                        {{else}}
                            {{> components/common/login-for-pricing}}
                        {{/or}}
                    </div>
                {{/if}}
                {{#and rating show_rating}}
                    <p class="listItem-rating"><span class="rating--small">{{> components/products/ratings rating=rating}}</span></p>
                {{/and}}
                {{#if summary}}
                    <p>{{{summary}}}</p>
                {{/if}}
            </div>
            <div class="listItem-actions">
                <div class="listItem-buttons">
                {{#if show_cart_action}}
                {{#unless hide_add_to_cart}}
                    {{#if has_options}}
                        <a href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}} class="button button--small button--primary{{#unless hide_product_quick_view}}{{#if theme_settings.show_product_quick_view}}{{#if theme_settings.ajax_add_to_cart}} quickview-alt{{/if}}{{/if}}{{/unless}}" data-product-id="{{id}}">{{lang "products.choose_options"}}</a>
                    {{else}}
                        {{#if out_of_stock_message }}
                        <a href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}} class="button button--small button--primary">{{out_of_stock_message}}</a>
                        {{else}}
                        <a href="{{add_to_cart_url}}" {{#if settings.data_tag_enabled}}{{#unless theme_settings.ajax_add_to_cart}} data-event-type="product-click"{{/unless}}{{/if}} class="button button--small button--primary"{{#if theme_settings.ajax_add_to_cart}} data-emthemesmodez-cart-item-add{{/if}}>{{lang "products.add_to_cart"}}</a>
                        {{/if}}
                    {{/if}}
                {{/unless}}
                {{/if}}
                {{#if show_compare}}
                <a class="button button--small button--compare" href="#" data-compare-id="{{id}}">{{lang 'products.compare'}}</a>
                {{/if}}
                {{#unless hide_product_quick_view}}
                    {{#if theme_settings.show_product_quick_view}}
                        <a {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}} class="button button--small quickview" data-product-id="{{id}}">{{lang 'products.quick_view'}}</a>
                    {{/if}}
                {{/unless}}
                </div>
            </div>
        </div>
    </div>
</article>

components/common/payment-icons:
