components/amp/css/base:
*,
*:after,
*:before {
    box-sizing: border-box;
}
html {
    font-size: 14px
}
html,
body {
    height: auto
}

body {
    font-size: 1rem;
    font-family: Roboto, Arial, Helvetica, sans-serif;
    font-weight: 400;
    line-height: 1.5;
    color: #4f4f4f;
    padding-top: 55px;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}
ul {
    margin: 0;
    padding: 0;
}
li {
    padding: 0;
    margin: 0;
}
a {
    color: #282828;
    line-height: inherit;
    text-decoration: none;
}
a:hover,
a:focus {
    color: #a5a5a5
}
p {
    margin-bottom: 1.5rem
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #282828;
    font-family: Roboto, Arial, Helvetica, sans-serif;
    font-weight: 700;
    letter-spacing: .25px;
    margin-bottom: .78571rem;
    margin-top: 2.28571rem
}
h1 small,
h2 small,
h3 small,
h4 small,
h5 small,
h6 small {
    color: #989898;
    font-size: 60%;
    line-height: 0
}
h1 {
    font-size: 28px
}
h2 {
    font-size: 25px
}
h3 {
    font-size: 22px
}
h4 {
    font-size: 20px
}
h5 {
    font-size: 15px
}
h6 {
    font-size: 13px;
    text-transform: uppercase
}

hr {
    border: solid #e8e8e8;
    border-width: 1px 0 0;
    clear: both;
    height: 0;
    margin: 2rem 0 1.92857rem
}
em,
i {
    font-style: italic;
    line-height: inherit
}
strong,
b {
    font-weight: 700;
    line-height: inherit
}
small {
    font-size: 60%;
    line-height: inherit
}
ul,
ol,
dl {
    list-style-position: outside;
    margin-bottom: 1.5rem
}
ul ul,
ul ol,
ol ul,
ol ol {
    margin-left: 1.2rem;
    margin-bottom: 0
}
ul {
    margin-left: 1.15rem
}
ol {
    margin-left: 1.15rem
}
dl dt {
    font-weight: 700;
}
abbr,
acronym {
    border-bottom: 1px dotted #dfdfdf;
    color: #4f4f4f;
    cursor: help;
    font-size: 90%;
    text-transform: uppercase
}
abbr {
    text-transform: none
}
blockquote,
blockquote p {
    font-size: 22px;
    line-height: 29px
}
cite {
    color: #a5a5a5;
    display: block;
    font-size: 1rem;
    line-height: 1.5;
}
table {
    border-collapse: collapse;
    border-spacing: 0;
}
td,
th {
    padding: 0;
}
.icon {
    height: 1.14286rem;
    width: 1.14286rem;
    display: inline-block;
    vertical-align: middle
}
.icon svg {
    display: inline-block;
    fill: #4f4f4f;
    height: 100%;
    vertical-align: top;
    width: 100%
}
.is-srOnly,
.modal {
    display: none;
}

.body {
    margin-bottom: 6rem;
    margin-top: 1.5rem
}

.container {
    margin-left: auto;
    margin-right: auto;
    max-width: 91.71429rem;
    padding: 0 1.5rem;
    position: relative;
}

.button {
    border-radius: 0;
    border-style: solid;
    border-width: 1px;
    cursor: pointer;
    font-family: "Montserrat", Arial, Helvetica, sans-serif;
    font-weight: 400;
    line-height: normal;
    margin: 0 0 1rem;
    position: relative;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    background-color: transparent;
    border-color: #dfdfdf;
    color: #454545;
    font-size: 1rem;
    padding: .85714rem 2.28571rem;
    border-radius: 4px;
    outline: none;
    vertical-align: middle
}
.button:hover,
.button:focus,
.button.is-active,
.button.active {
    background-color: transparent;
    border-color: #989898;
    color: #666
}
.button:active {
    background-color: transparent;
    border: 1px solid #454545;
    color: #454545
}
.button:focus,
.button.is-active,
.button.active {
    outline: none
}
.button--primary {
    background-color: #454545;
    border-color: #454545;
    color: #fff
}
.button--primary:hover,
.button--primary:focus,
.button--primary.is-active,
.button--primary.active {
    background-color: #666;
    border-color: #666;
    color: #fff
}
.button--primary:active {
    background-color: #989898;
    border: 1px solid #989898;
    color: #fff
}
.button[disabled] {
    background-color: #dadada;
    border-color: #dadada;
    color: #fff;
    cursor: default
}

.button--wide {
    display: block;
    width: 100%
}

.button--icon {
    font-size: 1rem;
    padding: .78571rem
}
.button--icon svg {
    fill: #4f4f4f
}

.icon--ratingEmpty svg {
    fill: #dfdfdf;
}

.icon--ratingFull svg {
    fill: #ffb400;
}

.price--rrp,
.price--non-sale,
.price--discounted {
    text-decoration: line-through;
}
.price-section .price--withoutTax {
    color: #ff6600;
}
.table {    
    margin: 2rem 0;
    border: 1px solid #dcdcdc;
    table-layout: auto;
    width: 100%;
}
.table-tbody tr {
    border-bottom: 1px solid #dcdcdc;
}
.table-tbody tr:last-child {
    border-bottom: 0;
}
.table td + td {
    border-left: 1px solid #dcdcdc;
}
.table tr th,
.table tr td {
    padding: 0.78571rem 1.5rem;
    text-align: left;
}
.table thead tr th, 
.table tfoot tr th, 
.table tfoot tr td, 
.table tbody tr th, 
.table tbody tr td, 
.table tr td {
    display: table-cell;
}

components/amp/css/header:
.amp-menu-button {
    background-color: transparent;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="-3 -6 24 24"><path fill="{{#replace '#' theme_settings.navUser-color}}%23{{/replace}}" d="M0 12h18v-2H0v2M0 7h18V5H0v2M0 0v2h18V0H0"/></svg>');
    background-repeat: no-repeat;
    background-size: 2rem 1.8rem;
    background-position: center center;
    width: 3.92857rem;
    height: 3.92857rem;
    cursor: pointer;
    align-self: center;
    font-size: 0;
    border: 0;
    flex-shrink: 0;
}

.amp-header {
    background-color: {{theme_settings.header-backgroundColor}};
    border-bottom: 1px solid #e8e8e8;
    position: fixed;
    top: 0;
    width: 100%;
    height: 55px;
    z-index: 20;
    display: flex;
    align-items: center;
    box-shadow: 0 1px 5px rgba(0, 0, 0, 0.1);
}

.amp-header-link {
    display: inline-block;
    text-decoration: none;
}

.amp-header-logo {
    flex-grow: 1;
    padding-right: 3.92857rem;
    text-align: center;
    display: flex;
    align-items: center;
    justify-content: center;
}

.amp-header-logo amp-img {
    max-height: 55px;
    max-width: calc(100vw - 55px);
}

.amp-header-logo img {
    object-fit: contain;
}

.amp-empty-space {
    width: 3.92857rem;
}

.amp-header-logo-text {
    display: block;
    font-family: "Roboto", Arial, Helvetica, sans-serif;
    font-size: 16px;
    font-weight: 700;
    letter-spacing: 0;
    margin-left: auto;
    margin-right: auto;
    overflow: hidden;
    padding: 0.21429rem 0;
    text-overflow: ellipsis;
    text-transform: uppercase;
    white-space: nowrap;
    color: {{theme_settings.storeName-color}};
}

.amp-quickSearch {
    padding: 0.78571rem 1.14286rem;
}

.amp-quickSearch .form-input {
    width: 100%;
    padding: 0.35714rem 0.78571rem;
    height: 2.21429rem;
}

.amp-quickSearch .form-field {
    position: relative;
}

.amp-quickSearch .form-field:before {
    content: '';
    display: block;
    background-color: transparent;
    background-image: url('data:image/svg+xml;utf8,<svg enable-background="new 0 0 128 128" height="128px" id="Layer_1" version="1.1" viewBox="0 0 128 128" width="128px" xml:space="preserve" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"><g id="search"><path d="M115.613,105.593L91.421,81.401c5.06-7.278,8.035-16.115,8.035-25.651c0-24.853-20.147-45-45-45   s-45,20.147-45,45s20.147,45,45,45c9.536,0,18.373-2.975,25.651-8.035l24.192,24.192c1.562,1.562,3.609,2.343,5.657,2.343   s4.095-0.781,5.657-2.343C118.737,113.782,118.737,108.718,115.613,105.593z M17.456,55.75c0-20.402,16.598-37,37-37   s37,16.598,37,37s-16.598,37-37,37S17.456,76.152,17.456,55.75z" fill="#454545"/></g></svg>');
    background-repeat: no-repeat;
    background-size: 1.5rem 1.5rem;
    background-position: center center;
    width: 2.21429rem;
    height: 2.21429rem;
    position: absolute;
    top: 0;
    right: 0.382855rem;
}

.banners {
    background-color: #4f4f4f;
    color: #fff;
    padding: .78571rem 0;
    text-align: center
}
.banners p:last-child {
    margin-bottom: 0
}
.banners a {
    color: #fff
}
.banner {
    margin: 0 auto;
    max-width: 85.71429rem;
    width: 100%
}
.banner:before,
.banner:after {
    content: " ";
    display: table
}
.banner:after {
    clear: both
}
.banner + .banner {
    margin-top: 1.5rem
}


.form-fieldset {
    border-width: 0;
    margin: 0;
    padding: 0
}


.form-input {
    appearance: none;
    background-color: #fff;
    border-color: #dfdfdf;
    border-style: solid;
    border-width: 1px;
    border-radius: 4px;
    color: #454545;
    display: block;
    font-family: inherit;
    font-size: 1rem;
    font-smoothing: antialiased;
    height: 3rem;
    margin: 0;
    padding: .75rem 1rem;
    transition: border-color 100ms ease-out;
}
.form-input:disabled {
    background-color: #fff;
    color: #a5a5a5;
    cursor: not-allowed
}
.form-input[readonly] {
    background-color: #f8f8f8;
    border-color: #454545;
    color: #989898;
    cursor: text
}
.form-input:focus {
    border-color: #989898;
    outline: none
}

.navPages {
    background-color: #f2f2f2;
    height: 100%;
    overflow-y: auto;
    padding: 1.5rem
}

.navPages-list {
    margin: .78571rem 0 0;
    padding: 0;
    list-style: none;
}
.navPages-list + .navPages-list {
    border-top: 1px solid #dfdfdf;
    padding-top: .78571rem
}
.navPages-action {
    display: block;
    font-size: 15px;
    font-weight: 700;
    padding: .78571rem 0;
    text-decoration: none;
    text-transform: uppercase
}

components/amp/css/footer:
.amp-footer {
    color: #505050;
    border-top: 1px solid #e8e8e8;
    display: flex;
    flex-wrap: wrap;
    align-items: flex-start;
}

.amp-footer-col {
    padding: 0 1.14286rem;
    width: 50%;
}

.amp-footer-col--about {
    width: 100%;
    text-align: center;
}

.amp-footer-col--about address {
    font-style: normal;
    margin-bottom: 0.78571rem;
}

.amp-footer-col--brands {
    width: 100%;
}
.amp-footer-col--brands .amp-footer-list {
    columns: 2;
}

.amp-footer a {
    color: #989898;
    text-decoration: none
}
.amp-footer a:hover {
    color: #4f4f4f
}

.amp-footer-heading {
    text-transform: uppercase;
    font-size: 18px;
}

.amp-footer-heading a {
    color: #282828;
}

.amp-footer-list {
    margin: 0;
    list-style-type: none;
}

.amp-footer-list li {
    padding: 3px 0;
}

.amp-footer-copyright {
    width: 100%;
    background: #f0f0f0;
    padding: 0.78571rem;
    text-align: center;
    margin-top: 3rem;
}

.amp-footer-copyright p {
    margin: 0;
}

components/amp/css/product:
.productView {
    margin-left: auto;
    margin-right: auto;
    max-width: 91.71429rem;
    padding: 1rem 1.5rem;
}

.productView-images {
    padding: 2rem 0;
}

.productView-action {
    margin-bottom: 2rem;
}

.productAccordion-title {
    background-color: #f2f2f2;
    border-right: 0;
    border-left: 0;
    padding: .75rem 1.5rem;
    margin-bottom: -1px;
    font-size: 1rem;
    text-transform: uppercase;
    position: relative;
}

.productAccordion-title:after {
    content: '';
    display: block;
    width: 16px;
    height: 16px;
    background-image: url('data:image/svg+xml;utf8,<svg version="1.1" id="Layer_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" width="24px" height="24px" viewBox="0 0 24 24" enable-background="new 0 0 24 24" xml:space="preserve"><polygon points="23,11 13,11 13,1 11,1 11,11 1,11 1,13 11,13 11,23 13,23 13,13 23,13 "/></svg>');
    background-size: 100%;
    position: absolute;
    top: 50%;
    right: .75rem;
    transform: translateY(-50%);
    transition: transform .2s ease;
}

amp-accordion > section[expanded] > .productAccordion-title:after {
    transform: translateY(-50%) rotate(45deg);
}

.productAccordion-content {
    padding: 1rem 0;
}

.socialLinks {
    margin: 1rem 0 0;
    padding: 0;
    text-align: center;
}

.socialLinks-item {
    margin: 0;
    padding: 0;
    display: inline-block;
    font-size: 0;
    margin-bottom: .35714rem;
    margin-right: .78571rem
}
.socialLinks-item:last-child {
    margin-right: 0
}


.writeReview-productDetails .product-brand {
    color: #a5a5a5;
    margin: 0
}
.writeReview-productDetails .product-title {
    margin-top: 0
}
.account--addReturn .table {
    display: block;
    margin-bottom: 3rem
}
.quickView:before,
.quickView:after {
    content: " ";
    display: table
}
.quickView:after {
    clear: both
}
.productReviews-list {
    list-style: none;
    margin: 0 -.75rem;
    max-width: none;
    width: auto;
    font-size: 0
}
.productReviews-list ul,
.productReviews-list ol {
    list-style: none;
    margin-bottom: 0
}
.productReviews-list:before,
.productReviews-list:after {
    content: " ";
    display: table
}
.productReviews-list:after {
    clear: both
}
.productReview {
    padding-left: .75rem;
    padding-right: .75rem;
    width: 100%;
    float: left;
    display: inline-block;
    float: none;
    font-size: 1rem;
    vertical-align: top
}
.productReview-title {
    margin: .5rem 0;
    text-transform: none
}
.productReview-author {
    color: #989898;
    margin: .5rem 0 1rem
}
.productReview-rating {
    display: inline-block;
    margin-bottom: .35714rem
}
.productReview-ratingNumber {
    border: 0;
    clip: rect(1px, 1px, 1px, 1px);
    height: 1px;
    overflow: hidden;
    padding: 0;
    position: absolute;
    width: 1px
}
.productView-details {
    margin-bottom: 1rem
}
.productView-product >:last-child {
    margin-bottom: 0
}
.productView-title {
    font-size: 16px;
    margin: -.78571rem 0 .35714rem
}
.productView-brand {
    font-family: Karla, Arial, Helvetica, sans-serif;
    color: #a5a5a5;
    font-size: 14px;
    font-weight: normal;
    margin: 0 0 .78571rem
}
.productView-brand a {
    color: #989898
}
.productView-brand a:hover {
    color: #4f4f4f
}
.productView-price {
    font-size: 15px;
    margin-bottom: .5rem
}
.productView-price .price-section--saving {
    font-size: 15px
}
.productView-price abbr {
    border: 0;
    color: inherit;
    font-size: inherit
}
.productView-info {
    margin-top: .78571rem
}
.productView-info >:last-child {
    margin-bottom: 0
}
.productView-info-name,
.productView-info-value {
    display: inline;
    margin: 0;
}
.productView-info-name {
    margin-right: 5px;
}
.productView-info-value::after {
    content: "\A";
    white-space: pre;
}

.productView-addition-table {
    margin-top: 1rem;
}

.alertBox {
    margin-bottom: .78571rem;
    padding: .78571rem 1.14286rem;
    background-color: #e8e8e8;
    border-radius: 4px
}

.alertBox .icon {
    height:1.42857rem;
    width:1.42857rem
}

.alertBox--success {
    background-color:#d5ffd8;
    border-color:#d5ffd8;
}

.alertBox--success svg {
    fill:#69d66f
}

.alertBox--error{
    background-color:#fdd;
    border-color:#fdd
}

.alertBox--error svg {
    fill:#ff7d7d
}

.alertBox-column{
    display:table-cell;
    vertical-align:middle
}

.alertBox-icon{
    text-align: center;
    padding-right: 1.14286rem
}

.alertBox-message {
    margin: 0;
    width: 100%
}

[data-custom-tab-title] {
    font-weight: bold;
    text-transform: uppercase;
    border-top: 1px solid #dcdcdc;
    padding-top: 1.5rem;
}
