components/common/footer:
{{#or banners.bottom theme_settings.instantload}}
<div class="container">
    {{#if settings.data_tag_enabled}}
        <div class="banners banners--bottom" data-banner-location="bottom">
            {{#each (limit banners.bottom_metadata 1)}}
                <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                    <div data-event-type="promotion-click">
                        {{{this.content}}}
                    </div>
                </div>
            {{/each}}
        </div>
    {{else}}
        <div class="banners banners--bottom" data-banner-location="bottom">
            <div class="banner">
                {{{limit banners.bottom 1}}}
            </div>
        </div>
    {{/if}}
</div>
{{/or}}

{{{region name="footer_before"}}}
{{{region name="footer_before--global"}}}

{{#if settings.show_newsletter_box}}
    {{#if theme_settings.nl_popup_show '===' 'home'}}
        {{#if page_type '===' 'default'}}
            {{> components/emthemes-modez/newsletter/modal}}
        {{/if}}
    {{else if theme_settings.nl_popup_show '===' 'all'}}
        {{> components/emthemes-modez/newsletter/modal}}
    {{/if}}
{{/if}}

<footer class="footer" role="contentinfo">
    {{#if settings.show_newsletter_box}}
    {{#if theme_settings.footer_show_newsletter}}
    <div class="emthemesModez-footer-topColumns-wrapper">
        <div class="container">
            <div class="emthemesModez-footer-topColumns">
                <div class="emthemesModez-footer-newsletter">
                    {{> components/emthemes-modez/newsletter/section}}
                </div>
            </div>
        </div>
    </div>
    {{/if}}
    {{/if}}

    {{{region name="newsletter_below"}}}
    {{{region name="newsletter_below--global"}}}


    <div class="container">
        <section class="footer-info">

            {{#if theme_settings.footer_show_aboutus}}
                <article class="footer-info-col footer-info-col--about" data-section-type="storeInfo">
                    {{{region name="footer_before_logo--global"}}}
                    <div class="footer-info-heading">
                        <div class="footer-logo">
                            {{> components/common/store-logo style="footer"}}
                        </div>
                    </div>
                    {{{region name="footer_below_logo--global"}}}

                    {{#if theme_settings.footer_show_contact}}
                        <ul class="fa-ul">
                            <li><i class="fa fa-li fa-map-marker"></i><address>{{nl2br settings.address}}</address></li>
                            {{#if settings.phone_number}}
                            <li><i class="fa fa-li fa-phone"></i><a href="tel:{{settings.phone_number}}">{{lang 'footer.call_us' phone_number=settings.phone_number}}</a></li>
                            {{/if}}
                        </ul>
                    {{/if}}

                    {{#if theme_settings.social_icon_placement_bottom}}
                        {{> components/common/social-links style="alt"}}
                    {{/if}}

                    {{{region name="footer_below_contact--global"}}}
                </article>
            {{/if}}
            

            {{#if theme_settings.footer_show_pages '>' -2}}
            
              <article class="footer-info-col" data-section-type="footer-webPages">
                    <p style="font-weight:bold" class="footer-info-heading">WHO WE ARE</p>
                    <ul class="footer-info-list">
                      
                                <li>
                                    <a href="https://gritroutdoors.com/about-us/">About us</a>
                                </li>
                           
                                <li>
                                    <a href="https://gritroutdoors.com/contact-us/">Contact us</a>
                                </li>
                                  <li>
                                    <a href="https://gritroutdoors.com/affiliate-program/">Affiliate program</a>
                                </li>
                                
                           
                        <li>
                            <a href="{{urls.sitemap}}">{{lang 'sitemap.page_title'}}</a>
                        </li>
                        <li>
                                    <a href="https://gritroutdoors.com/careers/">Careers</a>
                                </li>
                                 <li>
                                    <a href="https://gritroutdoors.com/expert-guides-tutorials/">Shopping Guides</a>
                                </li>
                    </ul>
                </article>
                
                
                 <article class="footer-info-col" data-section-type="footer-webPages">
                    <p style="font-weight:bold" class="footer-info-heading">ORDER INFORMATION</p>
                    <ul class="footer-info-list">
                        
                                <li>
                                    <a href="https://gritroutdoors.com/prices-sales-tax-content-policies/">Prices / Sales Tax / Content Policies</a>
                                </li>
                           
                                <li>
                                    <a href="https://gritroutdoors.com/shipping-delivery/">Shipping & Delivery</a>
                                </li>
                                <li>
                                    <a href="https://gritroutdoors.com/returns-refunds-policy/">Returns & Refunds Policy / Start a Return</a>
                                </li>
                                <li>
                                    <a href="https://gritroutdoors.com/faq/">FAQ</a>
                                </li>
                        
                        
                        
                        
                    </ul>
                </article>
                
                
                
                
                
                 <article class="footer-info-col" data-section-type="footer-webPages">
                    <p style="font-weight:bold" class="footer-info-heading">SHIPPING & TAXES</p>
                    <ul class="footer-info-list">
                         <li>
                                    <a href="https://gritroutdoors.com/domestic-restrictions/">Domestic restrictions</a>
                                </li>
                           
                                <li>
                                    <a href="https://gritroutdoors.com/international-ordering/">International ordering</a>
                                </li>
                                  <li>
                                    <a href="https://gritroutdoors.com/export-policy/">Export policy</a>
                                </li>
                           
                    </ul>
                </article>
                
                
                
                
                
                 <article class="footer-info-col" data-section-type="footer-webPages">
                    <p style="font-weight:bold" class="footer-info-heading">FOR CUSTOMERS</p>
                    <ul class="footer-info-list">
                         <li>
                                    <a href="https://gritroutdoors.com/customer-service/">Customer service</a>
                                </li>
                           
                                <li>
                                    <a href="https://gritroutdoors.com/rewards-program/">Reward program</a>
                                </li>
                                  <li>
                                    <a href="https://gritroutdoors.com/offer-terms-conditions/">Offer Terms & Conditions</a>
                                </li>
                                <li>
                                    <a href="https://gritroutdoors.com/ada-statement/">ADA Statement</a>
                                </li>
                           
                    </ul>
                </article>
            {{/if}}

            {{#if theme_settings.footer_show_categories '>' -1}}
                <article class="footer-info-col" data-section-type="footer-categories">
                    <h5 class="footer-info-heading">{{lang 'footer.categories'}}</h5>
                    <ul class="footer-info-list">
                        {{#if theme_settings.footer_show_categories '>' 0}}
                            {{#each (limit categories theme_settings.footer_show_categories)}}
                                <li>
                                    <a href="{{url}}">{{name}}</a>
                                </li>
                            {{/each}}
                        {{else}}
                            {{#each categories}}
                                <li>
                                    <a href="{{url}}">{{name}}</a>
                                </li>
                            {{/each}}
                        {{/if}}
                    </ul>
                    {{{region name="footer_below_categories--global"}}}
                </article>
            {{/if}}

            {{#if theme_settings.footer_show_links_1}}
                {{> components/emthemes-modez/common/footer-links index=1}}
            {{/if}}

            {{#if theme_settings.footer_show_links_2}}
                {{> components/emthemes-modez/common/footer-links index=2}}
            {{/if}}
            
            {{#if theme_settings.footer_show_links_3}}
                {{> components/emthemes-modez/common/footer-links index=3}}
            {{/if}}
            
            {{#if theme_settings.footer_show_links_4}}
                {{> components/emthemes-modez/common/footer-links index=4}}
            {{/if}}


            {{#if theme_settings.show_shop_by_brand}}
                
                    {{{region name="footer_below_brands--global"}}}
               
            {{/if}}

        </section>
        {{{region name="footer_below_columns--global"}}}
    </div>

    <div class="footer-copyright">
        <div class="container">
            <p class="powered-by">
                {{#if theme_settings.show_copyright_footer}}
                    &copy; <span id="copyright_year"></span> {{settings.store_name}}.
                    <script>
                        document.getElementById("copyright_year").innerHTML = new Date().getFullYear();
                    </script>
                {{/if}}
                {{#if theme_settings.show_powered_by}}
                    Powered by <a href="http://bigcommerce.com" rel="nofollow">BigCommerce</a>. Theme designed by <a href="https://papathemes.com" rel="nofollow">Papathemes</a>.
                {{/if}}
            </p>

            {{> components/common/payment-icons}}

            {{{region name="ssl_site_seal--global"}}}
        </div>
    </div>
    
</footer>

{{{region name="footer_bottom--global"}}}

components/common/payment-icons:
{{#with theme_settings}}
    {{#or show_accept_amex show_accept_discover show_accept_mastercard show_accept_paypal show_accept_visa}}
        <div class="footer-payment-icons">
            {{#if show_accept_amex}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-american-express{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_discover}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-discover{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_mastercard}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-mastercard{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_paypal}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-paypal{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_visa}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-visa{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_applepay}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-applepay{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_amazon}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-amazon{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_googlepay}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-googlepay{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
            {{#if show_accept_klarna}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-klarna{{#if payment_icons_style}}{{#if payment_icons_style '!=' 'flat'}}-{{payment_icons_style}}{{/if}}{{/if}}"></use></svg>
            {{/if}}
        </div>
    {{/or}}
{{/with}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks {{#if style}}socialLinks--{{style}}{{/if}}">
        {{#each social_media}}
            <li class="socialLinks-item">
                <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener">
                    <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                </a>
            </li>
        {{/each}}
    </ul>
{{/if}}

components/common/store-logo:
{{#if settings.store_logo.image}}
    {{#if theme_settings.logo_size '===' 'original'}}
        <img class="header-logo-image-unknown-size" src="{{getImage settings.store_logo.image 'logo_size'}}" alt="{{settings.store_logo.title}}" title="{{settings.store_logo.title}}">
    {{else}}
        <div class="header-logo-image-container">
            {{#if style}}
                <img class="header-logo-image" src="{{getImage settings.store_logo.image 'logo_size'}}" srcset="{{getImageSrcset settings.store_logo.image 1x=theme_settings.logo_size 2x=(concat (multiply (first (split theme_settings.logo_size 'x')) 2) 'w')}}" alt="{{settings.store_logo.title}}" title="{{settings.store_logo.title}}">
            {{else}}
                {{#if page_type '==' 'default'}}
                <img class="header-logo-image" src="{{getImage settings.store_logo.image 'logo_size'}}" srcset="{{getImageSrcset settings.store_logo.image 1x=theme_settings.logo_size 2x=(concat (multiply (first (split theme_settings.logo_size 'x')) 2) 'w')}}" alt="{{settings.store_logo.title}}" title="{{settings.store_logo.title}}">
                {{else}}
                <a href="https://gritroutdoors.com/">
                    <img class="header-logo-image" src="{{getImage settings.store_logo.image 'logo_size'}}" srcset="{{getImageSrcset settings.store_logo.image 1x=theme_settings.logo_size 2x=(concat (multiply (first (split theme_settings.logo_size 'x')) 2) 'w')}}" alt="{{settings.store_logo.title}}" title="{{settings.store_logo.title}}">
                </a>
                {{/if}}
            {{/if}}
        </div>
    {{/if}}
{{else}}
    <span class="header-logo-text">{{settings.store_logo.title}}</span>
{{/if}}
