components/emthemes-modez/sections/secti:
{{#if section '===' ''}}

{{else if section '===' 'slideshow'}}
    {{#if carousel}}
        {{> components/carousel}}
    {{/if}}

{{else if section '===' 'special_products_tabs'}}
    {{#or theme_settings.specialProductsTab_show_featured theme_settings.specialProductsTab_show_top theme_settings.specialProductsTab_show_new}}
        {{#or theme_settings.specialProductsTab_init_count theme_settings.specialProductsTab_lazy_count}}
            <div class="emthemesModez-section {{#if index}}emthemesModez-section--index{{index}}{{/if}} emthemesModez-section--specialProductsTabs">
            <div class="emthemesModez-section-inner">
                {{> components/papa-supermarket/special-products-tabs/section}}
            </div>
            </div>
        {{/or}}
    {{/or}}

{{else if section '===' 'special_products_columns'}}
    {{#or theme_settings.specialProductsColumns_show_featured theme_settings.specialProductsColumns_show_top theme_settings.specialProductsColumns_show_new}}
    <div class="emthemesModez-section {{#if index}}emthemesModez-section--index{{index}}{{/if}} emthemesModez-section--specialProductsColumns">
    <div class="emthemesModez-section-inner">
    {{> components/papa-supermarket/sections/section-special-products-columns}}
    </div>
    </div>
    {{/or}}

{{else if section '===' 'products_by_category_sorting_tabs'}}
    {{#if theme_settings.productsByCategorySortingTabs_catIDs}}
        {{inject 'hasProductsByCategorySortingTabs' true}}
        <div class="emthemesModez-sections-group"
        data-pbcst-group="{{theme_settings.productsByCategorySortingTabs_catIDs}}"
        data-sort="{{theme_settings.productsByCategorySortingTabs_active}}"
        data-limit="{{theme_settings.productsByCategorySortingTabs_products}}"
        {{#if theme_settings.productsByCategorySortingTabs_lazy}}data-lazy{{/if}}></div>
    {{else if theme_settings.productsByCategorySortingTabs_count '>' 0}}
        {{inject 'hasProductsByCategorySortingTabs' true}}
        <div class="emthemesModez-sections-group"
            data-pbcst-group="{{join (pluck (limit categories theme_settings.productsByCategorySortingTabs_count) 'id') ','}}"
            data-sort="{{theme_settings.productsByCategorySortingTabs_active}}"
            data-limit="{{theme_settings.productsByCategorySortingTabs_products}}"
            {{#if theme_settings.productsByCategorySortingTabs_lazy}}data-lazy{{/if}}></div>
    {{/if}}

{{else if section '===' 'categories_list'}}
    {{#if theme_settings.categoriesList_count '>' 0}}
    <div class="emthemesModez-section {{#if index}}emthemesModez-section--index{{index}}{{/if}} emthemesModez-section--categoriesGrid">
    <div class="emthemesModez-section-inner">
    {{> components/papa-supermarket/sections/section-categories-list limit=theme_settings.categoriesList_count sub_limit=theme_settings.categoriesList_subCount}}
    </div>
    </div>
    {{/if}}

{{else if section '===' 'blog_recent'}}
    {{#if theme_settings.homepage_blog_posts_position '===' 'content'}}
    {{#if theme_settings.homepage_blog_posts_count '>' 0}}
    <div class="emthemesModez-section {{#if index}}emthemesModez-section--index{{index}}{{/if}} emthemesModez-section--blog">
    <div class="emthemesModez-section-inner">
        {{#if theme_settings.blog_style '==' 'featured'}}
            {{> components/emthemes-modez/blog/featured posts=blog.recent_posts style="featured"}}
        {{else}}
            {{> components/emthemes-modez/blog/featured posts=blog.recent_posts style="3col"}}
        {{/if}}
    </div>
    </div>
    {{/if}}
    {{/if}}

{{else if section '===' 'bc_banner_home_1'}}
    {{> components/papa-supermarket/sections/section-bc-banner find="emthemesModezBannersBlockHome01"}}

{{else if section '===' 'bc_banner_home_2'}}
    {{> components/papa-supermarket/sections/section-bc-banner find="emthemesModezBannersBlockHome02"}}

{{else if section '===' 'bc_banner_home_3'}}
    {{> components/papa-supermarket/sections/section-bc-banner find="emthemesModezBannersBlockHome03"}}

{{else if section '===' 'new_products'}}
    {{#if products.new}}
        <div class="emthemesModez-section emthemesModez-section--alt {{#if index}}emthemesModez-section--index{{index}}{{/if}} emthemesModez-section--newProducts emthemesModez-section--displayType-grid">
        <div class="emthemesModez-section-inner">
        {{> components/papa-supermarket/products/new 
            products=products.new 
            columns=4 
            display_type='grid'}}
        </div>
        </div>
    {{/if}}
    {{{region name="home_below_new_products"}}}

{{else if section '===' 'featured_products'}}
    {{#if products.featured}}
        <div class="emthemesModez-section {{#if index}}emthemesModez-section--index{{index}}{{/if}} emthemesModez-section--featuredProducts emthemesModez-section--displayType-grid">
        <div class="emthemesModez-section-inner">
        {{> components/products/featured 
            products=products.featured 
            columns=4 
            display_type='grid'}}
        </div>
        </div>
    {{/if}}
    {{{region name="home_below_featured_products"}}}

{{else if section '===' 'popular_products'}}
    {{#if products.top_sellers}}
        <div class="emthemesModez-section {{#if index}}emthemesModez-section--index{{index}}{{/if}} emthemesModez-section--topProducts emthemesModez-section--displayType-grid">
        <div class="emthemesModez-section-inner">
        {{> components/products/top 
            products=products.top_sellers 
            columns=4 
            display_type='grid'}}
        </div>
        </div>
    {{/if}}
    {{{region name="home_below_top_products"}}}
{{/if}}

components/carousel:
{{inject 'hasMainCarousel' true}}
<section class="heroCarousel-container{{#if theme_settings.carousel_banners}} heroCarousel-container--hasBanners{{/if}}">
    <div class="heroCarousel"
        data-slick='{
            "dots": true,
            "mobileFirst": true,
            "slidesToShow": 1,
            "slidesToScroll": 1,
            "autoplay": true,
            "autoplaySpeed": {{carousel.swap_frequency}},
            "lazyLoad": "anticipated"
        }'
        {{#if theme_settings.carousel_video_autoplay}} data-youtube-autoplay{{/if}}
        {{#if theme_settings.carousel_video_mute}} data-youtube-mute{{/if}}>
        {{#each carousel.slides}}
        <div class="heroCarousel-slide {{#if ../theme_settings.homepage_carousel_style '==' 'stretch'}}heroCarousel-slide--stretch{{/if}} heroCarousel-slide--{{@index}}{{#if @first}} heroCarousel-slide--first{{/if}}">
            <div class="heroCarousel-image-wrapper{{#and image_height image_width}} heroCarousel-image-wrapper--sized{{/and}}">
                <a href="{{url}}">
                    {{#if @index '==' 0}}
                        {{#if (merge this video=../../theme_settings.carousel_vid0)}}{{/if}}
                    {{/if}}
                    {{#if @index '==' 1}}
                        {{#if (merge this video=../../theme_settings.carousel_vid1)}}{{/if}}
                    {{/if}}
                    {{#if @index '==' 2}}
                        {{#if (merge this video=../../theme_settings.carousel_vid2)}}{{/if}}
                    {{/if}}
                    {{#if @index '==' 3}}
                        {{#if (merge this video=../../theme_settings.carousel_vid3)}}{{/if}}
                    {{/if}}
                    {{#if @index '==' 4}}
                        {{#if (merge this video=../../theme_settings.carousel_vid4)}}{{/if}}
                    {{/if}}



                    {{#if video}}
                        <div class="heroCarousel-video-wrapper">
                            <div class="heroCarousel-image-box" style="{{#if image_width}}width:{{image_width}}px{{/if}}">
                                {{#and image_height image_width}}<div class="heroCarousel-image-box-padding" style="padding-bottom:{{multiply (divide image_height image_width) 100}}%">{{/and}}
                                    {{> components/common/responsive-img
                                        image=stencil_image
                                        class="heroCarousel-image"
                                        fallback_size='1920w'
                                        lazyload='disabled'
                                    }}
                                {{#and image_height image_width}}</div>{{/and}}
                            </div>
                            <div class="heroCarousel-video"><div data-youtube="{{video}}"></div></div>
                        </div>
                    {{else}}
                        <div class="heroCarousel-image-box" style="{{#if image_width}}width:{{image_width}}px{{/if}}">
                            {{#and image_height image_width}}<div class="heroCarousel-image-box-padding" style="padding-bottom:{{multiply (divide image_height image_width) 100}}%">{{/and}}
                                {{#if @first}}
                                    {{> components/common/responsive-img
                                        image=stencil_image
                                        class="heroCarousel-image"
                                        fallback_size='1920w'
                                        lazyload='disabled'
                                        otherAttributes=(concat (concat (concat (concat 'width="' image_width) '" height="') image_height) '"')
                                    }}
                                {{else}}
                                    {{> components/common/responsive-img
                                        image=stencil_image
                                        class="heroCarousel-image"
                                        fallback_size='1920w'
                                        lazyload='lazyload'
                                        otherAttributes=(concat (concat (concat (concat 'width="' image_width) '" height="') image_height) '"')
                                    }}
                                {{/if}}
                            {{#and image_height image_width}}</div>{{/and}}
                        </div>
                    {{/if}}

                    {{#if heading}}
                        {{> components/carousel-content show_background=true}}
                    {{else}}
                        {{#if text}}
                            {{> components/carousel-content show_background=true}}
                        {{else}}
                            {{#if button_text}}
                                {{> components/carousel-content show_background=false}}
                            {{/if}}
                        {{/if}}
                    {{/if}}
                </a>
            </div>
        </div>
        {{/each}}
    </div>
    {{#if theme_settings.carousel_banners}}
        <div class="heroCarousel-banners">
            {{> components/papa-supermarket/sections/section-bc-banner find="papathemesHeroCarouselBanner"}}
        </div>
    {{/if}}
</section>

components/papa-supermarket/special-prod:
{{inject 'hasSpecialProductsTabs' true}}
<div class="emthemesModez-specialProductsTabs emthemesModez-specialProductsTabs--supermarket1" data-special-products-tabs>
    <ul class="tabs" id="emthemesModez-specialProductsTabs-tabs" data-tab role="tablist">
        {{#if theme_settings.specialProductsTab_show_featured}}
            {{#or (unless theme_settings.specialProductsTab_init_count) products.featured}}
                <li class="tab{{#if theme_settings.specialProductsTab_active_tab '===' 'featured'}} is-active{{/if}}" role="presentation"><a class="tab-title" href="#tab-featured-products" role="tab" tabindex="0" aria-selected="{{#if theme_settings.specialProductsTab_active_tab '===' 'featured'}}true{{else}}false{{/if}}" aria-controls="tab-featured-products">{{lang 'products.featured' }}</a></li>
            {{/or}}
        {{/if}}
        {{#if theme_settings.specialProductsTab_show_top}}
            {{#or (unless theme_settings.specialProductsTab_init_count) products.top_sellers}}
                <li class="tab{{#if theme_settings.specialProductsTab_active_tab '===' 'top'}} is-active{{/if}}" role="presentation"><a class="tab-title" href="#tab-bestselling-products" role="tab" tabindex="0" aria-selected="{{#if theme_settings.specialProductsTab_active_tab '===' 'top'}}true{{else}}false{{/if}}" aria-controls="tab-bestselling-products">{{lang 'products.top' }}</a></li>
            {{/or}}
        {{/if}}
        {{#if theme_settings.specialProductsTab_show_new}}
            {{#or (unless theme_settings.specialProductsTab_init_count) products.new}}
                <li class="tab{{#if theme_settings.specialProductsTab_active_tab '===' 'new'}} is-active{{/if}}" role="presentation"><a class="tab-title" href="#tab-new-products" role="tab" tabindex="0" aria-selected="{{#if theme_settings.specialProductsTab_active_tab '===' 'new'}}true{{else}}false{{/if}}" aria-controls="tab-new-products">{{lang 'products.new' }}</a></li>
            {{/or}}
        {{/if}}
    </ul>
    <div class="tabs-contents">
        <section role="tabpanel" aria-hidden="{{#if theme_settings.specialProductsTab_active_tab '===' 'featured'}}false{{else}}true{{/if}}" class="tab-content{{#if theme_settings.specialProductsTab_active_tab '===' 'featured'}} is-active{{/if}}" id="tab-featured-products" data-product-type="featured">
            {{> components/products/featured 
                products=products.featured
                columns=4 
                hide_heading=true 
                display_type='grid'}}
            {{#if theme_settings.specialProductsTab_lazy_count}}
                <div class="emthemesModez-specialProductsTabs-viewportCheck" data-viewport-check="featured"></div>
            {{/if}}
        </section>
        <section role="tabpanel" aria-hidden="{{#if theme_settings.specialProductsTab_active_tab '===' 'top'}}false{{else}}true{{/if}}" class="tab-content{{#if theme_settings.specialProductsTab_active_tab '===' 'top'}} is-active{{/if}}" id="tab-bestselling-products" data-product-type="top">
            {{> components/products/top 
                products=products.top_sellers
                columns=4 
                hide_heading=true 
                display_type='grid'}}
            {{#if theme_settings.specialProductsTab_lazy_count}}
                <div class="emthemesModez-specialProductsTabs-viewportCheck" data-viewport-check="top"></div>
            {{/if}}
        </section>
        <section role="tabpanel" aria-hidden="{{#if theme_settings.specialProductsTab_active_tab '===' 'new'}}false{{else}}true{{/if}}" class="tab-content{{#if theme_settings.specialProductsTab_active_tab '===' 'new'}} is-active{{/if}}" id="tab-new-products" data-product-type="new">
            {{> components/products/new 
                products=products.new
                columns=4 
                hide_heading=true 
                display_type='grid'}}
            {{#if theme_settings.specialProductsTab_lazy_count}}
                <div class="emthemesModez-specialProductsTabs-viewportCheck" data-viewport-check="new"></div>
            {{/if}}
        </section>
    </div>
    <div class="loading" style="display:none"></div>
    <div class="emthemesModez-specialProductsTabs-actionGroup">
        <a href="#" class="button button--small loadMore" style="display:none">{{lang 'papasupermarket.products_by_category_sorting_tabs.load_more'}}</a>
        <a href="#" class="button button--small collapse" style="display:none">{{lang 'papasupermarket.products_by_category_sorting_tabs.collapse'}}</a>
    </div>
</div>

components/papa-supermarket/sections/sec:
{{!--
Arguments:
    - categories (array)
    - limit (number)
    - sub_limit (number)
    - style (string)
--}}
{{#if limit '>' 0}}
<h2 class="page-heading"><span>{{lang 'papasupermarket.categories_list.heading'}}</span></h2>
<section class="papaSupermarket-categoriesList-section {{#if style}}papaSupermarket-categoriesList-section--{{style}}{{/if}} {{#if theme_settings.categoriesList_showBanner}}papaSupermarket-categoriesList-section--hasImage{{/if}}">
    <ul class="papaSupermarket-categoriesList">
        {{#each (limit categories limit)}}
            <li class="papaSupermarket-categoriesList-item">
                {{#if ../theme_settings.categoriesList_showBanner}}
                <div class="papaSupermarket-categoriesList-item-image"><a href="{{url}}">{{> components/common/responsive-img-url image=(concat (concat '/product_images/uploaded_images/categories-list-thumbnail-' (add @index 1)) '.jpg?c=2') alt=name lazyload=../../theme_settings.lazyload_mode}}</a></div>
                {{/if}}
                <div class="papaSupermarket-categoriesList-item-body">
                    <h4 class="papaSupermarket-categoriesList-item-name"><a href="{{url}}">{{name}}</a></h4>
                    {{#if children}}
                    <ul class="papaSupermarket-subCategoriesList">
                        {{#each (limit children ../../sub_limit)}}
                        <li class="papaSupermarket-subCategoriesList-item"><a href="{{url}}">{{name}}</a></li>
                        {{/each}}
                    </ul>
                    <div class="papaSupermarket-categoriesList-item-moreLink"><a href="{{url}}">{{lang 'papasupermarket.categories_list.see_all'}}</a></div>
                    {{/if}}
                </div>
            </li>
        {{/each}}
    </ul>
</section>
{{/if}}

components/products/featured:
{{#unless hide_heading}}
<h2 class="page-heading">{{lang 'products.featured' }}</h2>
{{/unless}}

