components/products/preload-main-image:
{{#partial "head-last"}}
    {{#if product.main_image}}
        {{#if theme_settings.product_image_responsive}}
            <link href="{{getImageSrcset product.main_image 'product_size' (cdn theme_settings.default_image_product) 1x=theme_settings.product_size}}" rel="preload" as="image">
        {{else}}
            <link href="{{getImage product.main_image 'product_size' (cdn ../../theme_settings.default_image_product)}}" rel="preload" as="image">
        {{/if}}
    {{/if}}
{{/partial}}
