components/amp/common/share:
{{#if settings.add_this.buttons}}
    <ul class="socialLinks" data-addthis:url="{{url}}" data-addthis:title="{{title}}">
        {{#each settings.add_this.buttons}}
            {{#all (if service '!==' 'google') (if service '!==' 'print') (if service '!==' 'facebook')}}
                <li class="socialLinks-item socialLinks-item--{{service}}">
                    <amp-social-share class="amp-social-share" data-vars-product-link="{{url}}" data-vars-product-id="{{id}}" data-vars-product-name="{{name}}" data-vars-social-type="{{service}}" type="{{service}}"></amp-social-share>
                </li>
            {{/all}}
        {{/each}}
    </ul>
{{/if}}

components/amp/products/reviews:
{{#if reviews.total}}
    <section expanded class="productAccordion">
        <h4 class="productAccordion-title">
            {{lang 'products.reviews.header' total=reviews.total}}
        </h4>
        <div class="productAccordion-content">
            <ul class="productReviews-list" id="productReviews-list">
                {{#each reviews.list}}
                <li class="productReview">
                    <article itemprop="review" itemscope itemtype="http://schema.org/Review">
                        <header>
                            <span class="productReview-rating rating--small" itemprop="reviewRating" itemscope itemtype="http://schema.org/Rating">
                                {{> components/amp/products/ratings rating=rating}}
                                <span class="productReview-ratingNumber" itemprop="ratingValue">{{ rating }}</span>
                            </span>
                            <h5 itemprop="name" class="productReview-title">{{ title }}</h5>
                            {{#if name}}
                                <meta itemprop="author" content="{{name}}">
                                <p class="productReview-author">
                                    {{lang 'products.reviews.post_on_by' date=date name=name }}
                                </p>
                            {{else}}
                                <meta itemprop="author" content="{{lang 'products.reviews.anonymous_poster'}}">
                                <p class="productReview-author">
                                    {{lang 'products.reviews.post_on_by' date=date name=(lang 'products.reviews.anonymous_poster')}}
                                </p>
                            {{/if}}
                        </header>
                        <p itemprop="reviewBody" class="productReview-body">{{nl2br text}}</p>
                    </article>
                </li>
                {{/each}}
            </ul>
            {{> components/common/paginator pagination.reviews}}
        </div>
    </section>
{{/if}}

components/common/paginator:
{{#or previous links next}}
<div class="pagination">
    {{#if total}}
    {{#if start}}
    {{#if end}}
        <div class="pagination-info">
            {{#if end '>' total}}
                {{{lang 'common.paginator.items_of' start=start end=total total=total}}}
            {{else}}
                {{{lang 'common.paginator.items_of' start=start end=end total=total}}}
            {{/if}}
        </div>
    {{/if}}
     {{/if}}
     {{/if}}
    <ul class="pagination-list">
        {{#if previous}}
            <li class="pagination-item pagination-item--previous">
                <a class="pagination-link" href="{{previous}}" {{#if instantload}}data-instantload{{/if}} {{#unless reload}}data-faceted-search-facet{{/unless}}>
                    <i class="icon" aria-hidden="true">
                        <svg>
                            <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-chevron-left"></use>
                        </svg>
                    </i>
                    {{lang 'common.previous'}}
                </a>
            </li>
        {{/if}}
        {{#each links}}
            {{#if this.number '==' ../current}}
                <li class="pagination-item pagination-item--current">
                    <a class="pagination-link" href="{{this.url}}" {{#if ../../instantload}}data-instantload{{/if}} {{#unless ../../reload}}data-faceted-search-facet{{/unless}}>{{this.number}}</a>
                </li>
            {{else}}
                <li class="pagination-item">
                    <a class="pagination-link" href="{{this.url}}" {{#if ../../instantload}}data-instantload{{/if}} {{#unless ../../reload}}data-faceted-search-facet{{/unless}}>{{this.number}}</a>
                </li>
            {{/if}}
        {{/each}}

        {{#if next}}
            <li class="pagination-item pagination-item--next">
                <a class="pagination-link" href="{{next}}" {{#if instantload}}data-instantload{{/if}} {{#unless reload}}data-faceted-search-facet{{/unless}}>
                    {{lang 'common.next'}}
                    <i class="icon" aria-hidden="true">
                        <svg>
                            <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-chevron-right"></use>
                        </svg>
                    </i>
                </a>
            </li>
        {{/if}}
    </ul>
</div>
{{/or}}

components/products/price:
{{! Supermarket: 
    Add class: price-was-label, price--main
}}
{{!-- There are 2 code paths for generating the PDP pricing HTML determined by the following #and condition. 
    If a 'price_range' exists then we generate all HTML in price-range.html, otherwise it is defined here. Both code
    paths generate the same HTML structure with some differences in whether that element is displayed by default (css styling).
    
    If you are making a change here or in price-range.html, you probably want to make that change in both files. --}}
    
    {{#and price.price_range (if theme_settings.price_ranges '==' true)}}
        {{> components/products/price-range price=price schema_org=schema_org}}
    {{else}}
        {{#if price.with_tax}}
            <div class="price-section price-section--withTax rrp-price--withTax" {{#unless price.rrp_with_tax}}style="display: none;"{{/unless}}>
                <span class="price-was-label">{{theme_settings.pdp-retail-price-label}}</span>
                <span data-product-rrp-with-tax class="price price--rrp">
                    {{price.rrp_with_tax.formatted}}
                </span>
            </div>
            <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="https://schema.org/Offer"{{/if}}>
                <span class="price-label" {{#if price.non_sale_price_with_tax}}style="display: none;"{{/if}}>
                    {{theme_settings.pdp-price-label}}
                </span>
                <span class="price-now-label" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
                    {{theme_settings.pdp-sale-price-label}}
                </span>
                <span data-product-price-with-tax class="price price--withTax price--main">{{price.with_tax.formatted}}</span>
                {{#if schema_org}}
                    <meta itemprop="url" content="{{product.url}}">
                    <meta itemprop="availability" itemtype="https://schema.org/ItemAvailability"
                        content="https://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
                    <meta itemprop="itemCondition" itemtype="https://schema.org/OfferItemCondition" content="https://schema.org/{{product.condition}}Condition">
                    <meta itemprop="price" content="{{toFixed price.with_tax.value settings.money.decimal_places}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="priceValidUntil" content='{{moment "next year" "YYYY-MM-DD"}}'>
                    <div itemprop="priceSpecification" itemscope itemtype="https://schema.org/PriceSpecification">
                        <meta itemprop="price" content="{{toFixed price.with_tax.value settings.money.decimal_places}}">
                        <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                        <meta itemprop="valueAddedTaxIncluded" content="true">
                    </div>
                {{/if}}
                {{#if price.without_tax}}
                    <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
                {{/if}}
            </div>
            <div class="price-section price-section--withTax non-sale-price--withTax" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
                <span class="price-was-label">{{theme_settings.pdp-non-sale-price-label}}</span>
                <span data-product-non-sale-price-with-tax class="price price--non-sale">
                    {{price.non_sale_price_with_tax.formatted}}
                </span>
            </div>
        {{/if}}
        {{#if price.without_tax}}
            <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" {{#unless price.rrp_without_tax}}style="display: none;"{{/unless}}>
                <span class="price-was-label">{{theme_settings.pdp-retail-price-label}}</span>
                <span data-product-rrp-price-without-tax class="price price--rrp"> 
                    {{price.rrp_without_tax.formatted}}
                </span>
            </div>
            <div class="price-section price-section--withoutTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="https://schema.org/Offer"{{/if}}>
                <span class="price-label" {{#if price.non_sale_price_without_tax}}style="display: none;"{{/if}}>
                    {{theme_settings.pdp-price-label}}
                </span>
                <span class="price-now-label" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
                    {{theme_settings.pdp-sale-price-label}}
                </span>
                <span data-product-price-without-tax class="price price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}} price--main">{{price.without_tax.formatted}}</span>
                {{#if schema_org}}
                    <meta itemprop="url" content="{{product.url}}">
                    <meta itemprop="availability" itemtype="https://schema.org/ItemAvailability"
                        content="https://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
                    <meta itemprop="itemCondition" itemtype="https://schema.org/OfferItemCondition" content="https://schema.org/{{product.condition}}Condition">
                    <meta itemprop="price" content="{{toFixed price.without_tax.value settings.money.decimal_places}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="priceValidUntil" content='{{moment "next year" "YYYY-MM-DD"}}'>
                    <div itemprop="priceSpecification" itemscope itemtype="https://schema.org/PriceSpecification">
                        <meta itemprop="price" content="{{toFixed price.without_tax.value settings.money.decimal_places}}">
                        <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                        <meta itemprop="valueAddedTaxIncluded" content="false">
                    </div>
                {{/if}}
                {{#if price.with_tax}}
                    <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
                {{/if}}
            </div>
            <div class="price-section price-section--withoutTax non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
                <span class="price-was-label">{{theme_settings.pdp-non-sale-price-label}}</span>
                <span data-product-non-sale-price-without-tax class="price price--non-sale">
                    {{price.non_sale_price_without_tax.formatted}}
                </span>
            </div>
        {{/if}}
        {{#if page_type '===' 'product'}}
             <div class="price-section price-section--saving" {{#unless price.saved}}style="display: none;"{{/unless}}>
                    <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                    <span data-product-price-saved class="price price--saving">
                        {{price.saved.formatted}}
                    </span>
                    <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
             </div>
        {{/if}}
    {{/and}}

components/products/price-range:
{{#and price.price_range.min.with_tax price.price_range.max.with_tax}}{{! Added by Papathemes to fix display tax/non-tax retail price when select product option }}
{{#and price.retail_price_range.min.with_tax price.retail_price_range.max.with_tax}}
    <div class="price-section price-section--withTax rrp-price--withTax">
        <span class="price-was-label">{{theme_settings.pdp-retail-price-label}}</span>
        <span data-product-rrp-price-with-tax class="price price--rrp">{{price.retail_price_range.min.with_tax.formatted}} - {{price.retail_price_range.max.with_tax.formatted}}</span>
    </div>
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax rrp-price--withTax" style="display: none;">
            <span class="price-was-label">{{theme_settings.pdp-retail-price-label}}</span>
            <span data-product-rrp-with-tax class="price price--rrp">
                {{price.rrp_with_tax.formatted}}
            </span>
        </div>
    {{/if}}
{{/and}}
{{/and}}
{{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
    <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
        <span class="price-label">{{theme_settings.pdp-price-label}}</span>
        <span class="price-now-label" style="display: none;">{{theme_settings.pdp-sale-price-label}}</span>
        <span data-product-price-with-tax class="price price--withTax price--main">{{price.price_range.min.with_tax.formatted}} - {{price.price_range.max.with_tax.formatted}}</span>
        {{#and price.price_range.min.without_tax price.price_range.max.without_tax}}
            <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.price_range.min.tax_label}}</abbr>
        {{/and}}
        {{#if schema_org}}
            <meta itemprop="url" content="{{product.url}}">
            <meta itemprop="availability" itemtype="http://schema.org/ItemAvailability"
                content="http://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
            <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
            <meta itemprop="price" content="{{toFixed price.price_range.min.with_tax.value settings.money.decimal_places}}">
            <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
            <meta itemprop="priceValidUntil" content='{{moment "next year" "YYYY-MM-DD"}}'>
            <div itemprop="priceSpecification" itemscope itemtype="http://schema.org/PriceSpecification">
                <meta itemprop="minPrice" content="{{toFixed price.price_range.min.with_tax.value settings.money.decimal_places}}"/>
                <meta itemprop="price" content="{{toFixed price.price_range.min.with_tax.value settings.money.decimal_places}}">
                <meta itemprop="maxPrice" content="{{toFixed price.price_range.max.with_tax.value settings.money.decimal_places}}"/>
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="valueAddedTaxIncluded" content="true">
            </div>
        {{/if}}
    </div>
    {{!-- Never display the "non-sales price" if there is a price range to be shown, but we do want the element on the page --}}
    <div class="price-section price-section--withTax non-sale-price--withTax" style="display: none;">
        <span class="price-was-label">{{theme_settings.pdp-non-sale-price-label}}</span>
        <span data-product-non-sale-price-with-tax class="price price--non-sale">
            {{price.non_sale_price_with_tax.formatted}}
        </span>
    </div>
{{/and}}
{{#and price.retail_price_range.min.without_tax price.retail_price_range.max.without_tax}}
    <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}">
        <span class="price-was-label">{{theme_settings.pdp-retail-price-label}}</span>
        <span data-product-rrp-price-without-tax class="price price--rrp">{{price.retail_price_range.min.without_tax.formatted}} - {{price.retail_price_range.max.without_tax.formatted}}</span>
    </div>
{{else}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" style="display: none;">
            <span class="price-was-label">{{theme_settings.pdp-retail-price-label}}</span>
            <span data-product-rrp-price-without-tax class="price price--rrp">
                {{price.rrp_without_tax.formatted}}
            </span>
        </div>
    {{/if}}
{{/and}}
{{#and price.price_range.min.without_tax price.price_range.max.without_tax}}
    <div class="price-section price-section--withoutTax{{#and price_range.min.with_tax price_range.max.with_tax}} price-section--minor{{/and}}" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
        <span class="price-label">{{theme_settings.pdp-price-label}}</span>
        <span class="price-now-label" style="display: none;">{{theme_settings.pdp-sale-price-label}}</span>
        <span data-product-price-without-tax class="price price--withoutTax price--main">{{price.price_range.min.without_tax.formatted}} - {{price.price_range.max.without_tax.formatted}}</span>
        {{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
            <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.price_range.min.tax_label}}</abbr>
        {{/and}}
        {{#if schema_org}}
            <meta itemprop="url" content="{{product.url}}">
            <meta itemprop="availability" itemtype="http://schema.org/ItemAvailability"
                content="http://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
            <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
            <meta itemprop="price" content="{{toFixed price.price_range.min.without_tax.value settings.money.decimal_places}}">
            <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
            <meta itemprop="priceValidUntil" content='{{moment "next year" "YYYY-MM-DD"}}'>
            <div itemprop="priceSpecification" itemscope itemtype="http://schema.org/PriceSpecification">
                <meta itemprop="minPrice" content="{{toFixed price.price_range.min.without_tax.value settings.money.decimal_places}}"/>
                <meta itemprop="price" content="{{toFixed price.price_range.min.without_tax.value settings.money.decimal_places}}">
                <meta itemprop="maxPrice" content="{{toFixed price.price_range.max.without_tax.value settings.money.decimal_places}}"/>
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="valueAddedTaxIncluded" content="false">
            </div>
        {{/if}}
    </div>
    {{!-- Never display the "non-sales price" if there is a price range to be shown, but we do want the element on the page --}}
    <div class="price-section price-section--withoutTax non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" style="display: none;">
        <span class="price-was-label">{{theme_settings.pdp-non-sale-price-label}}</span>
        <span data-product-non-sale-price-without-tax class="price price--non-sale">
            {{price.non_sale_price_without_tax.formatted}}
        </span>
    </div>
{{/and}}

{{!-- Never display the "saving price" element by default if there is a price range on the page to be shown --}}
{{#if page_type '===' 'product'}}
     <div class="price-section price-section--saving price" style="display: none;">
